	["cu_sawedoff_kisslove"] = { name = "Kiss♥Love", desc = "È stato verniciato con un motivo personalizzato che rappresenta una donna in rosa e viola\n\n\"Non accetterò risposte negative, devo averlo!\"" },
	["cu_dual_elites_evil_flora"] = { name = "Flora Carnivora", desc = "Tre piante carnivore affamate dai colori accesi sono state verniciate su ciascuna Beretta.\n\nFalle crescere sane e forti." },
	["gs_ump_roadblock"] = { name = "Roadblock", desc = "È stato verniciato con un motivo personalizzato di righe orizzontali arancioni e frecce puntate in avanti.\n\nAlla ricerca di strade alternative." },
	["gs_glock_elite_camo"] = { name = "Winterized", desc = "È stata verniciata con un motivo mimetico militare in colorazioni invernali.\n\nQui si gela!" },
	["cu_mag7_insomnia"] = { name = "Insomnia", desc = "È stato decorato con un design che rappresenta dei pesci che nuotano nella bocca di una persona su una trama di colori arancione e nero.\n\nFuor d'acqua." },
	["cu_ak_head_shot_holo"] = { name = "Head Shot", desc = "È stato verniciato con un graffito che recita \"HEAD SHOT GUN\" e rifinito con una vernice perlata.\n\nTutti hanno degli obiettivi, ma hai la stoffa per raggiungerli davvero? - Booth, trafficante d'armi." },
	["gs_awp_limbo_snake"] = { name = "Duality", desc = "È stato verniciato con un motivo che presenta un serpente per lato. Un serpente è rosso e uno è dorato.\n\nDue facce della stessa medaglia." },
	["cu_m4a1s_feeding_frenzy"] = { name = "Emphorosaur-S", desc = "È stato verniciato per sembrare un dinosauro che graffia un lato dell'arma.\n\nFuria famelica." },
	["cu_p2000_decline"] = { name = "Wicked Sick", desc = "Questo motivo personalizzato presenta disegni in colori neon pensati per attirare (e mantenere) la tua attenzione.\n\nPosso avere la tua attenzione, prego?" },
	["cu_ump_clutch_kick"] = { name = "Wild Child", desc = "Tra schizzi di proiettili, una corona e altri disegni, delle bande dai colori al neon fanno da base per questo UMP-45 con motivo personalizzato.\n\nIl concetto è semplice, è l'esecuzione che è complessa." },
	["cu_mp9_superlight"] = { name = "Featherweight", desc = "È stato verniciato in bianco e nero per apparire poroso, così da risparmiare del peso per chi impugna l'arma.\n\nUn buon tiratore non incolpa il suo equipaggiamento." },
	["gs_scar_fragments_black"] = { name = "Fragments", desc = "Questa verniciatura personalizzata color canna di fucile crea l'illusione che la geometria dell'arma sia cambiata.\n\nFrammenti della tua immaginazione." },
	["cu_glock_moon_rabbit"] = { name = "Umbral Rabbit", desc = "È stata verniciata con l'immagine fantastica e surreale di un coniglio mistico che salta su una luna piena.\n\nAtto di fede." },
	["cu_m4a4_temukau"] = { name = "Temukau", desc = "È stata applicata una verniciatura in stile animazione giapponese che rappresenta un agente AT donna che affronta l'assalto dei Terroristi.\n\n\"Mantenere la posizione!\"" },
	["cu_mac10_sakkaku"] = { name = "Sakkaku", desc = "In alcune condizioni di luce gli occhi di questa verniciatura personalizzata del MAC-10 si illuminano di rosso.\n\nChe la tua percezione sia la loro realtà." },
	["cu_p250_rebuilt"] = { name = "Re.built", desc = "Un design con triangoli che si incastrano e altri particolari in stile minimal è stato personalizzato su questa P250.\n\nIl nono tentativo è quello vincente." },
	["cu_mp5sd_quick_liquidation"] = { name = "Liquidation", desc = "La sua base scura e i dettagli accuratamente selezionati blu e rossi fanno in modo che questo MP5-SD sembri brillare sotto la luce ambientale.\n\nRiflessi silenziosi." },
