	["gs_p90_neoqueen"] = { name = "Neoqueen", desc = "È stato verniciato usando un mix di stili militari e di fantascienza.\n\nSu misura per tutte le tue esigenze di assalto." },
	["cu_sg553_cyberforce"] = { name = "Cyberforce", desc = "Il logo CYBERFORCE INC è stato applicato su una base rossa e nera.\n\n\"CYBERFORCE INC: pensiamo al tuo futuro, così che tu non debba pensarci più!\"" },
	["cu_tec9_freedom"] = { name = "Rebel", desc = "Questa TEC-9 è decorata con schizzi e disegni liberi usando una palette di colori tenui.\n\nUn colpo, una uccisione, un round alla volta." },
	["gs_revolver_acanthus_bronze"] = { name = "Inlay", desc = "È stato verniciato con incisioni di acanto e un'impugnatura in marmo.\n\nL'opulenza nel palmo della mano." },
