	["cu_mac10_scarab"] = { name = "Echoing Sands", desc = "È stato verniciato ispirandosi alle mimetiche e gli ambienti del deserto. Al di sotto del calcio pieghevole è stato dipinto uno scarabeo.\n\n\"È UN'IMBOSCATA! SIAMO CIRCONDATI!\"" },
