	["ak_porcelain"] = { name = "Inheritance", desc = "Questa verniciatura personalizzata si ispira alle porcellane tradizionali cinesi e olandesi.\n\nGioielli di famiglia tramandati tra generazioni." },
	["awp_tempered"] = { name = "Chrome Cannon", desc = "Verniciato con una vernice a specchio, questo AWP è poi rifinito con dettagli di vari colori e un mirino in fibra di carbonio.\n\n\"Questo da dove viene fuori? Perché me lo chiedi?\" - Booth, trafficante d'armi." },
