	["tec9_junkyard_engineering"] = { name = "Slag", desc = "È stato verniciato per apparire come realizzato a mano, dall'impugnatura alla saldatura sul cane.\n\nHai un'assicurazione, giusto?" },
	["ump45_assembled_engine"] = { name = "Motorized", desc = "Questa verniciatura personalizzata ricorda un motore a propulsione, ripensato per essere utilizzato come arma.\n\n\"Con una manutenzione ordinaria durerà almeno 500.000 proiettili!\"" },
	["usps_doggy"] = { name = "Jawbreaker", desc = "Questa USP-S personalizzata usa una vernice lucida e raffigura un cane che abbaia dall'impugnature al silenziatore. Sul silenziatore c'è scritto \"GOOD DOGGY\".\n\nHo detto \"a cuccia\"!" },
	["xm1014_irezumi"] = { name = "Irezumi", desc = "È stato dipinto un serpente su una base dorata in uno stile ispirato a quello dei tatuaggi Irezumi giapponesi.\n\nI serpenti rappresentano la saggezza e il destino, due parole che descrivono egregiamente gli utilizzatori dell'XM1014." },
