	["m249_hypnosis"] = { name = "Hypnosis", desc = "È stato verniciato con un accattivante design ipnotico nero e blu.\n\n\"Concentrati sulla mia voce.\"" },
	["r8_roses_and_guns"] = { name = "Tango", desc = "È stato verniciato con un'impugnatura in legno e delle rose.\n\nDirai il fatidico \"sì\"?" },
	["scar20_fury_topo"] = { name = "Trail Blazer", desc = "È stato verniciato con un design monocromatico di colore nero e intarsi argentei.\n\nPER L'USO ESCLUSIVO DA PARTE DI AGENTI AT AUTORIZZATI." },
	["m4a4_fusion"] = { name = "Turbine", desc = "È stato verniciato con una base color vinaccia.\n\nATTENZIONE: un utilizzo prolungato più causarne il surriscaldamento." },
	["dual_elite_hydro_strike"] = { name = "Hydro Strike", desc = "Sono state verniciate nei colori del blu e dell'arancione. Le goccioline indicano che le armi potrebbero non essere ancora asciutte.\n\nL'arma migliore è quella che i tuoi nemici non si aspettano." },
	["mac10_ozaki"] = { name = "Saibā Oni", desc = "È stato verniciato con un demone cibernetico sul bordo e sull'impugnatura.\n\nEnergia scoppiettante e freddo acciaio." },
	["p90_randy_rush"] = { name = "Randy Rush", desc = "È stato verniciato con motivo mimetico, mostrine e... Randy. Nel bene o nel male.\n\n\"Ovvero: come ho imparato a non preoccuparmi e ad amare il P90\"." },
	["ssg08_transit_white"] = { name = "Rapid Transit", desc = "È stato verniciato con un motivo personalizzato con diagrammi di transito fittizi disegnati sulla base delle mappe di Counter-Strke.\n\n\"Da che parte andiamo?\"" },
