	["hy_overpass_paintover_teal"] = { name = "Midnight Paintover", desc = "È stata verniciata con un motivo idrografico blu e nero in stile graffiti.\n\nCon affetto, il dott. Romanov." },
	["sp_overpass_doodle_plum"] = { name = "Wash me", desc = "È stato applicato un motivo che ricorda graffiti su una base rosa e viola.\n\nNiente che un po' di olio di gomito non possa risolvere." },
