	["gs_lilpig_aug"] = { name = "Lil' Pig", desc = "Questa verniciatura personalizzata presenta una base rossa con dettagli oro metallizzati. È stato aggiunto un maialino rosso vicino al calcio.\n\nMi sento a casa al poligono." },
	["gs_independe_awp"] = { name = "CMYK", desc = "È stato verniciato con un motivo multicolore astratto su una base bianca.\n\nSapevo che avresti fatto ritorno, ma non pensavo così presto... - Booth, trafficante d'armi." },
	["sp_nukestripe"] = { name = "sp_nukestripe", desc = "È stato spruzzato di vernice a fantasia di avvisi di pericolo di radiazioni." },
	["sp_spray_waves_bravo"] = { name = "sp_spray_waves_bravo", desc = "Quest'arma arrivata da Seaside è stata spruzzata di vernice a mano libera con linee corte e spesse con colori contrastanti.\n\nPerfetto per il ribelle in movimento." },
	["hy_ocean_bravo"] = { name = "hy_ocean_bravo", desc = "Quest'arma arrivata da Seaside è stata verniciata usando una stampa idrografica mimetica blu.\n\nIl nostro è un mondo di inganni... ma a volte una mimetica è fatta per essere notata. - Booth, trafficante d'armi." },
	["sp_spitfire_famas_bravo"] = { name = "sp_spitfire_famas_bravo", desc = "Quest'arma arrivata da Cache è stata verniciata con l'immagine delle fauci di uno squalo che ricorda lo stile delle grafiche sul muso dei velivoli.\n\nJester è morto." },
	["hy_tile_bravo"] = { name = "hy_tile_bravo", desc = "Quest'arma arrivata da Ali è stata verniciata usando una stampa idrografica con una fantasia a mosaico.\n\nLa pazienza è la virtù dei forti." },
	["an_navy_bravo"] = { name = "an_navy_bravo", desc = "Quest'arma arrivata da Ali è stata verniciata con uno strato base cromato caramellato con una tinta blu navy anodizzata trasparente.\n\nHo già perso un uomo per le menzogne di Valeria... non intendo perderne altri. - Felix Riley, Ufficiale comandante." },
	["sp_hazard_bravo"] = { name = "sp_hazard_bravo", desc = "Quest'arma arrivata da Siege è stata spruzzata di vernice in una fantasia a strisce di pericolo.\n\nVIETATO ATTRAVERSARE." },
	["sp_tape_dots_bravo"] = { name = "sp_tape_dots_bravo", desc = "Quest'arma arrivata da Gwalior è stata spruzzata di vernice usando pezzi di nastro adesivo strappati e metallo perforato come sagome.\n\nNon preoccuparti... gli affari stanno per riprendersi. - Booth, trafficante d'armi." },
	["sp_palm_bravo"] = { name = "sp_palm_bravo", desc = "Quest'arma arrivata da Gwalior è stata spruzzata di vernice usando cartone ritagliato, una rete sottile e foglie di palma come sagome.\n\nBellissimo oggi... cenere domani." },
	["hy_ddpat_bravo"] = { name = "hy_ddpat_bravo", desc = "Quest'arma arrivata da Ruins è stata verniciata usando una stampa idrografica Digital Disruptive Pattern (DDPAT).\n\nAnche se non puoi vederlo, lo sentirai di sicuro." },
	["so_jungle_bravo"] = { name = "so_jungle_bravo", desc = "Quest'arma arrivata da Ruins ha le varie parti spruzzate di vernice a tinta unita usando uno schema di colori in stile giungla.\n\nPer chi ha il gusto per la distruzione." },
	["so_tornado_bravo"] = { name = "so_tornado_bravo", desc = "Quest'arma arrivata da Ali ha le varie parti spruzzate di vernice a tinta unita usando uno schema di colori in stile tornado.\n\nLe parole si spargono al vento, ma il vento può uccidere." },
	["so_sand_bravo"] = { name = "so_sand_bravo", desc = "Quest'arma arrivata da Gwalior ha le varie parti spruzzate di vernice a tinta unita usando uno schema di colori in stile dune di sabbia.\n\nIl giornalista in stile gonzo Alex Kincaide è scomparso dopo essere arrivato nel Maghreb per investigare sull'Operazione Vanguard." },
	["so_olive_bravo"] = { name = "so_olive_bravo", desc = "Quest'arma arrivata da Cache ha le varie parti spruzzate di vernice a tinta unita con uno schema di colori in stile verde militare.\n\nNon dare mai per scontata la vittoria." },
	["an_gunmetal_bravo"] = { name = "an_gunmetal_bravo", desc = "Quest'arma arrivata da Siege è stata verniciata con una lega metallica anodizzata su di uno strato base cromato.\n\nNon conosci Felix Riley quanto me... quell'uomo è impossibile da spezzare. - Il traditore e il cercatore di verità, parte prima." },
	["am_ossify_blue_p2000_bravo"] = { name = "am_ossify_blue_p2000_bravo", desc = "Quest'arma arrivata da Seaside è stata verniciata usando una stampa idrografica trasparente blu astratta su di uno strato base metallizzato.\n\nLe onde s'infrangono, la nostra fermezza no. - Kotaro Izaki, esperto incursore." },
	["am_crumple_bravo"] = { name = "am_crumple_bravo", desc = "Quest'arma arrivata da Agency è stata verniciata con una fantasia poligonale con pennellate metalliche a vari livelli di riflettenza su di uno strato base cromato.\n\nGiù la penna, mani in alto!" },
	["aq_steel"] = { name = "aq_steel", desc = "È ancora perfettamente funzionante sebbene la scocca esterna si sia arrugginita.\n\nAd alcune persone non interessa nascondere quanto è sporca la loro anima." },
	["hy_hex"] = { name = "hy_hex", desc = "È stato verniciato usando una stampa idrografica a fantasia esagonale." },
	["hy_camo_large"] = { name = "hy_camo_large", desc = "È stato verniciato usando una stampa idrografica con la fantasia sovradimensionata di una mimetica." },
	["am_ddpatdense"] = { name = "am_ddpatdense", desc = "È stato ricoperto da una lamina metallica con sopra stampato un Digital Disruptive Pattern." },
	["twigs"] = { name = "twigs", desc = "È stato spruzzato di vernice a mano libera usando dei rametti come sagoma." },
	["varicamo"] = { name = "varicamo", desc = "È stato verniciato usando una stampa idrografica con una trama VariCamo.\n\nLa ricostruzione può avvenire solo sulla scia della distruzione." },
	["aq_damascus_knife"] = { name = "aq_damascus_knife", desc = "È stato forgiato da un lingotto di ferro composto da due diversi tipi di acciaio al carbonio.\n\nFinalmente un'arma degna della tua determinazione." },
	["am_crystallized"] = { name = "am_crystallized", desc = "Quest'arma è stata verniciata con una tinta metallizzata, usando una sagoma cristallizzata per creare una fantasia.\n\nTutti vogliono lasciare il segno." },
	["snakeskin"] = { name = "snakeskin", desc = "È stato verniciato con una stampa idrografica a fantasia di pelle di serpente.\n\nSo che non credi nella nostra causa... e quando lo renderò pubblico, Valeria vorrà la tua testa. - Lo scudo e il serpente, parte prima." },
	["cu_usp-s_elegant"] = { name = "cu_usp-s_elegant", desc = "È stato verniciato di blu cobalto, con lo stemma degli Anti-Terroristi spruzzato sul carrello.\n\nPosso seguire la procedura e tirarne fuori sette, oppure posso fare a modo mio e salvarli tutti e 10... decidi tu. - Carmen Cocinero, esperta di estrazioni di ostaggi." },
	["hy_plaid2"] = { name = "hy_plaid2", desc = "È stato verniciato con una stampa idrografica recante un motivo a quadretti color grigio talpa e verde acqua.\n\nNon è una cornamusa, ma emette comunque un suono ad ogni funerale." },
	["cu_fiveseven_urban_hazard"] = { name = "cu_fiveseven_urban_hazard", desc = "È stata verniciata con un motivo a mimetizzazione urbana, con delle rifiniture rosse.\n\nOgni anno, Booth riunisce gli armaioli più abili per mettere in mostra le loro creazioni più recenti." },
	["an_emerald"] = { name = "an_emerald", desc = "È stata verniciata con uno strato base cromato, caramellata in una tinta smeraldina a effetto anodizzato semitrasparente.\n\nÈ a questo che si è ridotto il grande Sebastien Hannequet? A una maestrina petulante? Che fine hai fatto... - L'insegnante e l'iconoclasta, parte seconda." },
	["so_keycolors"] = { name = "so_keycolors", desc = "Ha ciascuna parte spruzzata di vernice a tinta unita in uno schema di colori blu e arancione.\n\nLa grande livella." },
	["cu_awp_mastery"] = { name = "cu_awp_mastery", desc = "È stato verniciato con una stampa idrografica unita all'utilizzo di sagome, ed è stato decorato con adesivi a trasferimento a caldo.\n\nChase Turner era un grand'uomo... non sarà facile essere alla sua altezza - I Phoenix e l'iniziato, parte prima." },
	["aq_dualberettas_cartel"] = { name = "aq_dualberettas_cartel", desc = "Presenta un bassorilievo con scheletri, fiori e serpenti.\n\nEcco perché Huxley sta facendo il grosso del lavoro. Tutto quello che dobbiamo fare è spingerla a continuare... - Il serpente e il cercatore della verità, parte seconda." },
	["aq_famas_contour"] = { name = "aq_famas_contour", desc = "È stata verniciata in uno schema moderno di colori rosso e grigio che dona molto all'aspetto dell'arma.\n\nInsieme siamo sempre più forti." },
	["cu_nova_hyperbeast"] = { name = "cu_nova_hyperbeast", desc = "È stato verniciato con l'immagine di una bestia mitica in uno schema di colori psichedelico.\n\nTi chiamano l'Oni, giusto? Che nome pauroso per una persona così triste... – Elliot Kingsman, mercenario." },
	["hy_ocean_knife"] = { name = "hy_ocean_knife", desc = "È stata verniciata con la stampa idrografica di una mimetica blu." },
	["cu_m4a1s_printstream"] = { name = "cu_m4a1s_printstream", desc = "È stata dipinta con un motivo bianco e nero e dettagli perlati.\n\nWHITE_1; BLACK_1; PEARLESCENT_1; SILENCED_1.;" },
	["cu_usp_printstream"] = { name = "cu_usp_printstream", desc = "È stata verniciata con un motivo bianco e nero e dettagli perlati.\n\nWHITE_1; BLACK_1; PEARLESCENT_1; SILENCED_1; PISTOL_1;" },
	["m4a1s_black_lotus"] = { name = "m4a1s_black_lotus", desc = "È stato verniciato con un fiore di loto nero su una base blu e viola.\n\nAggiunge 2 uccisioni durante il round dei fucili, poi viene scartato." },
	["ssg08_dezastre"] = { name = "ssg08_dezastre", desc = "È stato verniciato con un design tecnologico nero, bianco e rosso.\n\nSeguire sempre le istruzioni d'uso del produttore per motivi di sicurezza." },
	["ump45_neo_noir"] = { name = "ump45_neo_noir", desc = "È stato verniciato con una figura femminile stilizzata blu magenta su uno sfondo grigio.\n\n\"Lama alla mano, decise che era il momento di prendere posizione.\"" },
