text = {
	["Default"] = { name = "-", desc = "이름 없는 페인트 키트" },
	["so_yellow"] = { name = "불도저", desc = "총기 부품이 생산 설비에 쓰이는 노란색 배합의 단색으로 도색했습니다.\n\n이 뼈 분쇄기는 파괴왕이지." },
	["so_olive"] = { name = "지하수", desc = "진한 올리브색 색상 배합의 단색으로 부품을 도색했습니다." },
	["so_red"] = { name = "사과 사탕", desc = "각 부분을 검은색과 빨간색 배합의 단색으로 도색했습니다.\n\n자동차에 잘 어울리지만 무기에는 더 찰떡입니다." },
	["so_purple"] = { name = "자외선", desc = "각 부분을 검은색과 보라색 배합의 단색으로 도색했습니다.\n\n아름다운 외관과 폭력적인 의지의 조합" },
	["so_night"] = { name = "밤", desc = "각 부분을 야간 작전에서 쓰이는 단색의 색상 배합으로 도색했습니다.\n\n마치 바람이 속삭이는 소리처럼 들렸지만... 어느새 목을 물리고 말았다!" },
	["so_caramel"] = { name = "캐러멜", desc = "각 부분을 캐러멜색과 검은색 배합의 단색으로 도색했습니다." },
	["so_grassland"] = { name = "풀밭", desc = "각 부분을 풀밭을 연상시키는 색상 베합의 단색으로 도색했습니다." },
	["so_moss"] = { name = "푸른 나뭇이끼", desc = "각 부분을 이끼색 색상 배합의 단색으로 도색했습니다." },
	["so_sand"] = { name = "모래밭", desc = "총기 부품이 모래밭을 연상시키는 색상 배합의 단색으로 도색했습니다.\n\n터너가 죽었으니 우리에겐 새로운 목표가 생겼다: 알렉스 킨케이드를 구출한다 - 필릭스 라일리, 지휘관" },
	["so_stormfront"] = { name = "폭풍", desc = "각 부분을 폭풍을 연상시키는 색상 배합의 단색으로 도색했습니다.\n\n모노레일에서 기다리고 있을게" },
	["so_tornado"] = { name = "토네이도", desc = "각 부분을 토네이도를 연상시키는 색상 배합의 단색으로 도색했습니다." },
	["so_whiteout"] = { name = "백시", desc = "완전히 하얗게 도색했습니다.\n\n순수해 보여도 섣불리 믿어선 안 된다." },
	["so_jungle"] = { name = "정글", desc = "각 부분을 정글을 연상시키는 색상 배합의 단색으로 도색했습니다." },
	["so_space_marine"] = { name = "식민지", desc = "각 부분을 카키색과 회색으로 도색했습니다." },
	["so_pmc"] = { name = "용병", desc = "각 부분을 군청색, 진한 초록색, 살색으로 도색했습니다.\n\n로나 사브리는 세바스찬이 터너를 쫒을 사람으로 자신을 지목하지 않은 것에 대해 아직도 화가 나 있다." },
	["so_tangerine"] = { name = "파괴", desc = "각 부분을 검은색과 귤색 배합으로 도색했습니다.\n\n로나는 그녀가 생각하는 대로 실력 있는 사람이지... 단지 그걸 모두에게 상기시켜 줄 필요가 없다는 걸 느낄 나이가 안 됐을 뿐이야 - 필릭스 라일리, 지휘관" },
	["so_orange_accents"] = { name = "니트로", desc = "so_orange_accents" },
	["cu_broken_path_famas"] = { name = "잔상", desc = "엉켜있는 리본의 흔적을 커스텀 페인트로 도색했습니다." },
	["cu_bullet_rain_m4a1"] = { name = "총알 세례", desc = "하늘에서 탄환이 내리는 듯한 그림을 커스텀 페인트로 도색했습니다." },
	["cu_catskulls_p90"] = { name = "해골 고양이", desc = "반복되는 고양이 해골 패턴을 수전사 방식으로 입혔습니다." },
	["CSGO_Doomkitty"] = { name = "죽음의 고양이", desc = "CSGO_Doomkitty" },
	["hy_ddpat"] = { name = "숲 디지털 위장 무늬", desc = "디지털 위장 무늬를 수전사 방식으로 입혔습니다.\n\n위장 무늬를 알아챌 정도로 가까이 왔다면 이미 늦었다." },
	["hy_ddpat_urb"] = { name = "시가지 디지털 위장 무늬", desc = "hy_ddpat_urb" },
	["hy_ddpat_jungle"] = { name = "정글 디지털 위장 무늬", desc = "hy_ddpat_jungle" },
	["hy_ddpat_orange"] = { name = "주황색 디지털 위장 무늬", desc = "hy_ddpat_orange" },
	["hy_ddpat_pink"] = { name = "분홍색 디지털 위장 무늬", desc = "hy_ddpat_pink" },
	["hy_ddpat_desert"] = { name = "사막 디지털 위장 무늬", desc = "hy_ddpat_desert" },
	["hy_arctic"] = { name = "극지 위장", desc = "극지 위장 무늬가 수전사 공법으로 도색되었습니다.\n\n 눈은 차갑고, 죽음은 더 차갑다" },
	["hy_arctic_contrast"] = { name = "극지 위장", desc = "hy_arctic_contrast" },
	["hy_arctic_glacier"] = { name = "극지 빙하 위장", desc = "hy_arctic_glacier" },
	["hy_dive"] = { name = "위장 무늬 속으로 뛰어들기", desc = "파란 위장 무늬를 수전사 방식으로 입혔습니다.\n\n우리가 사는 곳은 속이는 것이 일상이지만... 가끔씩은 보이기 위한 위장을 하기도 한다 - 부스, 무기 거래상" },
	["hy_ocean"] = { name = "Bright Water", desc = "hy_ocean" },
	["hy_forest"] = { name = "숲 위장", desc = "숲 위장 무늬를 수전사 방식으로 입혔습니다.\n\n숲은 위험한 장소가 될 수 있다... 절대로 혼자 다니지 말라." },
	["hy_forest_winter"] = { name = "겨울 숲", desc = "hy_forest_winter" },
	["hy_forest_boreal"] = { name = "북방림", desc = "hy_forest_boreal" },
	["hy_forest_night"] = { name = "야간 숲 작전용 위장무늬", desc = "hy_forest_night" },
	["hy_desert"] = { name = "모래 폭풍", desc = "사막 위장 무늬를 수전사 방식으로 입혔습니다." },
	["hy_tiger"] = { name = "뱅골 호랑이", desc = "호랑이 줄무늬를 수전사 방식으로 입혔습니다." },
	["hy_copperhead"] = { name = "코퍼헤드 독사", desc = "구리머리살모사의 피부를 연상시키는 패턴을 수전사 방식으로 입혔습니다." },
	["hy_skulls"] = { name = "해골", desc = "적색과 흑색으로 강조한 해골 무늬를 수전사 방식으로 입혔습니다.\n\n여러분의 손에 꼭 맞는 커다란 무덤입니다!" },
	["hy_webs"] = { name = "붉은 그물", desc = "표면에 붉은색 코팅제를 바른 다음 거미줄 패턴을 수전사 방식으로 입힌 후 반광 마감재로 마무리했습니다.\n\n자신이 가는 곳을 주의해라, 거미줄이 어디에 쳐져 있을 지는 모르니." },
	["hy_splatter"] = { name = "푸른 결", desc = "푸른색 베이스 코팅제 위에 액체가 흩어진 듯한 반투명한 패턴을 수전사 방식으로 입혔습니다." },
	["hy_ak47lam"] = { name = "붉은색 합판", desc = "합판으로 된 개머리판이 주어졌습니다." },
	["hy_ak47lam_bw"] = { name = "검은 합판", desc = "hy_ak47lam_bw" },
	["hy_ak47lam_blue"] = { name = "파란색 합판", desc = "hy_ak47lam_blue" },
	["hy_gelpen"] = { name = "초연", desc = "젤펜으로 그려진 듯한 연기를 연상시키는 무늬를 수전사 방식으로 입혔습니다." },
	["hy_v_tiger"] = { name = "정글 호랑이", desc = "열대림 호랑이 위장 무늬를 수전사 방식으로 입혔습니다." },
	["hy_redtiger"] = { name = "핏빛 호랑이무늬", desc = "hy_redtiger" },
	["hy_zombie"] = { name = "바이러스", desc = "초록색 베이스 코팅제 위에 좀비 패턴을 수전사 방식으로 입혔습니다." },
	["hy_granite"] = { name = "대리석 무늬", desc = "대리석 무늬로 도색했습니다." },
	["hy_blizzard"] = { name = "눈보라 대리석", desc = "hy_blizzard" },
	["hy_sediment"] = { name = "진흙탕", desc = "hy_sediment" },
	["hy_reef_rock"] = { name = "푸른 대리석 무늬", desc = "hy_reef_rock" },
	["sp_spray"] = { name = "대조색 스프레이", desc = "짧고 굵은 선으로 이루어진 패턴을 여러 대비되는 색상의 스프레이 페인트로 도색했습니다.\n\n자주 이동하는 폭도라면 꼭 필요함" },
	["sp_spray_sage"] = { name = "약초 부분 채색", desc = "sp_spray_sage" },
	["sp_spray_jungle"] = { name = "정글 도색", desc = "sp_spray_jungle" },
	["sp_spray_sand"] = { name = "모래 부분 채색", desc = "sp_spray_sand" },
	["sp_spray_waves"] = { name = "파동 도색", desc = "sp_spray_waves" },
	["sp_spray_army"] = { name = "정찰용 위장무늬", desc = "sp_spray_army" },
	["sp_tape_dots"] = { name = "건기", desc = "찢겨진 테이프 조각과 촘촘하게 구멍이 난 금속판을 스텐실로 사용하여 스프레이 페인트로 도색했습니다." },
	["sp_tape_dots_urban"] = { name = "현대식 격자", desc = "sp_tape_dots_urban" },
	["sp_tape_dots_waves"] = { name = "천공된 파도", desc = "sp_tape_dots_waves" },
	["sp_leaves"] = { name = "나뭇잎", desc = "나뭇잎을 스텐실로 사용하여 스프레이 페인트로 도색했습니다." },
	["sp_leaves_grassland"] = { name = "초원의 풀", desc = "sp_leaves_grassland" },
	["sp_short_tape"] = { name = "이끼의 흔적", desc = "짧은 테이프 조각을 스텐실로 사용하여 스프레이 페인트로 도색했습니다." },
	["sp_short_tape_jungle"] = { name = "열대림의 흔적", desc = "sp_short_tape_jungle" },
	["sp_short_tape_sand"] = { name = "모래의 흔적", desc = "sp_short_tape_sand" },
	["sp_short_tape_urban"] = { name = "도시적 빗금무늬", desc = "sp_short_tape_urban" },
	["sp_tape"] = { name = "해골 마스크", desc = "종이 테이프를 감아 스텐실로 사용하여 스프레이 페인트로 도색했습니다.\n\n진정한 힘은 미묘한 동작을 통해 나타난다" },
	["sp_tape_orange"] = { name = "오렌지 껍질", desc = "sp_tape_orange" },
	["sp_tape_urban"] = { name = "도시의 위장", desc = "sp_tape_urban" },
	["sp_mesh"] = { name = "유목 그물", desc = "격자 무늬와 골판지 조각을 스텐실로 사용하여 스프레이 페인트로 도색했습니다.\n\n포식자는 환경에 상관없이 언제나 포식자이다." },
	["sp_mesh_tan"] = { name = "사파리 격자 무늬", desc = "sp_mesh_tan" },
	["sp_mesh_arctic_contrast"] = { name = "극지의 격자", desc = "sp_mesh_arctic_contrast" },
	["sp_mesh_fire"] = { name = "저주받은 자", desc = "sp_mesh_fire" },
	["sp_mesh_glacier"] = { name = "빙하 그물코", desc = "sp_mesh_glacier" },
	["sp_mesh_sand"] = { name = "모래 망무늬", desc = "sp_mesh_sand" },
	["sp_snake"] = { name = "뱀 위장", desc = "뱀 피부 무늬 스텐실을 군데군데 스프레이 페인트로 도색한 후 스텐실 가장자리를 붓으로 칠해 장식했습니다." },
	["an_silver"] = { name = "은색", desc = "이 무기는 부품을 크롬 코팅제를 칠한 다음 표면 처리 효과를 내는 은빛 페인트에 담궈 도색했습니다.\n\n간단함에서 아름다움이 창조된다 - 프란즈 크리겔드, 피닉스 전술가" },
	["an_red"] = { name = "핫 로드", desc = "이 무기는 크롬 코팅제를 칠한 다음 표면처리 효과를 내는 붉은색 페인트로 도색했습니다. \n\n자동식. 체계적. 유압자동식.	" },
	["an_gunmetal"] = { name = "양극 산화 처리된 포금", desc = "an_gunmetal" },
	["an_navy"] = { name = "도장 효과 군청색", desc = "이 무기는 크롬 코팅제를 칠한 다음 도장 처리 효과를 내는 군청색 페인트에 담궈져 도색했습니다." },
	["am_urban"] = { name = "금속성 디지털 위장 무늬", desc = "표면에 메탈릭 코팅제를 위에 현대적인 디지털 위장 무늬(DDPAT)를 수전사 방식으로 입혔습니다." },
	["am_tiger"] = { name = "황금 호랑이", desc = "이 무기에는 금 도금이 된 표면에 호랑이 무늬가 각인되어 있습니다." },
	["am_dragon_glock"] = { name = "용 문신", desc = "이 무기는 메탈릭 도료를 칠한 다음 용 문양을 전사하여 도색했습니다.\n\n동화에서는 기사가 용을 처리하지... 하지만 여기는 현실이야 - 발레리아 제너, 혁명가" },
	["am_caustics"] = { name = "부식성", desc = "짙은 회색 금속성 베이스 코팅제를 칠한 다음 빨간색과 하얀색 무늬를 수전사 방식으로 입혔습니다." },
	["am_lightning_awp"] = { name = "청천벽력", desc = "이 무기는 메탈릭 코팅제를 칠한 다음 표면 처리 효과를 내는 페인트를 이용해 천둥벼락을 연상시키도록 도색했습니다.\n\n때로는 같은 곳을 두 번 칠 필요가 없지요	" },
	["am_ossify"] = { name = "골화", desc = "메탈릭 코팅제를 칠한 다음 선명한 라임색의 추상적인 무늬를 수전사 방식으로 입혔습니다." },
	["aa_flames"] = { name = "주홍 불길", desc = "이 무기는 크롬 코팅제를 바른 뒤 선명한 색채의 페인트를 에어브러시를 이용하여 도색했습니다." },
	["aa_splash_p250"] = { name = "확산", desc = "이 무기는 크롬 베이스 코팅제를 칠한 다음 선명한 페인트를 이용한 스텐실 기법으로 도색했습니다." },
	["aa_fade"] = { name = "흐릿함", desc = "이 무기는 크롬 코팅제를 바른 뒤 서로 어울리는 색깔의 페인트를 에어브러시를 이용하여 도색했습니다. \n\n이것은 단순한 무기가 아니다. 이것은 소통의 방편이다 - 이모젠, 견습 무기 상인" },
	["aa_fade_metallic"] = { name = "호박빛 배색", desc = "aa_fade_metallic" },
	["aa_fade_grassland"] = { name = "초록빛 배색", desc = "aa_fade_grassland" },
	["aq_copper"] = { name = "구리 도금", desc = "이 무기는 부품에 구리가 도금되었습니다." },
	["aq_blued"] = { name = "푸른 강철", desc = "냉혈한을 나타내는 청색입니다. \n\n이것은 무기 디자인 계의 말벡 포도주이다 - 부스, 무기 거래상" },
	["aq_forced"] = { name = "얼룩", desc = "표면에 레몬즙과 겨자를 떨어뜨려 녹슬게 했습니다.\n\n지금은 엉망으로 보이지만, 여기에 자외선을 쬔다면 어떨까?" },
	["aq_oiled"] = { name = "표면 경화", desc = "이 무기는 고온의 목탄을 이용한 표면경화법으로 도색했습니다.\n\n약간의 다채로움은 해롭지 않아" },
	["sp_spitfire_famas"] = { name = "터지는 화염", desc = "sp_spitfire_famas" },
