	["cu_m4_asimov"] = { name = "아시모프", desc = "이 무기는 공상 과학적인 디자인을 목표로 도색했습니다. \n\n누구나 미래를 예측할 수는 있지만... 선구자는 미래를 직접 만들지	" },
	["cu_sawedoff_octopump"] = { name = "크라켄", desc = "이 무기는 손수 그린 문어를 도색했습니다.\n\n괴물이 풀려났다" },
	["cu_m4a1-s_elegant"] = { name = "수호자", desc = "코발트 블루 색으로 바탕을 칠한 다음 탄창에 대테러부대 로고를 스텐실 기법으로 도색했습니다.\n\n난 너를 좋아할 필요가 없어, 너를 보호해야 할 뿐이지 - 카르멘 코시네로, 인질 구출 전문가" },
	["cu_p250_refined"] = { name = "멘디", desc = "페이즐리 무늬를 수전사 방식으로 입혔습니다.\n\n나마스떼" },
	["cu_awp_cobra"] = { name = "붉은선", desc = "이 무기는 탄소 섬유 무늬를 수전사 공법으로 씌운 뒤 건식 전사 공법을 통해 붉은 줄을 도색했습니다.\n\n한계에 도달하는 것을 두려워하지 말라" },
	["cu_famas_pulse"] = { name = "펄스", desc = "거친 진분홍색 조각의 무늬를 수전사기법을 통해 도색했습니다. \n\n눈부신 무기에는 눈부신 색깔로	" },
	["hy_marina_sunrise"] = { name = "정박지", desc = "대조되는 색채의 노란색과 파란색 무늬를 수전사 방식으로 입혔습니다.\n\n누군가는 너를 영웅으로 부르지... 다른 사람들은 악당 취급할테지만." },
	["am_thorny_rose_mp9"] = { name = "강철의 장미", desc = "이 무기는 다양한 색상의 메탈릭 페인트를 이용해 장미와 가시를 주제로 손수 도색했습니다.\n\n수많은 가시를 조심하라" },
	["cu_skull_nova"] = { name = "떠오르는 해골", desc = "이 무기는 무광 페인트와 유성 페인트 마커를 이용해 두개골 그림을 그려넣었습니다. \n\n이자키씨, 불평하는 건 아니지만 산탄총이 대체 몇 개나 필요한 거죠? - 이모젠과 오니, 제 1장	" },
	["cu_sandstorm"] = { name = "모래폭풍", desc = "보라색과 베이지색이 조화를 이루는 무늬를 수전사 방식으로 입혔습니다.\n\n자신을 보호하려면 함정보다 더한 것이 필요할 것이다." },
	["hy_kami"] = { name = "종이", desc = "이 무기는 수전사 공법으로 일본 만화 풍의 무늬로 도색했습니다.\n\n어떤 유령들은 묻혀 있기 싫어한다" },
	["aq_obsidian"] = { name = "마그마", desc = "구리로 총기를 도장한 뒤 용암을 연상시키는 무늬를 씌운 후 소금과 고열을 이용해 붉은 고색을 냈습니다. 고색이 생기지 않은 부분은 유황을 이용해 산화시켜 어둡게 만들었습니다.\n\n광염이 솟구친다" },
	["am_turqoise_halftone"] = { name = "옥색 하프톤", desc = "메탈릭 도료가 첨가된 옥색 베이스 코팅제로 칠한 다음 수전사 공법으로 하프톤 무늬를 씌웠습니다.\n\n필릭스, 자네도 알다시피 발레리아는 전쟁을 할 준비를 하고 있어. 여기서 중요한 질문은 자네가 준비가 됐느냐지 - 부스, 무기 상인" },
	["cu_aug_chameleonaire"] = { name = "카멜레온", desc = "이 무기는 카멜레온의 무늬로 도색했습니다.\n\n미묘함이 모든 차이를 만듭니다	" },
	["cu_ump_corporal"] = { name = "상등병", desc = "이 무기는 군대풍 느낌이 나도록 도색했습니다.\n\n존경심은 얻을 수 있지만 빼앗아 올 수는 없다" },
	["cu_p90_trigon"] = { name = "삼각 퍼즐", desc = "이 무기는 서로 대조되는 색채의 주황색, 회색 그리고 검은색을 이용해 직접 그린 무늬를 도색했습니다.\n\n인간 폭풍이 되어라" },
	["cu_mac10_redhot"] = { name = "열기", desc = "이 무기는 방금 주조 과정을 마친 듯한 느낌을 내도록 도색했습니다.\n\n자신만의 윤곽을 만드세요." },
	["sp_negev_turq_terrain"] = { name = "지형지물", desc = "지형을 주제로 한 무늬를 수전사 방식으로 입혔습니다.\n\n당신의 딸은 중립적인 태도를 가져야 하는데 그에 맞지 않는 친구를 두었더군 - 아버지와 피닉스 1장" },
	["cu_nova_antique"] = { name = "골동품", desc = "이 무기는 수전사 공법으로 무늬를 넣고 각종 수전사 데칼을 붙여 목재로 만든 듯한 개머리판과 금장식을 한 것처럼 도색했습니다.n\n나이가 들수록 지혜와 저지력이 늘어난다" },
	["an_famas_sgt"] = { name = "부사관", desc = "이 무기는 마스킹 용액으로 만든 고색의 군대풍 무늬를 도색한 뒤 광택으로 마무리했습니다.\n\n모범을 보여 인도함	" },
