	["cu_usp_sandpapered"] = { name = "탄알 궤적", desc = "날아가는 총알의 그림을 그린 뒤 사포로 문질러 도색했습니다." },
	["hy_ssg08_marker"] = { name = "우회로", desc = "흰색 베이스 코팅제를 바른 뒤 퍼머넌트 마커로 장식했습니다." },
	["am_royal"] = { name = "성배", desc = "크롬 코팅제를 칠한 다음 선명한 색채의 진보라색과 호박색의 표면 처리 효과 페인트에 담궈 도색했습니다.\n\n현명한 선택을 하셨군요" },
	["am_metals"] = { name = "기사", desc = "르네상스 시대의 갑옷을 연상시키도록 황동을 세공했습니다.\n\n코시네로와 이자키를 못 찾았다고? - 필릭스 라일리, 지휘관" },
	["am_chainmail"] = { name = "쇄자갑", desc = "은색 베이스 코팅제를 바른 뒤 사슬 갑옷을 연상시키는 반투명한 무늬를 수전사 방식으로 입혔습니다.\n\n피닉스의 단원 중 발레리아의 경호원 나오미보다 더 충성스러운 자는 없다" },
	["aq_handcannon"] = { name = "손대포", desc = "오래된 황동 대포에서 영감을 얻어 산으로 고색을 입힌 황동 부품을 사용합니다.\n\n자신이 겪게 될 고난의 가시들을 맞이할 준비를 하자" },
	["am_metal_inlay"] = { name = "암흑기", desc = "메탈릭 도료가 든 페인트를 이용해 중세풍 무늬로 철을 세공한 것처럼 보이도록 도색했습니다.\n\n그녀의 애인을 내가 죽일 수 있을 거야... 아마도 너와 발레리아가 함께 할 시간만큼은 만들 수 있겠지 - 아버지의 사랑 2장" },
	["hy_vines"] = { name = "들장미", desc = "꼬인 줄기와 꽃 무늬를 수전사 방식으로 입혔습니다.\n\n진정한 사랑의 키스도 가시를 막을 수 없다" },
	["so_indigo"] = { name = "감청색", desc = "진홍색, 남색과 회색으로 묵직하게 도색했습니다.\n\n웃으라고 필릭스, 네가 항상 음울한 놈처럼 보이지만 않는다면 여자들에게 인기가 더 많을 거라고 - 이모젠, 견습 무기 상인" },
	["so_indigo_and_grey"] = { name = "쪽빛", desc = "남색, 파란색과 회색의 단색으로 도색했습니다.\n\n알렉스 킨케이드, 궁금한 게 많겠지... 바로 대답해 주겠어 - 피닉스와 이야기꾼 1장" },
	["cu_well_traveled_ak47"] = { name = "제트족", desc = "개머리판이 갈색 가죽으로 싸여 있습니다. 이 무기는 각지의 도시로 여행을 다닌 것 같군요.\n\n주의 깊은 사람이라면 그 관계를 보고 그녀가 무슨 일을 했는지 깨닫을 것이다. 우리에게는 해결법을 찾을 시간이 별로 없는 것 같다... - 어떤 아버지의 사랑 제 1장" },
	["cu_green_leather"] = { name = "일등석", desc = "개머리판이 초록색 가죽으로 싸여 있습니다.\n\n미모사는 사서 드셔야 합니다" },
	["cu_brown_leather_p90"] = { name = "가죽", desc = "개머리판이 갈색 가죽에 싸여 있습니다.\n\n걱정 마세요, 잠금장치는 TSA 기준을 지켰으니까요" },
	["cu_luggage_mac10"] = { name = "통근자", desc = "격자 무늬를 수전사 방식으로 입히고 일부분을 인조 악어 가죽으로 감쌌습니다.\n\n놈들이 미끼를 물었다, 가자... - 엘리엇 킹스맨, 용병" },
	["cu_medieval_dragon_awp"] = { name = "용의 전설", desc = "매듭 장식으로 만든 용을 커스텀 페인트로 도색했습니다.\n\n200개의 열쇠도 그 비밀을 밝혀내지 못하였다" },
	["cu_luggage_p2000"] = { name = "이코노미석", desc = "격자 무늬를 수전사 방식으로 입히고 손잡이 부분을 갈색 가죽으로 감쌌습니다.\n\n약간 조촐해도 목적지까지 가는 게 어디야" },
	["aq_pilot_deagle"] = { name = "조종사", desc = "비행사의 모자에 달린 날개처럼 생긴 황동 장식이 붙어있습니다.\n\n충격에 대비해 자세를 취해 주세요." },
	["cu_leather_xm1014"] = { name = "붉은 가죽", desc = "개머리판과 손잡이가 붉은 가죽에 싸여 있습니다.\n\n가방을 싸고 그 놈을 죽이러 가자. - 오니와 발키리 제 2장" },
	["cu_luggage_sg553"] = { name = "여행객", desc = "오래된 여행용 가방에서 영감을 얻은 줄무늬가 도색되어 있고 몇몇 부품이 갈색 가죽으로 감싸져 있습니다.n\n그 누구라도 자신에 대한 이야기는 있기 마련이고, 하비엘는 자신의 목표에게서 그 이야기를 끄집어낸 뒤 죽인다" },
	["cu_luggage_usp-s"] = { name = "비지니스석", desc = "오래된 여행용 가방에서 영감을 얻은 색과 무늬들이 도색되어 있고 몇몇 부품이 갈색 가죽에 싸여 있습니다.\n\n일부 여행객들은 다리를 뻗을 수 있는 공간보다는 평화와 고요를 더 중시합니다" },
	["hy_plaid1"] = { name = "올리브색 격자무늬", desc = "베이지색과 연한 녹색의 격자 무늬를 수전사 방식으로 입혔습니다.\n\n그거 좋아하죠. 제 눈이 카메라에 더 잘 나오게 해 줘요... - 로나 사브리, 신인 정예 요원" },
	["chy_plaid2"] = { name = "녹색 격자무늬", desc = "chy_plaid2" },
	["cu_m4a1-s_silence"] = { name = "명작", desc = "거친 느낌의 그래피티를 도색했습니다." },
	["cu_panther_ak47"] = { name = "재규어", desc = "호랑이 위장 무늬를 바탕으로 위에 표범을 그려 도색했습니다." },
	["cu_bratatat_negev"] = { name = "부타타타탕", desc = "시원한 사격음을 만화책에 나오는 의성어처럼 표현한 뒤 총알 구멍을 더해 도색했습니다." },
	["am_nuclear_pattern1_glock"] = { name = "원자로", desc = "메탈릭 페인트를 이용해 낙진 경고 표시의 무늬로 도색했습니다." },
	["hy_nuclear_pattern2_mp9"] = { name = "일출", desc = "낙진 경고 표시 무늬를 수전사 방식으로 입혔습니다." },
	["sp_nuclear_pattern3_negev"] = { name = "핵 폐기물", desc = "낙진 경고 표시 무늬를 수전사 방식으로 입혔습니다." },
	["am_nuclear_skulls1_xm1014"] = { name = "뼈 분쇄기", desc = "크롬으로 도금한 다음 반투명한 두개골과 뼈로 이루어진 낙진 경고 표시 무늬를 수전사 방식으로 입혔습니다." },
	["am_nuclear_skulls2_famas"] = { name = "스틱스 강", desc = "크롬으로 도금한 다음 반투명한 두개골과 뼈로 이루어진 낙진 경고 표시 무늬를 수전사 방식으로 입혔습니다." },
	["am_nuclear_skulls3_mac10"] = { name = "방사능 정원", desc = "크롬으로 도금한 다음 반투명한 두개골과 뼈로 이루어진 낙진 경고 표시 무늬를 수전사 방식으로 입혔습니다." },
	["hy_nuclear_skulls4_p250"] = { name = "오염", desc = "핵 해골과 뼈 패턴을 수전사 방식으로 입혔습니다." },
	["hy_nuclear_skulls5_tec9"] = { name = "중독", desc = "두개골과 뼈로 이루어진 낙진 경고 표시로 이루어진 무늬를 수전사 방식으로 입혔습니다." },
	["so_grey_nuclear_green_bizon"] = { name = "화학적 녹색", desc = "회색과 엷은 녹색의 단색 배합으로 도색했습니다." },
	["so_grey_nuclear_orange_five_seven"] = { name = "핫샷", desc = "연회색과 네온 그린 색의 단색 배합으로 도색했습니다." },
	["cu_cerbrus_galil"] = { name = "케르베로스", desc = "신화 속 세 개의 머리를 가진 지옥의 개를 연상케 하는 주문 제작된 도색입니다." },
	["cu_tribute_ak47"] = { name = "황무지 반란군", desc = "외관이 낡아버린 이 총은 블록 프린팅 기법으로 무늬를 넣었으며 그라피티로 장식했습니다.\n\n언젠가 그들은 우리가 왜 싸우냐고 물을 것이고, 그 때 그들은 이해할 것이다 - 나오미, 발레리아 제너의 경호원" },
	["aq_glock_coiled"] = { name = "분쇄기", desc = "강철로 만들어진 슬라이드를 유화 물감으로 스텐실 기법을 사용하되 다른 부분은 직접 칠해 도색했습니다.\n\n다이아몬드를 직접 만드는 방법에는 압력을 가하는 것밖에 없다" },
	["am_g3sg1_murky"] = { name = "어둑함", desc = "표면 처리를 한 후에 수전사 공법으로 표범의 점박무늬로 도색했습니다.\n\n자기가 있는 곳보다 더 어두운 곳은 언제나 있다" },
	["aq_m4a1s_basilisk"] = { name = "바실리스크", desc = "에칭공법으로 전설의 바실리스크가 새겨져 있습니다. \n\n빤히 쳐다보는 것은 실례다. 하지만 전쟁은 사교 파티가 아니지" },
	["cu_m4a4_griffin"] = { name = "그리핀", desc = "직접 그린 그리핀의 그림으로 도색했습니다.\n\n필릭스가 전 세계에서 그의 팀에 함께할 기술자들을 직접 뽑았고 사람들이 그들의 개성에 대해 질문을 하는 동안, 그들은 결과에 대해 질문하지 않았습니다	" },
	["sp_mag7_firebitten"] = { name = "방화범", desc = "스프레이로 겹겹이 칠하고 약간 손상시켜 테두리가 불타는 듯하게 표현했습니다.\n\n항상 작은 불꽃에서 시작하지" },
	["cu_mp9_chevron"] = { name = "다트", desc = "수전사 공법으로 V자형 무늬로 도색했습니다.\n\n아래쪽으로 쏘다 보면 머리를 맞출 수 있겠지" },
	["aq_p250_cartel"] = { name = "카르텔", desc = "뱀과 꽃 그리고 해골이 새겨져 있습니다.\n\n방아쇠를 당기기만 하면 어떤 날이든 죽은 자의 날로 바꿀 수 있지" },
	["cu_p2000_fire_elemental"] = { name = "불의 원소", desc = "살아있는 듯한 불의 괴물의 그림으로 도색했습니다. \n\n가끔 불장난하는 것은 좋은 일입니다	" },
	["aq_sawedoff_blackgold"] = { name = "노상강도", desc = "금색과 검은색의 래커로 큰엉겅퀴를 연상시키는 무늬로 도색했습니다.\n\n쏘면서 새끼손가락을 내밀고 쏠 필요는 없지만, 멋져 보이기는 해" },
	["cu_scar20_intervention"] = { name = "강심제", desc = "주황색과 파란색의 현대적인 디자인이 인쇄된 비닐로 총을 감싼 뒤 열을 가해 접착시켰습니다.\n\n삑. 삑. 삐이이이이...." },
	["sp_ump45_d-visions"] = { name = "망상", desc = "실크 스크린 기법으로 앵무조개 무늬로 도색했습니다.\n\n카르코사가 나를 부른다" },
	["cu_xm1014_caritas"] = { name = "평온", desc = "긴 머리 여성의 그림을 손으로 직접 그렸습니다.\n\n저는 제가 필요한 것 때문에 여기를 온 게 아닙니다. 저는 제가 원하는 것 때문에 여기를 왔습니다 - 이모젠과 오니, 제 2장" },
	["am_awp_glory"] = { name = "해상의 지배자", desc = "금속성의 푸른색으로 기본 배경을 칠하고 금속성의 금색으로 소용돌이와 강조점을 직접 손으로 도색했습니다. \n\n적을 처치하기 전에 먼저 어지럽게 만들어라" },
	["cu_elites_urbanstorm"] = { name = "도시 습격", desc = "시가지 위장무늬에 푸른색으로 강조를 넣어 도색했습니다.\n\n번개가 치면 항상 천둥이 울린다" },
	["aq_deagle_naga"] = { name = "나가", desc = "신화를 주제로 새김을 넣었습니다.\n\n인도 신화와 전장의 괴물" },
	["cu_galil_abrasion"] = { name = "속사포", desc = "전체적으로 오래된 것 처럼 보이도록 핸드 페인팅 되어 있습니다. 더스트 커버 위에 해골이 그려져 있어 노리쇠가 움직일 때 해골의 입이 움직이게 됩니다. \n\n웃으면 복이 온다고 하지요... 하지만 Chatterbox가 웃을 때는 비명이 돌아올 뿐입니다" },
	["cu_glock_deathtoll"] = { name = "지하묘지", desc = "은색 메탈릭 페인트 바탕위에 수전사 공법을 이용해 해골들을 커스텀 페인트로 도색했습니다. \n\n위협이야말로 가장 최고의 칭찬이다" },
	["cu_m4a4_ancestral"] = { name = "용왕", desc = "적색과 청색으로 이루어진 용의 형상으로 도색했습니다.\n\n손오공의 몸값의 가치를 지닌 무기 - 부스, 무기 거래상" },
	["cu_m249_sektor"] = { name = "체계 잠금", desc = "공상 과학적인 디자인으로 도색했습니다.\n\n엄호 사격이 이렇게 보기 좋을 줄이야" },
	["am_mac10_malachite"] = { name = "공작석", desc = "금속 효과 도료로 도색되고 고무 빗으로 물결 무늬를 넣었습니다.\n\n밝은 색으로 총의 어두운 속내를 숨기다" },
	["cu_mp9_deadly_poison"] = { name = "극약", desc = "역동적인 뾰족한 도형들과 간략히 그려진 거미 형상을 커스텀 페인트로 도색했습니다.\n\n한 번 물었다고 죽지 않아. 아직 29번 남았어" },
	["cu_p250_mandala"] = { name = "망자의 날", desc = "원형 장식을 커스텀 페인트로 도색했습니다.\n\n이 무기는 피와 전통이 잔뜩 스며들었다" },
	["cu_sawedoff_deva"] = { name = "평온", desc = "날개 단 여성의 형상을 손으로 칠했습니다.\n\n평온은 소수만이 누릴 수 있는 사치다 - 발레리아 제너, 혁명가" },
	["aq_scar20_leak"] = { name = "암굴", desc = "푸른색 금속 효과를 내는 페인트로 스펀지와 붓을 이용해 칠해졌습니다.\n\nSeason 기업의 창업주는 코타로 이자키라는 명성있는 사업가에게 구해졌을 때 이 무기를 의뢰했다." },
	["aq_xm1014_sigla"] = { name = "수은", desc = "은색으로 도색한 뒤 지형도에 쓰이는 수로도를 반투명하게 그렸습니다.\n\n가끔은 문제들을 해결하기 위해 불안정한 요소를 써야하는 법이다 - 코타로 이자키, 돌파 전문가" },
	["an_tiger_orange"] = { name = "호랑이 이빨", desc = "주황색으로 표면처리하고 호랑이 줄무늬를 손으로 새겼습니다.\n\n범처럼 희귀함... 범처럼 강력함..." },
	["am_marble_fade"] = { name = "흐릿한 대리석", desc = "검은색과 은색의 금속성 페인트가 중간재를 통해 한 데 섞어 대리석 무늬가 나고, 삼색으로 윤이 나도록 입혀졌습니다.\n\n이 칼날은 많은 색을 띄고 있지만, 이내 붉은 색으로 물들었다." },
	["am_marbleized"] = { name = "도플러", desc = "검은색과 은색 금속성 페인트를 이용해 마블링 효과를 주었으며 그 위에 윤이 나도록 코팅되어 있습니다. \n\n그 아름다운 색상에 넋을 잃으면 치명적인 결과를 초래할 수도 있습니다." },
	["cu_ak47_mastery"] = { name = "Elite Build", desc = "It has been custom painted using a combination of hydrographics and careful stenciling and detailed with heat-transfer decals.\n\nTread softly Naomi...people in your line of work aren't known for their longevity - The Shield and The Serpent Part 2" },
	["aq_mp7_ultramodern"] = { name = "기갑", desc = "스테인리스강 부품과 장식을 사용하며 검은색 유화 도료로 도색했습니다.\n\n화력과 품질을 우선시하는 고객들을 위한 제품 - 무기 상인 부스" },
	["aq_awp_twine"] = { name = "충신", desc = "내구성이 뛰어난 에나멜 도료로 해골과 뿌리를 그렸습니다.\n\n자신이 선택하고 싶지 않았던 뿌리는 항상 있다" },
	["am_bronze_sparkle"] = { name = "구리 장식", desc = "자동차를 도색할 때 쓰는 메탈릭 도료를 사용한 아르 데코 양식의 무늬를 도색했습니다.\n\n모두가 금과 은을 찾는다" },
	["aq_p250_contour"] = { name = "발랑스", desc = "파란색과 회색으로 무기의 형태를 돋보이게 하는 현대적인 무늬를 도색했습니다.\n\n능력이 아닌 태도가 중요한 것이다 - 선생과 우상타파주의자 제 1장" },
	["cu_fiveseven_banana"] = { name = "짓궂은 장난", desc = "바나나입니다. 설명이 더 필요할까요?\n\n가끔은 음식으로 장난쳐도 괜찮아요." },
	["cu_galil_eco"] = { name = "친환경", desc = "흰색과 초록색으로 도색되어 있고 열전사 기법을 통해 데칼을 추가했습니다.\n\n돈만 충분히 준다면 이 정예부대원들은 무엇이든지 합니다" },
	["aq_famas_jinn"] = { name = "정령", desc = "아랍의 신화에 등장하는 요정 진을 조각한 뒤 금과 니켈로 세공했습니다.\n\n소원을 빌 때는 조심해야 하리라" },
	["cu_m4a1_hyper_beast"] = { name = "Hyper Beast", desc = "It has been custom painted with a beastly creature in psychadelic colours.\n\nYou really want to impress me Booth? Make this black light sensitive - Rona Sabri, Rising Star" },
	["cu_mag7_redhot"] = { name = "PaintKit_cu_mac10_redhot_tag", desc = "PaintKit_cu_mac10_redhot" },
	["am_negev_glory"] = { name = "PaintKit_am_awp_glory_Tag", desc = "PaintKit_am_awp_glory" },
	["cu_mac10_neonrider"] = { name = "네온불빛 기수", desc = "위험해 보이는 폭주족이 그려져 있습니다.\n\n총이지만 트래퍼 키퍼만큼 화려하답니다" },
	["cu_sawedoff_origami"] = { name = "종이접기", desc = "이 아이템은 종이접기에서 영감을 얻은 도안으로 도색했습니다.\n\n짓는 것은 한 세월, 부수는 것은 찰나" },
	["cu_cz75_precision"] = { name = "제자리", desc = "빨간색과 회색으로 경주를 테마로 한 무늬를 도색했습니다.\n\n기회는 한 번뿐이니 반드시 성공하자고 - 필릭스 라일리, 지휘관" },
	["am_ump_racer"] = { name = "그랑프리", desc = "구시대의 경주를 주제로 도색했습니다.\n\n방탄모를 잊지 마세요!" },
	["am_aqua_flecks"] = { name = "황혼 은하", desc = "am_aqua_flecks" },
	["cu_chronos_g3sg1"] = { name = "크로노스", desc = "화려한 색의 시계 부품 그림을 수전사 방식으로 입힌 후 열전사 데칼로 장식했습니다.\n\n시간은 모든 것을 가능하게 한다." },
	["hy_hades"] = { name = "하데스", desc = "고대 그리스 투구의 무늬를 수전사 방식으로 입혔습니다.\n\n그녀가 인터뷰를 하고 싶었다면 더 쉽게 약속을 잡을 방법이 있었을 텐데 - 방패와 이야기꾼 2장" },
	["hy_icarus"] = { name = "추락한 이카루스", desc = "깃털을 이어붙인 밀랍이 태양 근처에서 녹아버려 추락한 이카루스 설화를 주제로 한 패턴을 수전사 방식으로 입혔습니다.\n\n터너의 배신은 곧 나의 실패다... 이걸 바로 잡아야 해 - 세바스찬 헨케, 저격수" },
	["cu_labyrinth"] = { name = "미노타우루스의 미궁", desc = "등축투영법으로 그린 미로를 수전사 방식으로 입혔습니다.\n\n나에게서 도망칠 수 없어..." },
	["sp_labyrinth1"] = { name = "아스테리온", desc = "등축투영법으로 그려진 미로 디자인을 스텐실로 사용하여 스프레이 페인트로 도색했습니다.\n\n왕도 사람에 불과하다... 숭배받는 것이 사람이 되서는 안된다 - 발레리아 제너, 혁명가" },
	["sp_labyrinth2"] = { name = "길잡이", desc = "등축투영법으로 그려진 미로 디자인을 스텐실로 사용하여 스프레이 페인트로 도색했습니다.\n\n개인적으로는 4번째 판이 좋지만, 손님 맘이지 - 부스, 무기 상인" },
	["sp_labyrinth3"] = { name = "다이달로스", desc = "등축투영법으로 그려진 미로 디자인을 스텐실로 사용하여 스프레이 페인트로 도색했습니다.\n\n이모젠... 그들이 네가 한 짓을 알아챈다면 우리는 모든 것을 잃을 수도 있어 - 문제의 가족사 1장" },
	["cu_medusa_awp"] = { name = "메두사", desc = "고르곤의 모습이 그려져 있습니다. \n\n누구든지 나를 쳐다보기만 하면 죽게 되어 있어" },
	["gs_mother_of_pearl_elite"] = { name = "결투자", desc = "손잡이에 진주층으로 도색하고 어두운 고색을 냈습니다.\n\n껌은 별도 판매" },
	["aa_pandora"] = { name = "판도라의 상자", desc = "에어브러쉬를 사용해 만화적인 영혼의 모습을 그려 넣었습니다. \n\n 이 세상은 화염에 휩싸였지만 적어도 우리에겐 희망이 있어" },
	["cu_poseidon"] = { name = "포세이돈", desc = "물고기자리와 바다의 신 포세이돈의 싸움이 그려져 있습니다.\n\n셋 중에 둘이 죽으면 셋끼리서도 비밀을 나눌 수 있다" },
	["hy_zodiac1"] = { name = "달의 리브라", desc = "별자리를 주제로 한 무늬를 수전사 방식으로 입혔습니다.\n\n오늘 당신은 사랑하는 사람을 만나며 원수를 죽이게 됩니다." },
	["hy_zodiac2"] = { name = "사자자리의 태양", desc = "별자리를 주제로 한 무늬를 수전사 방식으로 입혔습니다.\n\n이번 달은 여러분의 적들에게 불행한 달이 될 것입니다." },
	["hy_zodiac3"] = { name = "해상 기상 통보", desc = "별자리를 주제로 한 무늬를 수전사 방식으로 입혔습니다.\n\n미래는 그 누구도 알 수 없다." },
	["so_khaki_green"] = { name = "카키 그린", desc = "각 부품에 스프레이로 회록색과 검은색을 칠했습니다.\n\n그가 과거에 무엇이었든 지금의 그와는 상관없지. 걱정하지 마, 필릭스... 내가 그를 막겠어 - 기사와 지도자 2장" },
	["cu_anime_aug"] = { name = "아키하바라 입장", desc = "열전사공법을 통해 일본 만화 잡지의 표지가 인쇄된 비닐을 씌웠습니다.\n\n얏타!" },
	["am_bamboo_jungle"] = { name = "수경법", desc = "금속성 도료로 대나무를 주제로 한 무늬를 칠했습니다.\n\n이 총을 만든 유카코가 AK-47의 목재 부품으로 산뜻한 분위기를 어떻게 살렸는지 보이나... 수경법은 자연만큼 아름다우면서 치명적인 거야 - 이모젠, 견습 무기 상인" },
	["hy_bamboo_jungle_ink"] = { name = "대나무 무늬", desc = "붓으로 그린 대나무 무늬를 수전사 방식으로 입혔습니다.\n\n죽순과 관련된 농담을 넣어주세요." },
	["hy_bamboo_jungle_black"] = { name = "대나무 그림자", desc = "붓으로 그린 대나무 무늬를 수전사 방식으로 입혔습니다.\n\n코타로는 이 총에 자신의 아들을 잃었어... 그리고 언젠가는 이 총으로 아들의 복수를 할 테지." },
	["hy_bamboo_jungle"] = { name = "대나무 숲", desc = "붓으로 그린 대나무 무늬를 수전사 방식으로 입혔습니다.\n\n역시 팬더가 없으면 안 될 것 같은데 - 부스, 무기 상인" },
	["am_geometric_steps"] = { name = "아쿠아색 테라스", desc = "금속성 도료로 기하학적인 무늬를 칠했습니다.\n\n고요함이 찾아온다...." },
	["hy_geometric_steps"] = { name = "기하학 계단", desc = "기하학적인 무늬를 수전사 방식으로 입혔습니다." },
	["hy_geometric_steps_green"] = { name = "카운터 테라스", desc = "기하학적인 무늬를 수전사 방식으로 입혔습니다.\n\n사브리, 이걸 가져가... 너처럼 시끄러우니까 말이야. - 카르멘 코시네로, 인질 구출 전문가" },
	["hy_geometric_steps_yellow"] = { name = "테라스", desc = "기하학적인 무늬를 수전사 방식으로 입혔습니다.\n\n너는 발레리아를 너무 믿고 있어... 그게 네가 죽는 이유가 될 거야 - 프란즈 크리겔드, 피닉스 전술가" },
	["hy_kimono_diamonds"] = { name = "네온색 기모노", desc = "기하학적인 무늬를 수전사 방식으로 입혔습니다.\n\n힘들겠지만 미안하네... 터너는 자네 아들같은 사람이었어 - 기사와 지도자 1장" },
	["hy_kimono_diamonds_orange"] = { name = "오렌지색 기모노", desc = "기하학적인 무늬를 수전사 방식으로 입혔습니다.\n\n전통은 강력한 것이다." },
	["hy_kimono_diamonds_red"] = { name = "진홍색 기모노", desc = "기하학적인 무늬를 수전사 방식으로 입혔습니다.\n\n터너, 모든 사람은 망가질 수 있어... 약점을 찾기만 하면 되는 거야 - 배신자와 사실의 추적자 2장" },
	["sp_kimono_diamonds"] = { name = "민트색 키모노", desc = "스프레이로 기하학적인 무늬로 도색했습니다.\n\n여기로, 부스. 내가 만들고 있던 걸 보여주겠어 - 유카코, 총기 제작자" },
	["am_seastorm"] = { name = "자정의 폭풍", desc = "파란색 금속성 베이스 코팅제를 씌우고 폭풍우치는 바다 무늬를 수전사 방식으로 입혔습니다.\n\n난 널 싫어하지 않아, 세바스찬... 널 동정하지 - 체이스 터너, 연합특수부대 배신자" },
	["am_seastorm_blood"] = { name = "일몰의 폭풍 壱(일)", desc = "빨간색 금속성 베이스 코팅제 위에 폭풍우치는 바다 무늬를 수전사 방식으로 입혔습니다.\n\n모두 똑같아 보이지만, 다시 잘 봐봐... 미묘한 차이가 변화를 만들 테니까." },
	["am_seastorm_shojo"] = { name = "일몰의 폭풍 弐(이)", desc = "am_seastorm_shojo" },
	["am_kimono_sunrise"] = { name = "새벽녘", desc = "금속성 도료로 일출을 표현했습니다.\n\n해는 정의로운 자와 그렇지 못한 자 모두에게 자신을 비춘다" },
	["am_so_keycolors"] = { name = "충격 드릴", desc = "am_so_keycolors" },
	["so_aqua"] = { name = "바닷새", desc = "바다색과 황갈색 계통의 단색으로 총기 부품을 하나하나 스프레이 페인트로 도색했습니다.\n\n내꺼?" },
	["cu_ak47_courage_alt"] = { name = "물빛 복수", desc = "돌고래떼의 이미지를 테마로 한 무늬를 도색했습니다.\n\n여기에서 메아리가 울리나요?" },
	["cu_cz75a_chastizer"] = { name = "노란색 재킷", desc = "곡선이 돋보이는 도형들이 그려져 있습니다.\n\n나비처럼 날아서 멈출 수 없는 증오가 끓는 곤충처럼 쏘라" },
	["am_famas_dots"] = { name = "신경망", desc = "금속성 베이스 코팅제를 칠한 다음 반투명한 무늬를 수전사 방식으로 입혔습니다.\n\n나도 알아. 하지만 그렇게 할 수밖에 없었어.... - 문제의 가족사 2장" },
	["cu_galilar_particles"] = { name = "로켓팝 아이스바", desc = "줄 무늬 디자인과 픽셀처럼 표현된 조각들을 커스텀 페인트로 도색했습니다. \n\n무더운 여름날에 딱이죠" },
	["aq_glock18_flames_blue"] = { name = "분젠 버너", desc = "슬라이드에 파란색 녹을 씌운 후 화염처럼 보이도록 약간씩 긁어냈습니다.\n\n똑똑" },
	["cu_m4a4_evil_daimyo"] = { name = "사악한 다이묘", desc = "선명한 그래픽 디자인이 붉은색과 검은색으로 도색했습니다. \n\n카르멘. 그가 어디 있는지 알아냈어... - 오니와 발키리 제 1장" },
	["cu_mp7_nemsis"] = { name = "강적", desc = "금속으로 만들어진 괴물의 그림이 그려져 있습니다.\n\n너를 노리고 온다" },
	["am_mp9_nitrogen"] = { name = "홍옥색 독화살", desc = "검은색 베이스 코팅제를 씌운 뒤 빨간색과 보라색의 금속성 도료를 칠했습니다.\n\n다이아몬드는 여자가 가질 수 있는 최고의 친구라고 누가 그랬지?" },
	["cu_negev_annihilator"] = { name = "떠버리", desc = "괴물을 연상시키는 그림을 그려넣은 뒤 흠집을 냈습니다.\n\n그래, 너 말야! 이리 와!" },
	["cu_nova_ranger"] = { name = "레인저", desc = "붓으로 그린 선 무늬를 수전사 방식으로 입히고 열전사 데칼로 장식했습니다.\n\n점수 보고 있는 사람?" },
	["aq_p2000_boom"] = { name = "권총", desc = "손이 조준하는 듯한 시늉을 하는 그림을 도색했습니다.\n\n이제 더 이상 놀이가 아냐" },
	["cu_ump45_uproar"] = { name = "폭동", desc = "무늬를 수전사 방식으로 입히고 열전사 데칼로 장식했습니다.\n\n예상치 못한 피해를 주기 싫었다면 더 자세하게 일을 계획했어야지.... - 하비엘 알비소, 피닉스의 주먹" },
	["cu_usp_progressiv"] = { name = "토크", desc = "검은색, 흰색 그리고 노란색으로 현대적인 무늬를 칠했습니다.\n\n놈들은 널 조종하고 있어, 코타로. 그걸 알아채지 못한다면, 자넨 생각보다 큰 위험에 빠진 거야.... - 필릭스 라일리, 지휘관" },
	["cu_ak47_winter_sport"] = { name = "전방의 안개", desc = "남극을 연상시키는 다채로운 색으로 칠했습니다.\n\n난 눈에서 일하는 걸 좋아하지...항상 아름다운 흔적이 남거든 - 하비엘 알비소, 피닉스의 주먹" },
	["cu_dualberretta_dragons"] = { name = "두 마리의 용", desc = "레이저로 슬라이드와 총열에 용을 새겨넣었습니다.\n\n마리안은 항상 실비아보다 멋졌지" },
	["cu_famas_lenta"] = { name = "생존 전쟁 Z", desc = "마스킹 테이프를 이용한 스텐실 기법으로 무늬를 넣고 흠집을 냈습니다.\n\n빨갛고 흰색이면서 전자동 사격이 가능한 건 뭘까요?" },
	["gs_g3sg1_flux_purple"] = { name = "범람", desc = "보라색과 은색 도료로 도색했습니다.\n\n계획은 일어나지 않을 일들의 목록일 뿐이다" },
	["gs_galil_nightwing"] = { name = "차디찬 돌", desc = "파란 금속성 도료로 강조했습니다. 개머리판과 탄창에 제작자의 표식이 새겨져 있습니다.\n\n결국에는 스승의 그림자에서 벗어나야 한다" },
	["gs_glock18_wrathys"] = { name = "망령", desc = "슬라이드에 길 잃은 영혼들을 커스텀 페인트로 도색했습니다.\n\n처음엔 육체를 뚫고, 다음엔 그림자를 뚫는다" },
	["gs_m249_nebula_crusader"] = { name = "성운의 성전사", desc = "고전 오락기처럼 보이도록 도색했습니다.\n\n사용할 수 있지만 먼저 25센트부터 넣어야 합니다" },
	["gs_m4a1s_snakebite_gold"] = { name = "황금 코일", desc = "공격하는 뱀 패턴을 수전사 방식으로 입혔습니다.\n\n독사는 어디서든지 습격해 올 수 있다." },
	["cu_mac10_alekhya_duo"] = { name = "랑긴", desc = "고대 이집트를 주제로 도색했습니다.\n\n그의 분노에 찬 시선을 견딜 수 있는가?" },
	["cu_mag7_myrcene"] = { name = "코발트 핵", desc = "미래지향적인 그래픽 디자인의 무늬가 그려져 있습니다.\n\n그를 안전하게 지켜, 카르멘 - 발키리와 애인 제1장" },
	["cu_mp7_classified"] = { name = "특급 배송", desc = "이 총은 -검열-\n\n당신이 원하는 대로 하면 날 보내줄 건가? - 피닉스와 이야기꾼 2장" },
	["hy_p250_crackshot"] = { name = "윙샷", desc = "밝은 색 패턴을 수전사 방식으로 입혔습니다.\n\n가끔 원하는 것을 얻기 위해 미세한 움직임만 있으면 된다 - 프란즈 크리겔드, 피닉스 전술가" },
	["gs_scar20_peacemaker03"] = { name = "청록", desc = "메탈릭 도료로 위장 무늬를 입혔습니다.\n\n모두 잘 들어... 이자키와 코시네로가 실종됐지만, 우리의 임무는 끝나지 않았다: 우리는 알렉스 킨케이드를 구출한다 - 필릭스 라일리, 지휘관" },
	["cu_ssg08_technicality"] = { name = "빅 아이언", desc = "노란색과 주황색 무늬를 수전사 방식으로 입힌 다음 약간 흠집을 내 오래된 것처럼 보이도록 만들었습니다.\n\n결함 있는 승리라도 승리인 것은 분명하다 - 로나 사브리, 신인 정예 요원" },
	["cu_usp_kill_confirmed"] = { name = "처치 확인", desc = "총알이 두개골을 부서뜨리는 그림이 그려져 있습니다.\n\n가슴에 2발, 머리에 한 발" },
	["aq_xm1014_scumbria"] = { name = "등 푸른 고등어", desc = "금속성 도료로 눈에 잘 띄지 않는 무늬를 장식했습니다.\n\n성급하게 굴지 마. 발레리아의 주의를 끌 다른 방법이 있을 거야... - 아버지의 사랑 3장" },
	["cu_ak47_point_disarray"] = { name = "흐트러진 포인트", desc = "수전사 공법을 이용해 기하학적인 모양을 커스텀 페인트로 도색했습니다. \n\n가까이서 보면 혼돈 그 자체지만 멀리서 보면 아름다운... - 프란즈 크리겔드, 피닉스 전술가" },
	["am_aug_jumble"] = { name = "진지변환", desc = "두 가지 색의 매듭 무늬로 도색했습니다. \n\n코타로는 더이상 우리의 보호가 필요 없다 -발키리와 애인 제 2장" },
	["cu_bizon_noxious"] = { name = "연료봉", desc = "탄약이 방사능에 오염된 듯한 느낌을 주도록 도색했습니다.\n\n가끔씩 졸병들이 우세할 때도 있다" },
	["aq_deagle_corinthian"] = { name = "코린트", desc = "레이저 에칭공법으로 코린트식 문양이 새겨져 있으며 가죽 그립을 적용했습니다. \n\n보호하라. 기대하라. 신뢰하라. 인내하라." },
	["cu_fiveseven_retrobution"] = { name = "징벌", desc = "복고풍 무늬를 수전사 방식으로 입혔습니다.\n\n시간이 지나도 언제나 멋진 것들이 있다." },
	["cu_g3sg1_executioner"] = { name = "사형 집행인", desc = "뼈가 소름끼치도록 그려져 있습니다.\n\n나는 항상 전리품을 가져오지 - 하비엘 알비소, 피닉스의 주먹" },
	["gs_m4a4_royal_squire"] = { name = "왕실 기사", desc = "에칭공법으로 공을 들인 동시에 위엄이 느껴지도록 도색했습니다. \n\n우린 전화 통화한 적 없는거야, 알겠지? - 성기사와 아버지 제 1장" },
	["cu_negev_impact"] = { name = "파워 로더", desc = "중장비를 연상시키는 무늬를 수전사 방식으로 입혔습니다.\n\n여왕들에게는 공포의 대상이지만 주교들에게는 사랑을 받는 대상입니다." },
	["am_p2000_imperial_red"] = { name = "황제", desc = "레이저 에칭 공법으로 새겨진 무늬에 빨간색과 노란색의 금속성 도료로 도색했습니다.\n\n내 감정 따윈 아무래도 좋아, 키오. 난 지켜야 할 사업이 있다고. - 부스, 불법 무기 거래상" },
	["cu_p90_shapewood"] = { name = "목각", desc = "손으로 직접 조각하여 사포질을 한 뒤 박판 공정을 거친 개머리판이 달려 있습니다.\n\n족집게 미포함" },
	["gs_sawedoff_necromancer"] = { name = "요릭", desc = "해골 패턴을 수전사 방식으로 입히고 금색 페인트로 장식했습니다. \n\n이것은 햄릿의 로젠 크란츠와 길덴스턴도 가지지 못했던 것이다." },
	["hy_scar20_jungler"] = { name = "봉기", desc = "활기찬 느낌의 녹색 무늬로 도색했습니다.\n\n발레리아가 더욱 대담해졌다... 그녀와 맞설 때가 왔다 - 필릭스 라일리, 지휘관" },
	["gs_sg553_tiger_moth"] = { name = "호랑 나방", desc = "나방의 날개 무늬를 도색했습니다.\n\n발레리아가 대담해지고 있다... 그녀와 맞설 때가 왔다 - 필릭스 라일리, 지휘관" },
	["cu_tec9_avalanche"] = { name = "산사태", desc = "세련되고 미래적인 디자인을 수전사 방식으로 입혔습니다. \n\n그를 상대할 때는 무척 조심해야 한다. 자칫 잘못하면 우리 둘다 다칠 수 있으니... - 뱀과 진리 탐구자" },
	["aq_xm1014_hot_rod"] = { name = "테클루 버너", desc = "불꽃을 추가하기 위해 메탈릭 도료를 사용했습니다.\n\n가끔 분젠으로는 부족하다...." },
	["gs_ak47_supercharged"] = { name = "연료 분사기", desc = "소총의 몸체는 밝은 노란색, 탄창은 기계 내부의 모습을 표현하는 디자인으로 도색했습니다.\n\n내 탄창은 정말 소중하니까!" },
	["cu_bizon_citizen"] = { name = "해양 투광층", desc = "위장 무늬와 형광색 페인트로 도색했습니다. \n\n저건 파도가 부서지는 소리가 아니야..." },
	["aq_deserteagle_kumichodragon"] = { name = "용의 승천", desc = "슬라이드와 그립에 용 무늬가 레이저 에칭공법으로 새겨져 있습니다. \n\n유카코, 난 당신 작품의 팬이오. 얘기 좀 합시다. -헉슬리, 경쟁" },
	["cu_fiveseven_augmented"] = { name = "삼총사", desc = "밝은 파란색 디지털 위장 무늬를 수전사 방식으로 입히고 슬라이드를 도색했습니다. \n\n나는 전쟁 때문에 이득 보는 것이 아니라 인간의 본성 때문에 이득을 보는 것이다... - 부스, 불법 무기 거래상" },
	["gs_glock18_award"] = { name = "왕실 군단", desc = "공들여 새긴 무늬가 장식되어 있습니다.\n\n나오미는 언젠간 발레리아를 위해 목숨을 바쳐야 한다는 걸 받아들였다" },
	["gs_m4a4_pioneer"] = { name = "전투 용사", desc = "구매 영수증의 모든 모서리가 잘렸습니다.\n\nWatchtower에 대해 말하는 이들을 조심하세요." },
	["am_mac10_electricity"] = { name = "청금석 악어", desc = "선명한 악어 가죽 패턴을 수전사 방식으로 입혔습니다.\n\n수상비행기는 불포함" },
	["gs_mag7_praetorian"] = { name = "근위병", desc = "눈에 띄는 금속성 도료로 도색했습니다. \n\n소용돌이가 형성 중입니다" },
	["sp_mp7_impire"] = { name = "악동", desc = "작은 악동 패턴을 수전사 방식으로 입혔습니다. \n\n대규모 전투가 이렇게 귀엽긴 처음이군." },
	["cu_ssg08_necropos"] = { name = "네크로포스", desc = "극악한 룬 문자를 도색했습니다.\n\n목수는 자기 망치가 있어야 하고, 재단사는 자기 물레가락이 있어야 하고..." },
	["gs_tec9_jambiya"] = { name = "잠비야", desc = "레이저 에칭공법으로 중동 문양을 수작업으로 도색했습니다. \n\n샤흐라자드는 1000일 야화를 이야기했지만... 이 것은 당신이 듣는 마지막 이야기가 될 것이다" },
	["gs_usp_voltage"] = { name = "납 도관", desc = "소음기와 슬라이드에 문양이 새겨져 있습니다.\n\n원하는 것을 손에 넣었군. 그게 뭘 의미하는 지 알고 있는가? – 부스, 불법 무기 거래상" },
	["cu_aug_swallows"] = { name = "새 무리의 비행", desc = "새들이 구름을 가르며 날아가는 패턴을 수전사 방식으로 입혔습니다.\n\n어떤 새들은 조화와 평화를 상징하지... 이 새들은 아니야." },
	["cu_bizon_Curse"] = { name = "아누비스의 심판", desc = "이집트를 테마로 한 무늬를 도색했습니다.\n\n트리거의 무게는 깃털보다 가볍습니다" },
	["gs_cz75a_redastor"] = { name = "적색 호크", desc = "붉은색 바탕에 흰색으로 강조된 느낌을 주어 도색했습니다. \n\n부스는 막장 드라마 그 자체야; 우리는 진지하게 토론하기 바쁜데 드라마라니 - 헉슬리, 경쟁" },
	["gs_dualberettas_ventilators"] = { name = "통풍구", desc = "이 권총들은 크롬 도료로 우아하게 도색했습니다. \n\n아티스트를 위한, 아티스트에 의한 무기" },
	["sp_g3sg1_militiaorange"] = { name = "오렌지 충돌 현장", desc = "스텐실 기법과 분사 기법으로 오렌지색이 얼룩지게 도색되어 있는 소총입니다. \n\n정글북의 고약한 호랑이 시어칸처럼" },
	["gs_galilar_incenerator"] = { name = "화력전", desc = "진홍색이 강조되어있고 아이콘은 핸드 페인트되어 있습니다. \n\n부스는 발레리아에게 주기 위해 이 무기를 주문했으며 그것은 피닉스 사태에 대한 껄끄러움을 조금이라도 없애기 위해서였다" },
	["cu_m249_spectre"] = { name = "스펙터", desc = "깔끔하고 세련되게 도색했습니다. \n\n피닉스 내전이 곧 발발할 것이다" },
	["cu_m4a1s_soultaker"] = { name = "찬티코의 불", desc = "강렬한 색상으로 도색되어 있으며 개머리판에 스마일 표시가 두르러지는 디자인입니다. \n\n한 번 내 것은 영원한 내 것" },
	["am_mp9_bioleak"] = { name = "바이오 누출", desc = "화학약품이 흘러내린 듯한 디자인을 초록색으로 도색했습니다. \n\n직접 노출 주의" },
	["hy_p2000_oceani"] = { name = "푸르른 대양", desc = "검정은색 바탕 위에 푸른색 디자인을 도색했습니다.\n\n코타로나 카르멘 소식 아는 사람 누구 없나? - 필릭스 라일리, 지휘관" },
	["cu_p250_asiimov"] = { name = "아시모프", desc = "공상과학을 주제로 디자인한 무늬를 도색했습니다.\n\n내가 사람을 죽이기 위해 소총이 필요하다고 생각한다면 너는 네가 누구에게 이야기하고 있는지 모를 거야. - 미카 바이튼, 피닉스 저격수" },
	["gs_sawedoff_fubar"] = { name = "엉망진창", desc = "여기 저기서 전투 경험이 많은 이 산탄총은 총열에 체크 표시기 새겨져 있습니다. \n\n총소리가 크게 울리고... 그 후로는 정적만이" },
	["cu_sg553_atlas"] = { name = "아틀라스", desc = "현대적인 위장 무늬가 수전사 공법으로 그려져 있습니다.\n\n연합특수부대를 위해 특별히 제작된 디자인이었지만, 헉슬리의 산업 첩보 활동 덕에 피닉스의 전용 디자인이 되어버렸습니다." },
	["gs_ssg08_armacore"] = { name = "유령 십자군", desc = "흰색바탕에 북극의 푸른색으로 포인트를 주어 도색했습니다. \n\n우리가 그들에게 뺏긴 것을 대신할 수 있는 것은 아무것도 없다.... 그러나 그들에게서 우리는 더 많은 것을 빼앗을 수는 있다 - 피닉스와 신입 제 2장" },
	["am_tec9_redblast"] = { name = "재진입", desc = "총열에 불꽃 무늬가 전사되어있다.\n\n정답을 맞추는 것이 늘 재미있진 않군. - 카르멘 콘시네로, 인질 구출 전문가" },
	["cu_ump45_primalsaber"] = { name = "원시의 사브르", desc = "검치호랑이 두개골 그림이 장식되어 있습니다.\n\n모든 포식자는 결국 죽는다" },
	["cu_xm1014_spectrum"] = { name = "검은 넥타이", desc = "공식 행사에서 멋을 내고 싶다면 흑색과 백색 크롬 코팅제가 아름답게 조화를 이루도록 도색된 이 총을 들고 가라.\n\n이자키와 코시네로는 우리가 데리고 있다. 필요 없으면 바로 처치할까? - 용병단과 뱀 조직 제1장" },
	["cu_lore"] = { name = "전설", desc = "매듭 장식으로 도색했습니다." },
	["cu_stonewash"] = { name = "검은 합판", desc = "특수 처리되었고 검은 합판 손잡이를 달았습니다." },
	["am_marbleized_g"] = { name = "감마 도플러", desc = "검은색과 은색 메탈릭 도료를 이용해 마블링 효과를 주었으며 그 위에 윤이 나도록 코팅했습니다." },
	["gs_autotronic"] = { name = "자동 전자 장치", desc = "붉은색으로 도장 처리되어 있고 무게를 가볍게 하도록 철망을 사용합니다." },
	["am_marked_up"] = { name = "자유형", desc = "검정색과 보라색으로 도색된 후 메탈릭 마커로 장식되어 있습니다." },
	["gs_aug_aristocrat"] = { name = "귀족", desc = "밝은 청색의 도장 처리된 바탕에 은 줄 세공이 덧붙여져 있습니다." },
	["gs_awp_phobos"] = { name = "포보스", desc = "금속 패널과 내구성이 높은 전자 부품으로 구성되어 있습니다." },
	["cu_five_seven_daimyo"] = { name = "난폭한 다이묘", desc = "그래픽 디자인이 보라색과 검은색으로 도색했습니다." },
	["cu_glock_wasteland_rebel"] = { name = "황무지 반란군", desc = "낡은 블록 프린팅 기법으로 무늬를 넣었으며 그라피티로 도색했습니다." },
	["gs_m4a1_mecha_industries"] = { name = "기계 산업", desc = "강화된 도자기 패널로 구성되어 있습니다." },
	["cu_m4a4_desolate_space"] = { name = "고립된 공간", desc = "우주를 주제로 하여 도색했습니다." },
	["aq_mac_10_alien_camo"] = { name = "육식 동물", desc = "메탈릭 코팅제를 칠한 다음 붉은색과 보라색 무늬를 수전사 방식으로 입혔습니다.\n\n미리 준비하라." },
	["aq_nova_sci_fi"] = { name = "Exo", desc = "보라색 패턴 바탕에 은색 패널을 주제로 한 무늬를 수전사 방식으로 입혔습니다.\n\n내용물이 중요한 거지." },
	["gs_p2000_imperial_dragon"] = { name = "황제의 용", desc = "붉은색 메탈릭 도료를 사용하여 용을 주제로 한 무늬로 도색했습니다." },
	["gs_p250_metal_panels"] = { name = "철갑", desc = "깊이 새긴 메탈 패널로 장식했습니다." },
	["gs_p90_full_throttle"] = { name = "절단기", desc = "주황색과 파란색 메탈릭 도료를 사용하여 불꽃을 주제로 한 무늬로 도색했습니다." },
	["gs_pp_bizon_harvester"] = { name = "하베스터", desc = "생물 기계학에서 영감을 얻어 제작되었습니다.\n\n생체역학 실험은 잘 마무리되었다." },
	["cu_r8_cybersport"] = { name = "리부트", desc = "붉은색을 강조색으로 하여 은색 및 검은색 패널로 장식했습니다." },
	["cu_sawed_off_lime"] = { name = "라임라이트", desc = "라임빛 녹색과 흰색의 하강하는 곡선을 사용하여 도색했습니다." },
	["gs_scar20_bloodsport"] = { name = "유혈 스포츠", desc = "붉은색과 검은색 바탕을 수전사 방식으로 입히고 흰색 로고 데칼로 장식했습니다.\n\n CT 측 스폰서는…" },
	["cu_sg553_aerial"] = { name = "공중", desc = "흰색 날개를 전사한 은색과 노란색으로 도색했습니다." },
	["am_tec_9_sea_salt"] = { name = "만년설", desc = "파란색과 흰색의 추상적인 무늬를 수전사 방식으로 입혔습니다.\n\n적을 침몰하라." },
	["bloodhound_black_silver"] = { name = "숯검댕이", desc = "유행을 타지 않는 부드러운 검은색 가죽으로 만들었습니다.\n\n작전 도전 주화도 좋지만 등주먹으로 펀치를 날릴 때는 도움이 안 되지. - 카르멘 콘시네로, 인질 구출 전문가" },
	["bloodhound_snakeskin_brass"] = { name = "뱀가죽", desc = "소가죽과 뱀가죽으로 만들었습니다.\n\n솔직해 말해서, 세르게이를 보고 싶어하는 사람은 없어. - 한스 크리겔, 사조단 작전 참모" },
	["bloodhound_metallic"] = { name = "구리", desc = "포금과 구리 색으로 가죽을 처리했습니다.\n\n블러드하운드 작전으로 악당 스나이퍼 체이스 터너를 끝장냈죠." },
	["bloodhound_guerrilla"] = { name = "게릴라", desc = "위장 무늬가 있는 합성피혁과 갈색 가죽으로 만들었습니다.\n\n발레리아가 보복을 계획해. 이건 의심의 여지가 없어... - 세바스찬 헨케, 스나이퍼" },
	["handwrap_leathery"] = { name = "가죽", desc = "가죽과 거즈에 사악한 의도를 더해 만들었습니다.\n\n악명을 드높이자." },
	["handwrap_camo_grey"] = { name = "전나무 디지털 위장", desc = "겉감에서는 스크린 인쇄 방식으로 회색 디지털 위장 무늬를 표현했습니다.\n\n빈 수레가 요란한 법. 말보다 행동으로..." },
	["handwrap_red_slaughter"] = { name = "살인마", desc = "선홍빛으로 염색하여 어디까지 천이고 어디부터 핏자국인지 알아볼 수 없는 핸드랩입니다.\n\n적은 역시 내 손으로 직접 처리해야 제맛이죠." },
	["handwrap_fabric_orange_camo"] = { name = "황무지", desc = "여기저기서 주워온 재료로 직접 만든 손 보호 장비입니다.\n\n필릭스는 연합특수부대의 무허가 권투 리그를 눈감아주고 있습니다." },
	["slick_black"] = { name = "달빛 직조", desc = "짙은 암청색 스웨이드와 새까만 가죽을 엮어 만든 장갑으로 유행을 타지 않는 스타일입니다.\n\n손에 피 묻히지 말아라. 비유를 하자면 말이지." },
	["slick_military"] = { name = "수송대", desc = "부드러운 갈색 가죽과 염색한 스웨이드로 만들었습니다.\n\n가끔 특히 신중을 기해야 하는 작전이 있으니까요." },
	["slick_red"] = { name = "선홍빛 직조", desc = "검은색 스웨이드와 붉은색 가죽을 엮어 만든 장갑으로 유행을 타지 않는 스타일입니다.\n\n너만의 표식을 남기되 지문은 남기지 않도록 주의해라." },
	["slick_snakeskin_yellow"] = { name = "방울뱀", desc = "황갈색 가죽과 검은색 가죽, 뱀가죽으로 만들었습니다.\n\n보급형은 초짜들이나 쓰는 거지... - 이모젠, 견습 무기 상인" },
	["sporty_green"] = { name = "미로 정원", desc = "초록색과 흰색이 조화를 이루는 장갑으로 이카루스 스포츠에서 제작했습니다.\n\n태양을 향해 날아오른 이카루스처럼 용감하게!" },
	["sporty_purple"] = { name = "판도라의 상자", desc = "검은색과 보라색이 조화를 이루는 장갑으로 손바닥 부분에 은은한 무늬가 그려져 있습니다.\n\n손에 땀이 많이 나는 폭파 전문가에게 필수 아이템!" },
	["sporty_light_blue"] = { name = "초전도체", desc = "파란색과 검은색이 조화를 이루는 장갑으로 전설이 되고 싶은 분에게 안성맞춤입니다.\n\n파란 바다의 전설" },
	["sporty_military"] = { name = "불모의 땅", desc = "여러 가지 갈색과 은은한 위장 무늬로 어디서나 돋보이는 장갑입니다.\n\n뛰어난 영웅이라면 남다른 패션 감각을 지녀야죠. - 로나 사브리, 신인 정예 요원" },
	["specialist_ddpat_green_camo"] = { name = "숲 디지털 위장", desc = "손으로 한 땀 한 땀 완성한 가죽과 튼튼한 위장 무늬 직물로 만들었습니다.\n\n손에 흙 묻히는 것을 질색하는 사람들이 있으니까요." },
	["specialist_kimono_diamonds_red"] = { name = "진홍색 기모노", desc = "검은색 바탕에 새빨간 다이아몬드 무늬로 포인트를 주었습니다.\n\n갑자기 화가 치밀어 오를 때" },
	["specialist_emerald_web"] = { name = "에메랄드 거미줄", desc = "손등 부분에 거미줄 모양을 표현했습니다.\n\n절제는 성공을 낳는다." },
	["specialist_white_orange_grey"] = { name = "기초", desc = "흰색과 검은색이 조화를 이루는 장갑으로, 주황색으로 포인트를 주었습니다.\n\n소문에 의하면 토르튜가 아시모프 무기를 보고 영감을 받았다고 합니다." },
	["motorcycle_basic_black"] = { name = "월식", desc = "토르튜 이름을 세상에 알린 바로 그 검은색 원조 디자인으로 유행을 타지 않습니다.\n\n중년의 위기가 아니에요." },
	["motorcycle_mono_boom"] = { name = "쾅!", desc = "이 장갑은 만화책에 나오는 의성어로 가득합니다.\n\n펑! 쾅! 퍽!" },
	["motorcycle_mint_triangle"] = { name = "스피어민트", desc = "흰색 가죽에 빨간색 바늘땀으로 장식했으며 세련되면서도 편합니다.\n\n돈을 그렇게 많이 받는데, 나 같으면 민트라도 하나 끼워주겠다... - 필릭스 라일리, 지휘관" },
	["motorcycle_triangle_blue"] = { name = "쿨민트", desc = "북극의 색으로 이루어진 기하학적 디자인으로, 연한 파란색 가죽으로 포인트를 주었습니다.\n\n전투가 이렇게 신선하게 느껴지다니!" },
	["slick_snakeskin_white"] = { name = "킹 스네이크", desc = "하얀색 가죽과 뱀 가죽으로 제작되었습니다." },
	["slick_plaid_purple"] = { name = "왕실 격자 무늬", desc = "장엄한 색을 띄는 플란넬과 가죽으로 만들어진 한 쌍의 장갑입니다." },
	["slick_stitched_black_orange"] = { name = "추월", desc = "이 클래식한 검은색과 황갈색 가죽 장갑으로 속도의 정수를 느낄 수 있습니다." },
	["slick_slick_stitched_green_grey"] = { name = "레이싱 그린", desc = "초록색에 쉐브론 문양과 회색 가죽을 엮어 포인트를 주었습니다." },
	["sporty_poison_frog_blue_white"] = { name = "해저", desc = "파란색과 하얀색 합성섬유로 만든 이 장갑은 빨리 마르고 공기가 잘 통합니다." },
	["sporty_poison_frog_red_green"] = { name = "브론즈 모프", desc = "빨간색과 초록색 합성섬유로 만든 이 장갑은 빨리 마르고 공기가 잘 통합니다." },
	["sporty_black_webbing_yellow"] = { name = "오메가", desc = "검은색 합성 섬유로 만든 장갑에 노란색으로 특징을 강하게 살렸습니다." },
	["sporty_blue_pink"] = { name = "바이스", desc = "합성섬유로 만든 이 장갑은 눈에 띄는 분홍색과 파란색의 기능성 소재로 제작되었습니다." },
