	["motorcycle_basic_green_orange"] = { name = "거북이", desc = "초록색과 회색으로 된 한 쌍의 장갑에 주황색으로 특징을 살렸습니다." },
	["motorcycle_yellow_camo"] = { name = "수송", desc = "미묘한 디지털 위장 무늬가 있는 한 쌍의 장갑입니다." },
	["motorcycle_trigrid_blue"] = { name = "폴리곤", desc = "인조 소재와 다양한 종류의 파란색 가죽을 혼합하여 제작되었습니다." },
	["handwrap_leathery_fabric_blue_skulls"] = { name = "코발트 해골", desc = "해골 무늬가 인쇄된 파란색 바이어스 테이프로 만든 핸드 랩입니다." },
	["handwrap_leathery_fabric_geometric_blue"] = { name = "중복 인쇄", desc = "비스듬하게 테이핑된 검은색 핸드랩에 고무 처리된 기하학적 무늬가 인쇄되어 있습니다." },
	["handwrap_leathery_ducttape"] = { name = "접착 테이프", desc = "놀라울 정도로 내구성이 뛰어나지만 통풍이 그리 좋지는 않은 핸드 랩입니다." },
	["handwrap_leathery_fabric_green_camo"] = { name = "밀리터리", desc = "숲 위장 무늬로 염색된 바이어스 테이프로 만든 핸드 랩입니다." },
