	["gs_AUG_Syd_Mead"] = { name = "시드 미드", desc = "유명 디자이너 시드 미드가 디자인했습니다.\n\n과거에 그는 우리에게 미래의 비전을 제시해 주었고 현재의 그는 미래를 우리 손에 쥐여주었습니다..." },
	["gs_CZ75_Tread"] = { name = "각인", desc = "에칭 공법을 사용해 손으로 직접 공들여 완성했습니다.\n\n오뜨 꼬뛰르 총포가 체코 공화국을 단번에 점령했습니다" },
	["aq_Desert_Eagle_Constable"] = { name = "지시", desc = "크립을 막기위한 미래의 법률 적용을 돕기 위해 디자인되었습니다.\n\n당신 차례입니다..." },
	["gs_FAMAS_Rally"] = { name = "롤 케이지", desc = "전사 기법으로 진흙탕을 구른 차량의 모습과 흡사하게 표현했습니다.\n\n안전 제일" },
	["aq_Five_Seven_Scumbria"] = { name = "고등어", desc = "줄무늬가 따로 따로 도색했습니다.\n\n필릭스, 그를 그냥 죽게 내버려 두지 마. 네가 그를 살려야 해 - 팔라딘과 그의 딸, 제 1장" },
	["gs_G3SG1_Ventilator"] = { name = "통풍구", desc = "우아한 검정색과 크롬 도색을 하였고 고무로 된 맞춤 개머리판을 부착했습니다.\n\n걱정마, 나오미. 필릭스가 후회하도록 해줄게 - 미카 바이튼, 피닉스 저격수" },
	["cu_Glock18_Weasel"] = { name = "족제비", desc = "말썽꾸러기 족제비 바비의 모습을 수전사 방식으로 입혔습니다. 바비를 건드리지 마세요.\n\n귀엽게 생겼다고 해서 성질이 없는 건 아니랍니다." },
	["cu_MAG7_Tribal"] = { name = "암각화", desc = "마치 암각화와 같은 느낌을 주도록 도색했습니다.\n\n동굴에 살았던 최초의 원시인이 돌을 드는 방법을 배웠을 때부터 갈등은 시작되었지 – 부스, 무기 거래상" },
	["cu_MP9_Narcis"] = { name = "에어락", desc = "흰색으로 도색하고 탄창은 선명한 붉은 색으로 칠했습니다.\n\n총으로 건배하는 자들에게 제격입니다" },
	["hy_Negev_Dazzle"] = { name = "눈부심", desc = "파란색과 흰색으로 얼룩말 줄무늬 전사로 전체를 꾸몄습니다.\n\n드디어, 디테일을 사랑하는 분을 위한 무기가!" },
	["cu_P90_Grimm"] = { name = "죽음의 신", desc = "죽음의 망령 디자인을 수전사 방식으로 입히고 마른 붓을 사용하여 초록색으로 장식했습니다.\n\n이모젠... 무기를 파는 건가 아니면 벨벳 포스터를 파는 건가? – 로나 사브리, 신인 정예 요원" },
	["gs_SCAR20_Powercore"] = { name = "파워코어", desc = "초록색 바탕에 노란색 포인트가 있는 디자인으로 도색했습니다. 그리고, 빠르게 체크아웃할 수 있도록 USB 코드를 부여했습니다.\n\n첫 번째 규칙 – 도전은 만족을 요구한다" },
	["cu_SG556_Triarch"] = { name = "삼원형", desc = "이 소총은 왕실의 위탁을 받은 무기로서 사막 위장 무늬 디자인으로 도색했습니다.\n\n당신의 마음을 숨기지 말고 보여주세요." },
	["gs_TEC9_Supercharged"] = { name = "연료 분사기", desc = "몸통을 노란색으로 도색하고 탄창에는 엔진 벨트 패턴을 수전사 방식으로 입혔습니다. 손바느질로 제작한 가죽 그립을 더해 시애틀 클래식을 완성했습니다.\n\n피트 크루는 불포함" },
	["cu_UMP45_Metritera"] = { name = "브리핑", desc = "지형도를 수전사 방식으로 입혔습니다.\n\n이것은 매우 짧은 회의가 될 것이다..." },
	["hy_XM1014_Fractal_Blue"] = { name = "슬립스트림", desc = "스모키한 회색과 현란한 푸른색으로 도색했습니다.\n\n참아라, 엘리어트. 그들이 해줘야 할 일이 아직 남아있다... - 용병단과 뱀 조직 제 2장" },
	["am_czv2_mf"] = { name = "중합체", desc = "슬라이드에 중합체 구조를 닮은 무늬를 전사했습니다.\n\n이제 피닉스를 상대하는 게 아니야. 우리 자신을 책임질 줄은 안다고... - 팔라딘과 그의 딸, 제2장" },
	["aq_glock_dark-fall"] = { name = "철 세공", desc = "레이저 에칭공법으로 덩굴과 가시를 표현하여 슬라이드를 장식했습니다.\n\n소홀히 하는 순간 정원은 잡초밭이 된다." },
	["cu_ssg08_dragonfire_scope"] = { name = "용의 불길", desc = "무시무시한 용이 내뿜는 불길 디자인을 수전사 방식으로 입혔습니다.\n\n쌓아 놓은 금은보화 위에 앉아서 뺏으려고 다가오는 자를 기다려라." },
	["gs_dual_berettas_golden_venice"] = { name = "왕족의 배우자", desc = "손잡이는 초록색 진주 무늬로, 측면은 그리핀 문양으로, 그리고 슬라이드는 레이저 에칭공법으로 장식되어 있습니다. 이보다 더 우아한 권총은 찾기 힘드실 겁니다.\n\n얼굴을 바꾸고 비둘기를 날리다." },
	["gs_famas_mecha"] = { name = "기계 산업", desc = "공상 과학 영화에 나올 것 같은 차갑고 삭막한 느낌의 흰색과 검은색 디자인입니다.\n\n삐죽삐죽한 파란 머리와 로봇 친구는 포함되어 있지 않아." },
	["gs_final_pooldeadv2"] = { name = "새털구름", desc = "메탈릭 파란색으로 도색하고 해골 무늬를 전사했습니다.\n\n구름 속에서 이만 헤어나와." },
	["gs_g3sg1_viper_yellow"] = { name = "스팅어 미사일", desc = "노란색과 회색으로 포인트를 주었습니다.\n\n고통은 잠깐일 뿐. 그 후엔 아무것도 없다." },
	["cu_galil_ar-camo"] = { name = "검은 모래", desc = "검은색과 위장 무늬로 도색했습니다.\n\n미카 바통이 연합특수부대를 상대로 전투를 시작하려고 한다." },
	["aa_hide-mp9"] = { name = "모래 비늘", desc = "회색과 황갈색으로 도색했습니다.\n\n보복을 준비해야 해. - 필릭스 라일리, 지휘관" },
	["cu_m4a1_flashback"] = { name = "과거 회상", desc = "인간의 이중성이 표현되도록 도색했습니다.\n\n내 속엔 내가 너무도 많아." },
	["gs_m4a4_sector"] = { name = "분위기 파괴자", desc = "Sektor Industry의 총기로써 노란색과 초록색으로 매끈하게 도색했습니다.\n\n어디 가나 그런 사람은 한 명씩 꼭 있죠." },
	["am_mag7_malform"] = { name = "수중 음파 탐지기", desc = "터키석 색깔을 스텐실 기법으로 가볍게 도색했습니다.\n\n맞습니다. 패배를 맛보는 것도 경험이죠. – 오니와 발키리 제2장" },
	["am_nova_sand"] = { name = "미국독도마뱀", desc = "도마뱀 가죽 모양을 전사하여 전체를 도색했습니다.\n\n용병한테 포로로 잡혀 본 적이 없나 봐. – 오니와 발키리 제1장" },
	["gs_p2000-sport"] = { name = "잔디", desc = "손잡이와 슬라이드는 메탈릭 초록색으로 도색했습니다.\n\n초록은 동색." },
	["gs_p90_shallow_grave"] = { name = "얕은 무덤", desc = "손잡이는 룬 문자가 새겨진 돌 모양으로 도색하였고 개머리판에는 해골 패턴을 수전사 방식으로 입혔습니다.\n\n그렇게 시체가 많이 쌓이는데 무덤 팔 시간이 어디 있겠어요?" },
	["cu_usp_cyrex"] = { name = "사이렉스", desc = "미래의 느낌을 주고자 빨간색 하이라이트를 살짝 더했습니다.\n\n필릭스, 이모젠은 과거에 불과하고 내가 바로 미래야. - 헉슬리, 경쟁 상대" },
	["cu_wp_sawedoff"] = { name = "황무지 공주", desc = "누가 총열에 살인한 횟수를 기록해 놓았네요.\n\n세상을 다 가진 소시오패스에게 딱 알맞은 선물입니다." },
	["gs_ak47_bloodsport"] = { name = "유혈 스포츠", desc = "붉은색과 검은색 바탕을 수전사 방식으로 입히고 흰색 로고 데칼로 장식했습니다.\n\n교대 운전자 구함" },
	["cu_awp_psychopath"] = { name = "꿈꾸는 열병", desc = "검은색 배경에 분홍색, 파란색, 보라색으로 그려진 그림과 문구들로 도색했습니다.\n\n델리륨은 위험한 물건이지." },
	["hy_bizon_torn_green"] = { name = "정글 질주", desc = "녹색과 노란색 패턴을 수전사 방식으로 입혔습니다.\n\n나를 따르라." },
	["cu_blueprint_scar"] = { name = "청사진", desc = "건축가의 청사진과도 같은 느낌을 위하여 파란색의 테마로 도색했습니다.\n\n잘 만든 도면은 규모에 알맞게 그려진다." },
	["gs_cz_snakes_purple"] = { name = "향류", desc = "보라색과 금색으로 된 9마리의 뱀 디자인을 도색했습니다.\n\n밀물을 조심하라." },
	["gs_m4a1_decimator"] = { name = "학살자", desc = "강렬한 파란색과 분홍색을 강조하여 도색했습니다.\n\n미래주의" },
	["cu_desert_eagle_corroden"] = { name = "불꽃 산화물", desc = "녹이 스며든 색상과 하얀색 디테일로 도색했습니다.\n\n아무 것도 아닌 사포 조각일지라도, 이걸로 고칠 수 없는 건 없다." },
	["cu_fiveseven_vein"] = { name = "모세혈관", desc = "복잡한 청록색과 산호색으로 이루어진 페인트로 도색했습니다.\n\n100 중에 20" },
	["sp_galil_wave"] = { name = "핏빛 쓰나미", desc = "적갈색 바탕에 푸른 타이드 문양을 스텐실 기법으로 도색했습니다.\n\n웨스트워드 브레이크" },
	["sp_m249_frog_original"] = { name = "에메랄드 독화살", desc = "독화살개구리에서 영감을 얻은 패턴을 수전사 방식으로 입혔습니다.\n\n자연 보전은 우선 사항이 아니다." },
	["sp_mp7_tribal_yellow"] = { name = "전쟁 나팔", desc = "노란색 바탕에 파란색 패턴을 스프레이 페인트로 도색했습니다.\n\n나만의 전투의 함성" },
	["am_p250_sputnik"] = { name = "파란 물결", desc = "검은색 바탕에 양극 산화 처리된 청회색 패턴으로 도색했습니다.\n\n조약돌이 되어라." },
	["aa_mac10_the_last_dive"] = { name = "마지막 다이빙", desc = "초록색과 주황색 패턴을 무작위로 도색했습니다.\n\n혼돈 속에는 아름다움이 있다." },
	["gs_ump_abyss"] = { name = "사형집행", desc = "청록색을 도색하여 사실적인 3차원 효과를 주었습니다.\n\n차곡차곡" },
	["cu_usps_noir"] = { name = "네오 누와르", desc = "흑백의 배경 위에 파란색과 자홍색으로 그려진 여성의 모습으로 커스텀 페인팅되었습니다.\n\n'네온 불빛에 흠뻑 젖은 그녀는 하늘을 찌르는 듯한 마천루 아래 누워 있다.'" },
	["aq_xm_leaf_fade"] = { name = "사계절", desc = "다양한 색상의 무작위 나뭇잎 패턴으로 도색했습니다.\n\n봄, 여름, 가을, 드물게 겨울" },
	["aq_sawedoff_zander2"] = { name = "잔더", desc = "인기 있는 물고기 게임과 비슷한 느낌이 나도록 도색했습니다.\n\n이건 무조건 소장각이야." },
	["hy_p90_barebones_blue"] = { name = "죽음의 손아귀", desc = "파란색 바탕에 손들이 겹치는 패턴을 수전사 방식으로 입혔습니다.\n\n죽음을 쉽게 받아들이지 마라." },
	["cu_usps_blueprint"] = { name = "청사진", desc = "건축가의 청사진 같은 느낌이 나도록 파란색 테마로 도색했습니다.\n\n때로는 최선의 계획이 틀어지기도 한다." },
	["gs_ak_colony01_red"] = { name = "궤도 Mk01", desc = "빨간색과 검은색 디자인으로 도색했습니다.\n\n식민지 1호에서 해방" },
	["gs_dualberettas_cobra"] = { name = "코브라 공격", desc = "양쪽 그립에 코브라 디자인을 도색했습니다. 슬라이드에는 네온 색 독액 문양을 도색했습니다.\n\n그들은 항상 반격하지." },
	["sp_famas_macabre"] = { name = "죽음의 무도", desc = "노란색 바탕에 녹색 디자인을 스텐실 기법으로 도색했습니다.\n\n소름 끼치는 발견" },
	["cu_fiveseven_hyperbeast"] = { name = "괴수", desc = "야수와 같은 이미지를 현란한 색상으로 도색했습니다.\n\n우리는 모두 괴물이야." },
	["cu_galil_candychaos"] = { name = "사탕 가게", desc = "솜사탕을 주제로 한 색상과 다양한 패턴을 사용하여 도색했습니다.\n\n충돌에 대비하라." },
	["cu_awp_hannya"] = { name = "퇴마", desc = "다채로운 색상의 사무라이와 퇴마의 이미지를 도색했습니다.\n\n악마를 마주하라." },
	["cu_m4a1s_metritera"] = { name = "브리핑", desc = "지형도를 수전사 방식으로 입혔습니다.\n\n누구에게나 계획이 있지…" },
	["cu_m4a4_hellfire"] = { name = "지옥불", desc = "불과 사슬에 둘러싸인 악마로 보이는 생물을 도색했습니다.\n\n그리고 조약돌까지..." },
	["am_mac10_aloha"] = { name = "알로하", desc = "에어브러시로 빨간색 히비스커스 패턴을 도색했습니다.\n\n특별함을 지니도록..." },
	["am_mag7_caustic"] = { name = "센물", desc = "회색의 양극 산화 처리 패턴으로 도색했으며 파란색 하이라이트로 마감되었습니다.\n\n“저건 안 마실 거야”" },
	["aq_ump45_flameflower"] = { name = "강철 꽃송이", desc = "강철 느낌의 색상으로 장례식용 장미를 도색했습니다.\n\n추모하는 마음으로" },
