	["gs_mac10_exo_pipes"] = { name = "파이프 다운", desc = "마치 빨간 튜브로 채워진 것처럼 보이도록 도색했습니다.\n\n총기의 이면을 보라." },
	["aa_p250_gravediggers"] = { name = "네버모어", desc = "해골 새 패턴을 밝은 주황색으로 도색했습니다.\n\n쪼는 순서대로 자리를 잡으세요." },
	["cu_sawedoff_black_sand"] = { name = "검은 모래", desc = "황갈색과 검은색의 군용 위장 패턴으로 도색했습니다.\n\n미카는 강하게 반대하는 자들에게 아무런 설명도 하지 않았어." },
	["gs_sg553_over_heated"] = { name = "위험 사격", desc = "금속 느낌의 불을 테마로 한 디자인을 수작업으로 도색했습니다.\n\n불 조심해." },
	["gs_tec9_fubar"] = { name = "엉망진창", desc = "오랜 사용으로 인해 생긴 그을림과 군용 위장 무늬 디자인으로 도색했습니다.\n\n넌 '중고 무기'라고 부르겠지만 우린 '최애 무기'라고 표현하지." },
	["cu_usp_flashback"] = { name = "과거 회상", desc = "인간의 이중인격을 표현하는 문구와 디자인으로 도색했습니다.\n\n기분이 너무너무 째지는군!" },
