	["cu_five_seven_angry"] = { name = "화난 무리", desc = "거리 예술에서 영감을 얻은 밝은 색상의 디자인으로 도색했습니다.\n\n“내부의 목소리를 부탁합니다.'" },
	["cu_awp_viper"] = { name = "아테리스", desc = "검은색 바탕에 눈에 띄는 초록색과 파란색의 숲살무사를 도색했습니다.\n\n치명적일 정도로 아름다움" },
	["cu_tec9_bamboo"] = { name = "대나무", desc = "대나무 디자인을 도색했습니다.\n\n속지 마라." },
	["gs_mac10_fish_bait"] = { name = "백어", desc = "검은색 바탕에 하얀색으로 물고기 비늘, 뼈, 그리고 갈고리를 도색했습니다.\n\n잡히면 죽는다." },
	["gs_deagle_exo"] = { name = "라이트 레일", desc = "착시 효과를 일으키고 무기의 형상이 바뀌는 것처럼 보이도록 도색했습니다.\n\n파워 서플라이는 별도로 판매합니다." },
	["sp_galil_akoben"] = { name = "아코벤", desc = "하얀색 바탕 위에 녹슨 듯한 색의 패턴을 스프레이 페인트로 도색했습니다.\n\n나만의 전투 함성" },
	["cu_revolver_oppressor"] = { name = "두개골 분쇄자", desc = "노란색 불꽃을 빨간색과 흰색으로 장식한 디자인을 수작업으로 도색했습니다.\n\n\"무법천지\"" },
