	["cu_mp7_racketeer"] = { name = "장난기", desc = "붉은색 바탕에 웃는 입모양 디자인을 검은색과 흰색으로 도색했습니다.\n\n전혀 좋지 않아." },
	["aq_p250_verdigris"] = { name = "버디그리", desc = "구리 외피가 여러 성분에 노출되어 생긴 초록색 물 자국 디자인으로 도색했습니다.\n\n네가 숨 쉬는 공기로부터..." },
	["cu_p90_offworld"] = { name = "오프월드", desc = "파란색 페인트를 이전 보다 더 많이, 여러 번 칠하여 도색했습니다.\n\n이 표면은 절대 마르지 않아…" },
	["cu_xm1014_incinerator"] = { name = "악어", desc = "악어가 불을 뱉는 모습을 연상시키는 디자인으로 도색했습니다.\n\n붐. 뱀. 밥... 파우!" },
	["aa_ump45_moonrise_sunset"] = { name = "월출", desc = "보름달과 자홍색 밤하늘 아래 도시 전경 디자인을 도색했습니다.\n\n북극성을 보아라." },
	["cu_bizon_road_warrior"] = { name = "엠바고", desc = "청록색 바탕에 1950년대 하바나에서 볼 수 있었던 다양한 광고를 테마로 만들었습니다.\n\n나중에 봐, 친구!" },
	["gs_ak47_nibbler"] = { name = "랫 로드", desc = "기존의 파란색을 덧칠하고, 새롭게 칠하여 오래된 황무지의 폐차장에서 복구된 느낌이 나도록 도색했습니다.\n\n도망칠 곳은 없다." },
	["cu_aug_whitefang"] = { name = "북극늑대", desc = "북극 늑대의 하얀 털이 연상되도록 도색했습니다.\n\n매 라운드는 1 대 5 싸움이라니까." },
