	["gs_mp5_etch"] = { name = "애시드 워시", desc = "조립하기 전에 금속 느낌의 파란색 탑코트를 산화시킨 다음 황동 부품을 추가했습니다.\n\n블랙 앤 블루, 그리고 곳곳에 황동" },
	["gs_tec9_decimator"] = { name = "데시메이터", desc = "강렬한 파란색과 분홍색을 강조하여 도색했습니다.\n\n4개월 할부로 월 미국 달러 124.95에 이용 가능합니다!" },
	["cu_nova_featherswing"] = { name = "깃털", desc = "나무로 제작된 총열과 개머리판에 갈색 깃털이 그려진 디자인을 커스텀 페인트로 도색했습니다.\n\n깃털마냥 심기를 곤두세울거야" },
	["cu_g3sg1_blacksand"] = { name = "검은 모래", desc = "황갈색과 검은색 군용 위장 무늬로 도색했습니다.\n\n연합특수부대가 이기고 있었기 때문에, 미카는 더욱... 공격적인 방식을 쓰기로 마음을 바꿨지." },
	["gs_r8_memento"] = { name = "증표", desc = "상아색 손잡이의 고급스러움이 총기 몸체와 총구, 실린더의 복잡한 금속 세공과 잘 어울리도록 도색했습니다.\n\n신난다!" },
	["cu_mp7_replica"] = { name = "네온 플라이", desc = "래미네이트 처리되었으며 다양한 네온 색상으로 도색했습니다.\n\n'로이 지 비브? 들어본 적 없어.'" },
	["cu_p2000_obsidian"] = { name = "옵시디언", desc = "용암이 식으면서 흑요석으로 굳어지는 디자인을 도색했습니다.\n\n'유리 대포'라는 말에 완전히 새로운 의미를 부여해 봐." },
	["gs_dual_elites_rose"] = { name = "균형", desc = "검은 바탕에 은으로 필리그리 문양을 세공하고 장미를 붉은색으로 도색했습니다.\n\n모든 것에 있어서" },
	["sp_scar20_striker_dust"] = { name = "상처", desc = "스프레이 페인트로 무작위의 줄무늬 패턴을 도색했습니다.\n\n갈갈이 찢어버리기 위해..." },
	["cu_sg553_reactor"] = { name = "콜로니 IV", desc = "주황색과 노란색 타이드과 방독면 디자인 위에 해골 개미 디자인을 붉은색으로 도색했습니다.\n\n그들 세상의 일부가 되어라." },
	["gs_mac10_stalker"] = { name = "스토커", desc = "파란 네온 빛의 고양이와 노란색과 검은색 줄무늬로 도색했습니다.\n\n여기야 야옹아, 야옹..." },
	["cu_ssg08_tickler"] = { name = "블러드 샷", desc = "초록색 촉수와 이빨, 하나의 눈알로 구성된 괴물 디자인을 도색했습니다.\n\n너를 더 잘 보기 위해서란다." },
	["gs_m249_warbird_veteran"] = { name = "군용기", desc = "기존의 파란색과 노란색 열 차폐를 노후화와 오랜 사용으로 인해 외관이 손상된 느낌이 나도록 도색했습니다.\n\n늙은 새는 쉴 자격이 있다." },
	["aq_m249_aztec"] = { name = "아즈텍", desc = "아즈텍 유적에서 발굴된 M249로, 일부 녹슬었고 이끼가 덮여 있습니다.\n\n절대 흔들다리를 믿지 마라" },
	["gs_dual_elites_classic"] = { name = "엘리트 1.6", desc = "클래식한 듀얼 베레타의 측면에 ELITE 로고가 새겨져 있습니다.\n\n나 때는 이랬지..." },
	["cu_famas_nuke_tension"] = { name = "폐원자로", desc = "이 도색은 Counter-Strike 1.6의 de_nuke 맵에 등장하는 요소에서 영감을 얻었습니다.\n\n습득 시 시더 크릭 원자력 발전소로 보내주시기 바랍니다." },
	["cu_tec9_flash"] = { name = "섬광탄 투척", desc = "섬광탄처럼 보이도록 도색했습니다.\n\n답장: 조던과 함께하는 댄스 교실" },
	["cu_fiveseven_gsg9"] = { name = "동료", desc = "위장 패턴과 가늠자 쪽에 대테러부대 로고를 도색했습니다.\n\n후방은 내게 맡겨!" },
	["gs_p250_inferno"] = { name = "인페르노", desc = "인페르노의 정신을 반영하는 디자인으로 도색했습니다.\n\n\"But look at the time!(끝나기 일보 직전입니다!)\"" },
	["gs_mac10_dust_crate"] = { name = "클래식 크레이트", desc = "Counter-Strike 1.6의 de_dust2 맵에서 등장한 녹색 금속 상자에 영감을 얻은 디자인으로 도색했습니다.\n\nB 지점으로 가. 절대로 멈추지 마!" },
	["gs_mag7_popdog"] = { name = "팝도그", desc = "측면에 강아지 팝도그가 그려진 열차 차량에 영감을 받은 디자인으로 도색했습니다.\n\n상쾌한 강아지!" },
	["cu_mp9_hydra"] = { name = "히드라", desc = "CS:GO의 8번째 작전인 히드라 작전에서 영감을 받은 디자인으로 도색했습니다.\n\n기다린 보람이 있어." },
	["cu_p90_nostalgia"] = { name = "레트로", desc = "오리지널 Counter-Strike 메인 메뉴의 대테러부대 디자인을 도색했습니다.\n\n설치는 순식간, 통달하려면 평생." },
	["am_aug_death_by_doggy"] = { name = "강아지 팝아트", desc = "다양한 색상의 강아지 팝도그 패턴으로 도색했습니다.\n\n'사다리'와 혼동하지 마세요." },
	["cu_scar_assault"] = { name = "돌격", desc = "파란색 바탕에 하얀색의 연구소 로고를 도색했습니다.\n\n빨간색도 이용 가능" },
	["gs_mp5_fbi"] = { name = "요원", desc = "FBI의 특징적인 파란색으로 도색했으며 노란색으로 포인트를 살려 마감했습니다.\n\n\"이제 우리 이야기를 들어야 할 시간이다.\"" },
	["cu_ump_bomb"] = { name = "플라스틱 폭탄", desc = "전선, 초록색 회로 기판, 그리고 숫자 패드를 이어 붙인 디자인으로 도색했습니다.\n\n73556**" },
	["cu_awp_wildfire"] = { name = "들불", desc = "검은색과 빨간색 바탕에 들불 작전 로고를 도색했습니다.\n\n모난 돌이 정 맞는다." },
	["cu_glock_hero"] = { name = "희생", desc = "ESL One 퀼른 2014 당시 오버패스 맵에서 화염에 휩싸였음에도 폭탄을 계속해서 해체한 olofmeister에게 경의를 표하는 디자인으로 도색했습니다.\n\n\"The defuse is coming in—he's still going!(폭탄 해체 들어갑니다. 멈추질 않는 군요!)\"" },
	["gs_famas_legacy_gold"] = { name = "기념판", desc = "Counter-Strike의 20주년을 기념하여 황금으로 도색했으며 다양한 디테일이 살아있습니다.\n\n앞으로 20년 더!" },
	["gs_ak47_gold_arabesque"] = { name = "황금 아라베스크", desc = "개머리판과 핸드경비에 섬세한 문양 디자인을 수작업으로 도색했습니다. 금속은 금색으로 도색하고 필리그리 문양을 세공했습니다.\n\n\"부스는 또 어떤 트릭을 보여줄까요?\"" },
	["am_lizard_red"] = { name = "오렌지 아놀리스", desc = "빨간색과 금색의 도마뱀 비늘 무늬를 수전사 방식으로 입혔습니다.\n\n축척에 맞게 채색" },
	["hy_brush_camo_tan"] = { name = "사막 브러시", desc = "모래색 디자인을 수전사 방식으로 입혔습니다.\n\n위장 효과 테스트, V1" },
	["hy_ddpat_urban_red"] = { name = "빨간색 디지털 위장 무늬", desc = "빨간색 디지털 위장 무늬를 수전사 방식으로 입혔습니다.\n\n위장 무늬를 알아챌 정도로 가까이 왔다면 이미 늦었다." },
	["hy_dry_wood"] = { name = "나뭇결", desc = "마른 나무를 연상시키는 디자인을 수전자 공법으로 도색했습니다.\n\n네가 필요한 행운 전부를 얻을 수 있도록 행운을 빌어주도록 하지 - 필릭스 라일리, 지휘관" },
	["hy_desert_multicam"] = { name = "사막 위장", desc = "검은색과 황갈색 위장 무늬를 수전사 방식으로 입혔습니다.\n\n쏘지 않으면 빗나가지 않지." },
	["hy_torn_camo_paints"] = { name = "퀵 샌드", desc = "개머리판과 몸통에 황갈색과 파란색 패턴을 수전사 방식으로 입혔습니다. 총열은 진한 파란색으로 도색했습니다.\n\n'반가운 광경'이라고 하면 가볍게 말한 거지." },
	["sp_moro_carving_lightblue"] = { name = "새로운 뿌리", desc = "덩굴과 꽃 패턴을 다양한 음영의 초록색 스프레이 페인트로 도색했습니다.\n\n성장할 수 있는 공간" },
	["sp_moro_carving_yellow"] = { name = "오래된 뿌리", desc = "덩굴과 꽃 패턴을 다양한 음영의 초록색 스프레이 페인트로 도색했습니다.\n\n확고함, 노련함, 연륜... 하지만 그냥 '늙은'걸 수도 있겠지." },
	["sp_moro_textile_purple_yellow"] = { name = "시든 덩굴", desc = "꽃 패턴을 보라색과 금색의 스프레이 페인트로 도색했습니다.\n\n누군가는 성가시다고 생각하지만, 난 거기서 아름다움을 보았다." },
	["sp_palm_night"] = { name = "미드나잇 팜", desc = "회색과 보라색 위장 무늬 위에 검은 야자나무 잎을 스프레이 페인트로 도색했습니다.\n\n“표지판에 들어가지 말라고 쓰여있어!”" },
	["sp_desert_skulls"] = { name = "백골", desc = "스프레이 페인트로 동물의 두개골 패턴을 도색했습니다.\n\n백골만 남았어" },
	["sp_zebracam_red"] = { name = "먹잇감", desc = "녹이 슨 듯한 색깔의 얼룩말 줄무늬 패턴을 스프레이 페인트로 도색했습니다.\n\n먹고, 자고, 사냥하고, 반복하라." },
	["cu_ssg08_scorpion"] = { name = "죽음의 엄습", desc = "SSG 08 위에 황제 전갈과 흘러넘치는 모래 디자인을 도색했습니다.\n\n불타는 듯한 고통을 느낄 텐데..." },
	["hy_desert_bloom"] = { name = "메마른 땅", desc = "건조하고 갈라진 땅 위에 피어난 생기 넘치는 꽃을 스프레이 페인트로 도색했습니다.\n\n자연은 언제나 승리한다!" },
	["am_ddpat_purple"] = { name = "보라색 디지털 위장 무늬", desc = "보라색 디지털 위장 무늬를 수전사 방식으로 입혔습니다.\n\n위장 무늬를 알아챌 정도로 가까이 왔다면 이미 늦었다." },
	["gs_awp_hydra"] = { name = "데저트 히드라", desc = "AWP의 총열로 기어가는 듯한 금색 뱀 디자인을 도색했습니다. 조준경, 총열 및 개머리판은 하얀색으로 도색했습니다.\n\n하나의 머리를 절단하면 두 개의 머리가 나오는데, 넌 이 위험을 감당할 수 있어? - 발레리아 제너, 혁명가" },
	["gs_deagle_fennec"] = { name = "사막여우", desc = "총열에 사막 여우 디자인을 도색했습니다. 청록색과 주황색 디자인의 손잡이가 특징입니다.\n\n네 목소리를 더 잘 듣기 위해서야." },
	["am_mirage_flowers_metalic"] = { name = "음악 상자", desc = "금색 다마스크 패턴으로 도색했습니다.\n\n환상의 싸움" },
	["am_moro_textile_bright"] = { name = "우아한 덩굴", desc = "빨간색과 초록색의 꽃 패턴으로 도색했습니다.\n\n누군가는 성가시다고 생각하지만, 난 거기서 아름다움을 보았다." },
	["am_navy_shine"] = { name = "청색 위장 무늬", desc = "양극 산화 처리된 감청색 위장 무늬 패턴으로 도색했습니다.\n\n왜 그리 다운돼있어?" },
	["gs_aug_sand_storm"] = { name = "모래 폭풍", desc = "밤 하늘 아래로 지나가는 모래 폭풍을 생생하게 묘사한 맞춤 디자인을 커스텀 페인트로 도색했습니다.\n\n아름다움은 주변 어디에나 존재합니다. 그저 어디를 봐야 할지 알기만 하면 되죠." },
	["sp_dry_wood"] = { name = "유목", desc = "마른 나무를 연상시키는 패턴을 스프레이 페인트로 도색했습니다.\n\n네가 필요한 행운 전부를 얻을 수 있도록 행운을 빌어주도록 하지 - 필릭스 라일리, 지휘관" },
	["sp_desert_skulls_dawn"] = { name = "가뭄", desc = "스프레이 페인트로 동물의 두개골 패턴을 도색했습니다.\n\n백골만 남았어" },
	["sp_mirage_flowers_tan"] = { name = "시에나 다마스크", desc = "황갈색 바탕에 다마스크 꽃 패턴을 스프레이 페인트로 도색했습니다.\n\n엄밀히 따지자면 최고의 위장은 눈에 띄지 않는 것입니다..." },
	["sp_moro_carving_burnt"] = { name = "담배 보관함", desc = "덩굴과 꽃 패턴을 갈색과 보라색의 스프레이 페인트로 도색했습니다.\n\n'그 안에 뭘 보관하고 있지?'" },
	["sp_moro_textile_green_vine"] = { name = "우거진 덩굴", desc = "꽃 패턴을 다양한 음영의 초록색 스프레이 페인트로 도색했습니다.\n\n누군가는 성가시다고 생각하지만, 난 거기서 아름다움을 보았다" },
	["gs_mp5_neon_flektarn"] = { name = "산화된 오아시스", desc = "밝은 주황색과 청록색 위장 패턴을 도색하고 금색으로 장식해 마감했습니다.\n\n이렇게 조용한 무기가 그렇게 시끄럽진 않겠죠." },
	["aa_desert_bloom_bright"] = { name = "사막에 핀 꽃", desc = "건조하고 갈라진 땅 위에 피어난 생기 넘치는 꽃을 스프레이 페인트로 도색했습니다.\n\n자연은 언제나 승리한다!" },
	["hy_lizard_skin"] = { name = "아놀리스", desc = "초록색과 검은색 도마뱀 비늘 패턴을 수전사 방식으로 입혔습니다.\n\n축척에 맞게 채색" },
	["cu_ak-47_phantom_disruptor"] = { name = "망령 교란자", desc = "검은색 무광 바탕에 안개 형상을 한 괴물 디자인을 커스텀 페인트로 도색했습니다.\n\n\"원하는 무엇이든 될 수 있고 어디에든 갈 수 있지만, 그래도 당신 곁에 남기로 했어요...\"" },
	["gs_aug_thunderstorm"] = { name = "고양이 톰", desc = "건 메탈 베이스 코트에 황금 하이라이트, 뒤쪽으로 뻣어나가는 화살, 장난스러운 표정의 고양이 디자인으로 마감했습니다.\n\n자연도 천둥 치는 폭풍우를 쏟으며 고양이처럼 그르렁거립니다." },
	["cu_awp_vein"] = { name = "모세혈관", desc = "섬세한 혈관을 연상시키는 디자인을 청록색과 산호색 커스텀 페인트로 도색했습니다.\n\n요원의 심장을 얻으려면 $4750를 내세요." },
	["cu_cz75_cerakote"] = { name = "빈티지", desc = "사용할수록 마모되는 구리색 세라믹으로 마감했습니다.\n\n오래될 수록 근사해집니다." },
	["cu_deagle_replica"] = { name = "푸른 물결", desc = "잿빛 손잡이와 파란색 슬라이드로 이루어져 있으며 래미네이트 처리되었습니다.\n\n경고: 해당 무기는 복제본이며 실탄 테스트를 하지 않았습니다" },
	["cu_m4a1s_csgo2048"] = { name = "플레이어 2", desc = "밝은 색상을 사용하여 GIGN 대테러부대와 강아지 팝도그를 애니메이션 느낌의 디자인으로 도색했습니다.\n\n시작을 누르세요..." },
	["cu_mac10_nacre"] = { name = "디스코 테크놀로지", desc = "홀로그램 비닐 느낌이 나도록 진줏빛 마감재로 도색했습니다. 빛에 따라 색상이 달라집니다.\n\n그걸 가리킬 때는 주의하세요." },
	["gs_mag7_justice"] = { name = "정의", desc = "'정의' 타로 카드에서 영감을 얻은 디자인으로 도색했습니다.\n\n최후의 심판" },
	["cu_mp5_desert_strike"] = { name = "사막 공습", desc = "첨단 기술을 연상시키기는 디자인을 검은색과 황갈색 커스텀 페인트로 도색했습니다.\n\n우선 공격, 최종 타격" },
	["cu_negev_prototype"] = { name = "프로토타입", desc = "아직 완성되지 않은 임시 디자인을 흰색과 빨간색 커스텀 페인트로 도색했습니다.\n\nv1.2.1, PAT. 3422107" },
	["aq_p2000_acid_clover"] = { name = "애시드 에칭", desc = "희귀한 네잎클로버와 다양한 색상의 무작위 패턴으로 도색했습니다.\n\n네가 운이 좋다고 생각해?" },
	["gs_r8_leviathan"] = { name = "단조된 뼈", desc = "리볼버 손잡이에 고대 바다생물의 유해 디자인을 도색했습니다.\n\n아래에 뭐가 숨어있을까" },
	["cu_sawedoff_apocalypto"] = { name = "아포칼립토", desc = "보라색 우주 배경 위에 썩어들어가는 손이 폭탄처럼 보이는 행성을 향하는 디자인을 도색했습니다.\n\n“시간 좀 있나?”" },
	["gs_scar20_enforcer"] = { name = "집행자", desc = "총신의 여러 부분에 섬세한 노을빛 그라데이션을 도색했습니다.\n\nSYMETRY SMART GUN CORP의 자동 솔루션" },
	["cu_sg553_darkwing"] = { name = "어둠의 날개", desc = "깃털 디자인을 붉은색과 검은색 커스텀 페인트로 도색하고 일부 부분을 어둡게 장식했습니다.\n\n매의 눈을 가졌으며 매우 치명적입니다. 이보다 더 위험한 날지 못하는 새는 없을 거예요." },
	["cu_ssg08_fever_dream"] = { name = "피버 드림", desc = "검은색 바탕에 화려하고 선명한 분홍색, 파란색, 보라색 색상을 도색했습니다.\n\nㅎ1–Jㅎㅎl-ㅎrㅎ|—하!!" },
	["cu_glock18_warmaiden"] = { name = "총알 여왕", desc = "노란색과 검은색 바탕에 장난스러운 총알 캐릭터와 핑크 재킷을 입은 전쟁광 소녀 캐릭터 디자인을 커스텀 페인트로 도색했습니다.\n\n\"너만 조용히 하면 그만 쏠 거야.\"" },
	["cu_negev_ultralight"] = { name = "초경량", desc = "회색 디지털 위장 무늬에 청록색 화살촉을 도색했습니다.\n\n불 좀 빌려줄래?" },
	["cu_ak47_anubis"] = { name = "아누비스의 군단", desc = "삶과 죽음 사이의 균형을 나타내는 이집트 신 아누비스와 라의 특징을 살려 도색했습니다..\n\n그가 당신에게 유리하게 저울을 기울여 주길." },
	["aq_p2000_lost_world"] = { name = "에일리언", desc = "P2000의 슬라이드 밑 손잡이에 뒤틀린 외계인이 기지개를 펴고 있는 디자인을 도색했습니다.\n\n무한한 공허의 어두운 구석으로부터" },
	["cu_mag7_monster_call"] = { name = "괴물의 부름", desc = "전체적으로 파란색으로 도색했으며 앞면에는 수중 생물이 그려져 있습니다.\n\n더 크고 푸른 물고기는 언제나 찾을 수 있습니다." },
	["cu_printstream"] = { name = "프린트스트림", desc = "검은색과 하얀색을 조합한 디자인을 도색했으며 진줏빛 광택을 더했습니다.\n\nWHITE_1; BLACK_1; PEARLESCENT_1;" },
	["cu_glock_eyecontact"] = { name = "눈동자", desc = "한 쌍의 눈 디자인이 슬라이더를 우아하게 장식하는, 팝아트에서 영감을 받아 커스텀 페인트로 도색한 글록입니다.\n\n\"나를 봐! 널 쏘고 있잖아!\"" },
	["gs_tec9_guerilla"] = { name = "전우", desc = "갈색과 금색 위장 패턴을 커스텀 페인트로 도색했으며 가늠자 아래에 테러리스트 로고가 있습니다.\n\n네 등을 맡겨라." },
	["cu_mac10_isoonna"] = { name = "매혹", desc = "이소온나로 보이는 여인이 청록색 옷을 입고 잉어와 파도 앞에 서 있는 그림을 MAC-10에 커스텀 페인트로 도색했습니다.\n\n피 흘리는 아름다움" },
	["gs_sg553_rusty"] = { name = "올 러스티", desc = "연마된 강철로 만들어졌지만 습기의 영향으로 변색되었습니다. 방아쇠 위쪽 몸체에 'Ol' Rusty'라는 문구가 찍혀있습니다.\n녹슬고 믿음직한 친구" },
	["cu_ssg08_mainframe"] = { name = "메인프레임 001", desc = "파란색과 보라색을 사용하여 고도의 테크놀로지를 표현하는 디자인을 도색했습니다.\n\n_001에는 어떤 비밀이 숨어 있을까?" },
	["cu_p250_cassette"] = { name = "카세트", desc = "1990년대의 비디오 카세트 테이프 포장지에서 영감을 받은 디자인으로 도색했습니다.\n\n테이프 좀 확인해 봐." },
	["gs_p90_container"] = { name = "화물 열차", desc = "열차에 그려진 그래피티와 닮은 그림을 커스텀 페인트로 도색했습니다. \n\n예술은 반달리즘이 아니다" },
	["cu_xm1014_amulet_blue"] = { name = "안치된 무덤", desc = "수천 년 동안 묻혀 있다 최근에 발굴된 이 XM-1014는 푸른색 천으로 감싼 듯한 모습과 고대 이집트의 문양을 살려 도색했습니다.\n\n파라오에게 어울림" },
	["cu_m4a4_queenfairy"] = { name = "이빨 요정", desc = "앞니와 어금니, 그리고 이빨 요정이 오싹하게 표현되도록 도색했습니다.\n\n아무도 이빨이 네 것이어야 한다고 한 적 없어..." },
	["gs_galil_phoenix"] = { name = "피닉스", desc = "하얀색과 빨간색 바탕에 피닉스의 로고를 스프레이 페인트로 도색했습니다.\n\n놈들이 강력한 무기를 가져오고 있어." },
	["gs_bizon_hellraider"] = { name = "룬 문자", desc = "몸체에는 기원을 알 수 없는 룬 문자를, 탄창에는 게틀링 건과 유사한 디자인을 도색했습니다.\n\n지옥을 선사하라." },
	["gs_mp5sd_wasteland_legacy"] = { name = "키트배시", desc = "20주년을 기념하는 MP5 디자인으로 도색하고 테이프, 케이블 타이와 접착제를 함께 사용하여 마감했습니다.\n\n한 번에(그리고 두 번째, 세 번째에도) 성공하지 못했을 때..." },
	["gs_awp_exoskeleton"] = { name = "엑소스켈레톤", desc = "AWP 외관에 정교한 해골 문양 패턴이 찍힌 금속을 부착하여 장식했습니다.\n\n예견된 승부" },
	["gs_cz75_vendetta"] = { name = "벤데타", desc = "오랜 세월 동안 사용하여 닳고, 낙서가 새겨지고, 손에 맞게 굳어진듯한 디자인을 도색했습니다.\n\n내 차갑고, 죽어버린 손으로부터..." },
	["hy_p90_dino_rampage"] = { name = "광란", desc = "만화에 나오는 올리브와 금색 공룡 패턴을 수전사 방식으로 입혔습니다.\n\n공룡과 외계인과 헤성이라니, 맙소사!" },
	["gs_dual_elites_dezastre"] = { name = "Dezastre", desc = "검은색, 하얀색, 빨간색으로 차세대 지향적 디자인을 도색했습니다.\n\n안전한 사용을 위해 항상 제조업체 지침을 준수해 주세요." },
	["cu_five_seven_diary"] = { name = "핑크 유니콘", desc = "공주와 유니콘, 멀리 보이는 성채와 흩날리는 아름다운 꽃의 모습을 도색했습니다.\n\n\"오늘은 분명 좋은 날이 될 거야. 그런 기분이 들어.\"" },
	["gs_g3sg1_disrupt"] = { name = "디지털 메시", desc = "붉은색, 흰색, 짙은 회색 디지털 위장 무늬를 커스텀 페인트로 도색했습니다.\n\n파격적인 혼돈" },
	["gs_galil_vandal"] = { name = "반달리즘", desc = "녹색 바탕에 여러 흰색 그림과 패턴을 도색했습니다.\n\n각 그림에는 이야기가 담겨 있으며, 일부는 다른 것보다 더 기억에 남습니다." },
	["cu_p250_infect"] = { name = "오염", desc = "적갈색 마감 도색이 이미 갉아 먹혀 부패하고 있는 듯한 이 무기는 수명이 얼마 남지 않았습니다.\n\n보이는 것만큼 상태가 나쁘군요." },
	["cu_m249_deep_relief"] = { name = "딥 릴리프", desc = "가장 높아 보이는 층은 모래색으로 칠하고 깊이감을 주기 위해 녹색을 점점 짙게 도색했습니다.\n\n중앙까지 가려면 얼마나 많은 총알이 필요합니까?" },
	["cu_m4a4_cyberpunk"] = { name = "사이버 보안", desc = "M4의 검은 바탕에 파란색, 빨간색, 노란색의 띠를 대각선으로 도색했습니다. \n\n지금 가까운 사이버네틱 상점에서 구매 가능" },
	["gs_mp5_conditionzero"] = { name = "컨디션 제로", desc = "Counter-Strike: Condition Zero를 오마주하여 파란색과 노란색으로 도색했습니다.\n\n\"3시 방향에 RPG! 피하라!\"" },
	["cu_nova_polymer"] = { name = "투명 폴리머", desc = "케이스가 투명해 보이도록 도색하여 무기 내부를 볼 수 있습니다.\n\n네 목표는 명확하다." },
	["cu_glock_noir"] = { name = "네오 누와르", desc = "그레이 스케일 바탕에 여성 디자인을 블루 및 마젠타 색상의 커스텀 페인트로 도색했습니다.\n\n\"상황이 변하기 시작하며 그녀도 희망을 갖기 시작했다. 그리고 위를 올려다보았다.\"" },
	["cu_ssg08_chromatic"] = { name = "시차", desc = "눈길을 사로잡을 수 있도록 오프셋 위치에 화려한 색상의 페인트를 도색했습니다.\n\n일석이조" },
	["gs_ump_gold_bismuth"] = { name = "골드 비스무트", desc = "하얀색과 검은색 바탕 사이에 금색 비스무트 패턴을 도색했습니다.\n\n\"너 비싼 취향이구나…\"" },
	["cu_usp_krokos"] = { name = "몬스터 매쉬업", desc = "인기 있는 도시 예술에서 영감을 얻은 녹색 괴물과 다른 캐릭터가 특징인 디자인으로 도색했습니다. 소음기는 자개 빛으로 마감했습니다.\n\nX__X" },
	["cu_csgo_camo"] = { name = "글로벌 오펜시브", desc = "파란색, 노란색, 그리고 회색의 위장 무늬로 도색했습니다.\n\n\"독일 하르츠에서 첫 접선을 할 것이다. 시간은 17:25 GMT+1...\"" },
	["am_authority_brown"] = { name = "마그나 카르타", desc = "마그나 카르타의 문장, 두루마리, 문자로 이루어진 황갈색 패턴을 수전사 방식으로 입혔습니다.\n\n모두가 다 적법한 절차를 거쳐야 하죠. 물론 당신도요." },
	["hy_authority_purple"] = { name = "마그나 카르타", desc = "마그나 카르타의 문장, 두루마리, 문자로 이루어진 보라색 패턴을 수전사 방식으로 입혔습니다.\n\n모두가 다 적법한 절차를 거쳐야 하죠. 물론 당신도요." },
	["am_intelligence_grey"] = { name = "감시", desc = "조감도 HUD가 오버레이된 지도 패턴을 도색했습니다.\n\n목표 확보" },
	["am_intelligence_orange"] = { name = "위험 감지", desc = "조감도 HUD가 오버레이된 지도 패턴을 도색했습니다.\n\n목표 확보" },
	["am_intelligence_magenta"] = { name = "목표 확보", desc = "조감도 HUD가 오버레이된 지도 패턴을 도색했습니다.\n\n목표 확보" },
	["am_numbers_bronze"] = { name = "브론즈", desc = "무작위 숫자로 구성된 다양한 색상의 패턴으로 도색했습니다.\n\n숫자는 거짓말을 하지 않지만, 난 할 수 있어." },
	["am_numbers_red_blue"] = { name = "디스패치", desc = "무작위 숫자로 구성된 다양한 색상의 패턴으로 도색했습니다.\n\n숫자는 거짓말을 하지 않지만, 난 할 수 있어." },
	["hy_numbers_green"] = { name = "스위치 보드", desc = "무작위 숫자로 구성된 다양한 색상의 패턴으로 도색했습니다.\n\n숫자는 거짓말을 하지 않지만, 난 할 수 있어." },
	["am_numbers_magenta"] = { name = "대형 음모론", desc = "무작위 숫자로 구성된 다양한 색상의 패턴을 도색했습니다.\n\n숫자는 거짓말을 하지 않지만, 난 할 수 있어" },
	["aa_fade_red_blue"] = { name = "딸기와 체리", desc = "슬라이드의 절반은 빨간색으로 나머지 절반은 파란색으로 도색했습니다.\n\n유후!" },
	["cu_ump_crime_scene"] = { name = "범죄 현장", desc = "빨간색과 파란색 바탕에 노란색 경고 테이프를 도색했습니다.\n\n구역을 정리해. 할 일이 있으니까…" },
	["am_m4a1s_bluesmoke"] = { name = "파란 인광 물질", desc = "파란색 크롬 코팅 위에 파란색 메탈릭 페인트를 여러 겹으로 도색했습니다.\n\n우울해?" },
	["cu_ak_xray"] = { name = "X-Ray", desc = "엑스레이로 AK-47을 찍은 것처럼 내부가 보이는 디자인으로 도색했습니다.\n\n모든 것이 밝혀지면, 네가 보고 있는 게 마음에 들지 않을 수 있지..." },
	["am_heist_plans_green"] = { name = "강도", desc = "양극 산화 처리한 베이지색 지도 패턴을 바탕으로 도색했습니다. 처음 만들어진 이후로 누군가가 지도를 무기 위에 그린 것 처럼 보입니다.\n\n계획과 실행은 별개의 문제" },
	["am_heist_plans_purple"] = { name = "나이트 하이스트", desc = "양극 산화 처리한 보라색 지도 패턴을 바탕으로 도색했습니다. 처음 만들어진 이후로 누군가가 지도를 무기 위에 그린 것 처럼 보입니다.\n\n계획과 실행은 별개의 문제" },
	["am_heist_plans_yellow"] = { name = "볼트 하이스트", desc = "양극 산화 처리한 회색 지도 패턴을 바탕으로 도색했습니다. 처음 만들어진 이후로 누군가가 지도를 무기 위에 그린 것 처럼 보입니다.\n\n계획과 실행은 별개의 문제" },
	["hy_houndstooth_brown"] = { name = "새발 격자무늬", desc = "갈색 새발 격자무늬를 수전사 방식으로 입혔습니다.\n\n맡은 역할이 아닌 원하는 역할에 맞게 장비하세요." },
	["gs_mac10_snake"] = { name = "핫 스네이크", desc = "총신, 손잡이, 탄창에는 황동색 뱀 가죽 패턴을, 철사식 개머리판 아래에는 똬리를 틀고 있는 뱀을 커스텀 페인트로 도색했습니다.\n\n쉭하고 스쳐 날아가는 것은 총알입니다." },
	["hy_phoenix_tags_lilac"] = { name = "피닉스(분필)", desc = "다양한 패턴의 피닉스 커넥션 그래피티를 여러 가지 색상으로 도색했습니다.\n\n\"네가 이길 거라고 생각하는 건 멍청한 짓이야!\"" },
	["am_phoenix_tags_blue"] = { name = "피닉스 무늬", desc = "다양한 패턴의 피닉스 커넥션 그래피티를 여러 가지 색상으로 도색했습니다.\n\n\"이젠 그들도 우리를 멈출 수 없어!\"" },
	["hy_phoenix_tags_red"] = { name = "피닉스(스텐실)", desc = "다양한 패턴의 피닉스 커넥션 그래피티를 여러 가지 색상으로 도색했습니다.\n\n\"우린 죽을 수 없다!\"" },
	["sp_phoenix_tags_purple"] = { name = "피닉스(블랙라이트)", desc = "다양한 패턴의 피닉스 커넥션 그래피티를 여러 가지 색상으로 도색했습니다.\n\n\"우린 너의 무덤에서 춤을 출 것이다!\"" },
	["hy_tigers_tan"] = { name = "클레이 앰부시", desc = "어두운 갈색과 황갈색 호랑이 패턴을 수전사 방식으로 입혔습니다. 입혔습니다.\n\n주로 혼자 다니는 포식자이지만, 당신에게는 예외일 거예요." },
	["am_tigers_brown"] = { name = "타이거 피트", desc = "검은 바탕에 어두운 갈색과 황갈색 호랑이 패턴을 수전사 방식으로 입혔습니다.\n\n이곳을 제외한 모든 곳" },
	["am_tigers_blue"] = { name = "실크 타이거", desc = "파란색 바탕에 호랑이 패턴을 수전사 방식으로 입혔습니다.\n\n국보는 AWP가 아니라 호랑이" },
	["cu_m4a1_snake"] = { name = "바이퍼", desc = "황혼빛 정글 배경에 황금 독사와 고대 도상학적 문양을 도색했습니다.\n\n경고 없는 공격" },
	["cu_ak_jaguar"] = { name = "판테라 온카", desc = "나무로 만든 개머리판과 핸드경비에 아름다운 조각이 새겨져 있습니다. 종종 총신 옆면에 커스텀 페인트로 도색된 재규어 디자인보다 시선을 더 사로잡합니다.\n\n자리를 바꿀 수 없다고 누가 그러던가요?" },
	["cu_jaguar_p90"] = { name = "꼭꼭 숨어라", desc = "재규어 패턴을 커스텀 페인트로 도색하고 문양을 찍어낸 가죽으로 장식했습니다.\n\n멋을 돈으로 살 수는 있지만, 아무나 진정한 멋을 아는 건 아니죠.\n" },
	["aa_ancient_brown"] = { name = "고대 지구", desc = "갈색과 황금색으로 복잡한 문양과 고대 생물이 그려져 있는 패턴을 도색했습니다.\n\n특권은 당신에게" },
	["am_ancient_warm"] = { name = "고대 전설", desc = "옅은 초록색, 베이지색, 빨간색의 고대 타일 무늬로 도색했습니다.\n\n“설령 잘못된 길을 향해 여행을 떠났던 사람들이어도, 희망은 남아 있습니다.”" },
	["aa_ruins_green"] = { name = "초록 정글", desc = "무성한 나뭇잎 패턴의 배경에 고대의 신들이 가려져 있습니다.\n\n\"모두 집중해. 지금 감시당하고 있어...\"" },
	["hy_ancient_tiles_peach"] = { name = "고대 타일", desc = "옅은 초록색과 베이지색의 고대 타일 무늬로 도색했습니다.\n\n“설령 잘못된 길을 향해 여행을 떠났던 사람들이어도, 희망은 남아 있습니다.”" },
	["am_gold_brick"] = { name = "황금색 벽돌", desc = "금색 도색은 빛을 받아 반짝이며 무기의 모든 아름다운 결점을 강조합니다.\n\n반짝이 천지..." },
	["sp_ancient_bright"] = { name = "고대 환상", desc = "녹색과 붉은색으로 복잡한 문양과 고대 생물이 그려져 있는 패턴을 도색했습니다.\n\n특권은 당신에게" },
	["hy_ruins_red"] = { name = "붉은 정글", desc = "무성한 나뭇잎 패턴의 배경에 고대의 신들이 가려져 있습니다.\n\n\"모두 집중해. 지금 감시당하고 있어...\"" },
	["am_jade"] = { name = "옥 조각", desc = "크롬 바탕에 양극 산화 처리된 투명한 옥색 페인트를 도색했습니다. \n\n고급 중에서도 최고급	" },
	["am_ancient_wine"] = { name = "고대 의식", desc = "적갈색과 은색으로 이루어진 복잡한 선과 고대 생물이 그려진 패턴을 도색했습니다.\n\n당신의 특권" },
	["hy_drywood_green"] = { name = "풀숲", desc = "초록색 풀 패턴을 수전사 방식으로 입혔습니다.\n\n높은 풀 속에서는 절대로 좋은 일이 일어나지 않는다." },
	["am_black_panther"] = { name = "표범 위장", desc = "흑표범 얼룩무늬 패턴을 도색했습니다.\n\n나의 어두운 부분" },
	["gs_ak47_professional"] = { name = "슬레이트", desc = "'점판암처럼 검고 검은색'이라는 설명이 걸맞는 디자인으로 도색했습니다.\n\n\"의사 좀 불러봐, 그쪽에서 필요할 텐데.\"" },
	["cu_cz75_whirlwind"] = { name = "케르케투스", desc = "CZ75에 뱀을 낚아챈 새의 모습을 도색했습니다.\n\n생각하는 '그' Snake Eater가 아닙니다." },
	["cu_mp9_food_chain"] = { name = "먹이 사슬", desc = "MP9에 괴물의 먹이 사슬 디자인을 밝은 색상으로 도색했습니다.\n\n먹거나 먹히거나" },
	["cu_galil_chroma_pink"] = { name = "크로마틱 에버레이션", desc = "눈길을 사로잡을 수 있도록 오프셋 위치에 화려한 색상의 페인트를 도색했습니다.\n\n너무 화려해서 계속 쳐다보기 힘들군..." },
	["gs_glock_polymer"] = { name = "투명 폴리머", desc = "슬라이드는 황갈색으로, 방아쇠는 파란색으로 도색했습니다. 몸체는 투명한 폴리머로 만들었습니다.\n\n보이는 대로다." },
	["gs_usps_hangedman"] = { name = "반역자", desc = "'행맨' 타로 카드에서 영감을 얻은 디자인으로 도색했습니다.\n\n실패는 성공의 어머니" },
	["cu_m4a4_love"] = { name = "생기발랄", desc = "다양한 네온 색상을 사용하여 손수 그린 디자인을 도색했습니다.\n\n하나를 위해 싸우고, 모두를 위해 싸운다." },
	["gs_m249_combine"] = { name = "O.S.I.P.R.", desc = "이 M249는 건 메탈 색상 페인트에 콤바인 로고를 추가한 디자인으로 도색했습니다.\n\nO.S.I.P.R.(감시부대 보급형 펄스 라이플)" },
	["cu_negev_devtexture"] = { name = "개발자 텍스처", desc = "주황색 바탕에 회색 개발자 텍스처를 사용하여 도색했습니다.\n\nHammer를 사용할 시간입니다." },
	["gs_p250_cybershell"] = { name = "사이버 쉘", desc = "슬라이드는 은색으로, 손잡이는 육각형 모양으로 도색했습니다.\n\n당신은 사이버 보안을 위해 최전선에서 활동하는 사람들을 대표합니다." },
	["cu_mac10_portable"] = { name = "버튼 매셔", desc = "게임 패드와 비슷한 디자인으로 도색했으며, 투명한 폴리머 케이스에 다양한 버튼이 튀어나와 있습니다.\n\n'콘솔 전쟁'이라는 말에 완전히 새로운 의미 부여" },
	["aq_xm1014_punk"] = { name = "XOXO", desc = "선명하고 밝은 색상으로 이루어진 펑키한 감성의 패턴을 도색했습니다.\n\n그래, 맞아..." },
	["gs_r8_rustking"] = { name = "정크 야드", desc = "파란색 바탕에 은색 손글씨와 그림을 더했습니다. 실린더와 가늠쇠는 빨간색으로 도색했습니다.\n\n왕을 위한 무기" },
	["gs_sg553_deathmetal"] = { name = "헤비메탈", desc = "검은색 바탕에 주황색과 노란색 장식을 더해 마감했습니다. 개머리판 뒷부분에는 별 3개를 도색했습니다.\n\n\"여기 박힌 징들 중 하나는 터보 버튼인데, 어떤 거였는지 기억이 잘...\"" },
	["am_ump45_electrowave"] = { name = "오실리에이터", desc = "주황색, 검은색, 회색의 무작위 패턴으로 도색했습니다.\n\n누구에게나 한계점이 있지. 네 한계점은 어디야?" },
	["cu_deag_trigger_discipline"] = { name = "방아쇠 규율", desc = "틍측투영법으로 그려진 핑크색 격자무늬를 총신에 커스텀 페인트로 도색했습니다. 공이치기와 전면부의 가늠쇠는 밝은 파란색으로 도색했습니다.\n\n인내심은 그 자체로 보상입니다." },
	["sp_nova_wind_dispersal"] = { name = "민들레", desc = "민들레 들판 패턴을 스프레이 페인트로 도색했습니다.\n\n내 잔디밭에서 나가." },
	["am_nuclear_pattern4_famas"] = { name = "멜트다운", desc = "금속 페인트를 사용하여 방사능 위험 기호 패턴으로 도색했습니다.\n\n잠깐 물러서있어..." },
	["aa_spacerace_orange"] = { name = "우주 레이스", desc = "별과 위성으로 구성된 우주 테마 패턴을 도색했습니다.\n\n발신 및 수신" },
	["sp_spacerace_blue"] = { name = "스푸트니크", desc = "별과 위성으로 구성된 우주 테마 패턴을 도색했습니다.\n\n발신 및 수신" },
	["sp_technowar_red"] = { name = "피지 팝", desc = "황갈색과 붉은색으로 이루어진 레트로한 테크노 감성의 패턴을 도색했습니다.\n\n전쟁은 정보전!" },
	["hy_technowar_rwb"] = { name = "AWP 팝", desc = "흰색, 빨간색, 파란색으로 이루어진 레트로한 테크노 감성의 패턴을 도색했습니다.\n\n전쟁은 정보전!" },
	["hy_trainarchitect_green"] = { name = "봄 트윌리", desc = "화려한 검정색과 초록색 디자인을 수전사 방식으로 입혔습니다.\n\n마지막 기회다. 기차가 역을 떠나고 있어." },
	["hy_trainarchitect"] = { name = "가을 트윌리", desc = "화려한 붉은색과 황갈색 디자인을 수전사 방식으로 입혔습니다.\n\n마지막 기회다. 기차가 역을 떠나고 있어." },
	["gs_m4a4_coalition"] = { name = "연합특수부대", desc = "금색과 검은색으로 서로 맞물리는 삼각형 디자인에 파란색 포인트를 주어 도색했습니다.\n\n이 동맹은 일시적이지만, 넌 내가 이걸 다시 생각해보게 하는군…" },
	["cu_mac10_propaganda"] = { name = "프로파간다", desc = "러시아어로 '통제'와 '공포'를 포함한 다양한 단어들과 한 쌍의 눈 디자인을 도색했습니다.\n\n못 믿는 것 같은데, 안 그래?" },
	["gs_train_cz75"] = { name = "신디케이트", desc = "금색과 검은색으로 도색했으며 삼각형과 파란색 하이라이트를 추가했습니다.\n\n우린 함께 간다." },
	["hy_ak47lam_green"] = { name = "녹색 합판", desc = "AK 소총의 개머리판, 그립, 핸드가드를 녹색 라미네이트로 처리했습니다.\n\n단순한 물건이야." },
	["sp_galil_caution"] = { name = "주의!", desc = "대비도가 높은 검은색과 노란색을 사용하여 쉐브론 패턴을 스프레이 페인트로 도색했습니다.\n\n불편함 속에서 유지하는 편안함" },
	["hy_geometric_steps_pearl"] = { name = "프리즘 테라스", desc = "착시 현상을 일으키는 진줏빛 패턴을 도색했습니다.\n\n어디에서 시작해서 어디로 끝난 거지? 끝이 있긴 한 건가?" },
	["gs_m4a1_vertigo"] = { name = "일촉즉발", desc = "다양한 종류의 안전 표지판에서 영감을 얻은 디자인으로 도색했습니다.\n\n경고하지 않았다고는 하지 마… - 부스, 무기 상인" },
	["hy_vertigogeo"] = { name = "도식", desc = "다양한 색상의 등척성 격자무늬 패턴을 도색했습니다.\n\n넌 고통을 기억하고, 난 승리를 기억한다" },
	["hy_vertigospray_blue"] = { name = "스트랫", desc = "51층의 건축, 공기조화기술 및 배선 계획 디자인을 수전사 방식으로 입혔습니다.\n\n계획대로…" },
	["hy_vertigoillusion"] = { name = "프레임워크", desc = "착시 현상을 일으키는 회색과 황갈색 패턴을 수전사 방식으로 입혔습니다.\n\n잘 봤지?" },
	["hy_vertigoillusion_yellow"] = { name = "인터록", desc = "착시 현상을 일으키는 노란색과 파란색 패턴을 수전사 방식으로 입혔습니다.\n\n잘 봤지?" },
	["hy_vertigospray"] = { name = "인프라", desc = "51층의 건축, 공기조화기술 및 배선 계획 디자인을 수전사 방식으로 입혔습니다.\n\n계획대로…" },
	["aa_vertigogeo_neon"] = { name = "디지털 건축가", desc = "다양한 색상의 등척성 격자무늬 패턴을 도색했습니다.\n\n넌 고통을 기억하고, 난 승리를 기억한다" },
	["cu_sg553_caution"] = { name = "위험 수당", desc = "노란색 바탕에 주황색 트래픽 콘 색상으로 특징을 살렸습니다.\n\n한 번에 한 발씩 총알을 바람에 맡겨 보내세요." },
	["aa_wiring"] = { name = "두꺼비집", desc = "산화된 전기 배선 패턴을 도색했습니다.\n\n직접 수리하기 전에 전기 기술자와 상의하세요." },
	["aa_wiring_yellow"] = { name = "배선", desc = "전기 배선 패턴을 도색했습니다.\n\n직접 수리하기 전에 전기 기술자와 상의하세요." },
	["sp_engine_dirty"] = { name = "오일 교환", desc = "엔진 부품 모양의 패턴을 스프레이 페인트로 도색했습니다.\n\n'좋아보이진 않는데...'" },
	["aa_engine_performance"] = { name = "기계 공학", desc = "양극 산화 처리한 엔진 부품 패턴을 도색했습니다.\n\n“좋아보이진 않는데...”" },
	["cu_vertigo_fiveseven"] = { name = "추락 주의", desc = "다양한 색상의 커스텀 페인트로 '추락(FALL)'과 '건설(CONSTRUCTION)'이 적힌 도색을 적용했습니다.\n\n발빠짐 주의" },
	["sp_tire_tread_red"] = { name = "레드 타이어", desc = "검은색과 빨간색의 타이어 자국 패턴을 스프레이 페인트로 도색했습니다.\n\n탄 고무" },
	["sp_tire_tread_blue"] = { name = "블루 타이어", desc = "검은색과 파란색의 타이어 자국 패턴을 스프레이 페인트로 도색했습니다.\n\n탄 고무" },
	["gs_ak47_abstract"] = { name = "리트 뮤지엄", desc = "테러리스트 엘리트 요원이 더스트 2의 긴 A 쪽에서 숨어 들어오는 모습을 입체적으로 묘사했습니다.\n\n\"상상하는 모든 것이 사실입니다.\"" },
	["gs_aug_plague"] = { name = "전염병", desc = "다크 판타지와 공포에서 영감을 얻은 디자인으로 도색했습니다.\n\n'불꽃에 날아드는 나방처럼'" },
	["gs_mag7_bismuth"] = { name = "BI83 스펙트럼", desc = "무지개색의 비스무트 결정과 비슷한 색상으로 도색했습니다.\n\n희귀한 것이 가장 아름다운 이유는 무엇일까요?" },
	["cu_deagle_kitch"] = { name = "오션 드라이브", desc = "다양한 문양과 패턴을 강렬한 색상의 커스텀 페인트로 도색했습니다.\n\n화려함과 적, 둘 다 놓칠 수 없지" },
	["gs_dual_berettas_tread"] = { name = "트레드", desc = "양쪽 베레타에 타이드 문양의 패턴을 에칭 공법으로 입혔습니다. 슬라이드는 금색으로 도색했습니다.\n\n섬세한 디테일을 위해 가장 많은 시간을 쏟지만, 가장 적게 눈에 띕니다." },
	["cu_famas_spectron"] = { name = "ZX 스펙트론", desc = "오래된 게이밍 PC에서 영감을 받은 디자인을 검은색 바탕에 다양한 색상의 커스텀 페인트로 도색했습니다.\n\n클라이브가 자랑스러워할 거예요." },
	["gs_five_seven_efusion"] = { name = "부스트 프로토콜", desc = "고대비 색상의 커스텀 페인트로 도색했습니다. 가늠자 아래에 있는 로봇 아이콘은 슬라이드에 있는 디자인과 일치합니다.\n\n콜사인: MECHA 0272" },
	["aa_mp9_fuji_pink"] = { name = "후지산", desc = "새벽녘의 후지산을 아크릴 물감으로 도색했습니다. 패턴을 사용한 도색했기 때문에 무기마다 다르게 보일 수 있습니다.\n\n붉은 태양이 떠오른다." },
	["cu_g3sg1_tacticalmap"] = { name = "위험 경계", desc = "위험지대의 전술 지도에서 영감을 얻은 디자인으로 도색했습니다.\n\n필요한 정보는 모두 가졌겠지. - 프란즈 크리겔드, 피닉스 전술가" },
	["cu_mp7_khaki"] = { name = "게릴라", desc = "초록색 바탕과 검은색 디테일 그리고 정면을 바라보는 원숭이가 특징인 디자인으로 도색했니다.\n\n사자는 정글의 왕이었던 적이 없다." },
	["gs_m4_flowers"] = { name = "꽃무릇", desc = "남색 바탕에 탄창과 방아쇠 위쪽에 있는 빨간색 꽃무릇이 있는 디자인을 커스텀 페인트로 도색했습니다.\n\n밝은 색은 초대장이 아닌 경고다." },
	["cu_mac10_toybox"] = { name = "토이 박스", desc = "닭, 외계인, 해체 키트 등의 밝은 색상의 다양한 그림을 도색했습니다.\n\n어지럽힌 거 정리해." },
