	["cu_glock_snackattack"] = { name = "스낵 어택", desc = "정크 푸드 캐릭터 장식이 레진 안에 굳어 있는 듯한 디자인을 커스텀 페인트로 도색했습니다.\n\n정크 푸드의 반격" },
	["cu_ssg_overtake"] = { name = "터보 피크", desc = "네온색 저격 소총의 옆면에 스포츠카가 달리는 디자인을 도색했습니다.\n\n드리프트는 권리지 특권이 아냐." },
	["cu_usp_black_lotus"] = { name = "Black Lotus", desc = "슬라이드에 검은색 연꽃을 도색했습니다.\n\n권총전에서 3명을 죽였다면, 이건 버려지겠지." },
	["gs_xm1014_watchdog"] = { name = "감시자", desc = "옥색 바탕에 수호 사자 두 마리가 서로 다른 방향을 바라보고 있는 디자인을 커스텀 페인트로 도색했습니다. 가늠자 아래에 사자가 한 마리 더 그려져 있습니다.\n\n수호와 번영" },
