	["gs_famas_rapid_eyes"] = { name = "재빠른 눈짓", desc = "분홍색 사각형 디자인으로 도색했습니다. 사각형에는 사실적인 눈이 그려져 있습니다.\n\n몽상가의 눈은 광란 그 자체" },
	["cu_fiveseven_alpha_omega"] = { name = "낙서", desc = "노란색과 청록색 바탕에 굵은 선을 도색했습니다.\n\n첫 번째와 마지막" },
	["cu_g3sg1_glade"] = { name = "환상의 숲", desc = "나무로 가득 찬 숲속 사이로 푸른 하늘이 보이는 디자인을 도색했습니다.\n\n'우리 강산 푸르게 푸르게!'" },
	["gs_m4a1s_insomnia"] = { name = "밤의 공포", desc = "커스텀 페인트로 도색한 A1-S에 불면증 환자의 내면에 잠긴 여러 생각을 추가해 장식했습니다.\n\n잔잔한 밤의 품에서 빛을 찾을 때" },
	["cu_mac10_pixie"] = { name = "올가미", desc = "무기 안에 갇혀 빠져나오려 하는 꼬마 요정들의 모습을 도색했습니다.\n\n아직 속일 사람을 찾지 못했어." },
	["cu_mag7_predictor"] = { name = "선견지명", desc = "매치 결과를 예측하라는 요청을 받은 문어가 'WIN'을 선택하는 모습이 담긴 디자인으로 도색했습니다.\n\n이거 좋은 예감이 드는데..." },
	["cu_mp7_fear"] = { name = "심해 속 망령", desc = "버려진 영혼이 악몽의 구렁텅이로 빠지는 모습을 도색했습니다.\n\n넌 운명에서 벗어날 수 없어!" },
	["gs_mp9_starlight"] = { name = "별빛의 수호자", desc = "흰색 바탕에 무지개색 갈기가 있는 유니콘 디자인을 도색했습니다. 금색으로 포인트를 주었습니다.\n\n신이 당신을 선택한다면, 신이 당신을 보호하기를." },
	["am_xm_zombie_offensive"] = { name = "좀비 오펜시브", desc = "좀비화된 대테러부대 요원의 디자인을 도색했습니다.\n\n\"언데드 게임\"" },
	["cu_usp_to_hell"] = { name = "지옥행 티켓", desc = "검은색 바탕에 기술적인 하얀색 디자인을 도색했습니다.\n\n디자이너의 죽음" },
	["gs_mp5_kid_necronomicon"] = { name = "네크로 주니어", desc = "몸체에는 동화에서 영감을 얻은 생명체 디자인을, 소음기에는 닭 디자인을 도색했습니다.\n\n놀이 시간은 끝이다!" },
	["cu_p2k_flying_dream"] = { name = "떠오르는 영혼", desc = "숲 깊은 곳에서 쫓아오는 영혼을 표현하는 디자인으로 도색했습니다.\n\n잃어버린 수많은 영혼들이 발견되지 않기를..." },
	["sp_scar_chickenfight"] = { name = "폴트리가이스트", desc = "전장에 있는 닭을 묘사한 패턴을 스프레이 페인트로 도색했습니다.\n\n닭 한 마리가 상어를 타고 전투에 들어갑니다..." },
	["cu_sawedoff_ouija"] = { name = "스피릿 보드", desc = "오랫동안 사용된 스피릿 보드 느낌의 디자인으로 도색했습니다.\n\n'자, 어서, 네가 움직이고 있는거 알고 있어...'" },
	["cu_ak47_cogthings"] = { name = "아이스", desc = "선명한 초록색과 파란색의 그라데이션으로 도색했습니다.\n\n만지면 차갑다." },
	["cu_awp_chroma_pink"] = { name = "크로마틱 에버레이션", desc = "눈길을 사로잡을 수 있도록 오프셋 위치에 화려한 색상의 페인트를 도색했습니다.\n\n너무 화려해서 계속 쳐다보기 힘들군..." },
	["gs_revolver_purple_elite"] = { name = "크레이지 8", desc = "손잡이 부분에 숫자 8을 디자인하고 총열은 보라색으로 도색했습니다.\n\n2015년 12월 8일의 이벤트에서 영감을 받았습니다." },
	["gs_famas_corp_meow"] = { name = "미아오 36", desc = "초록색 바탕에 숫자 36 디자인을 도색했습니다.\n\n엄밀히 말하면 고양이는 4마리인데, 누가 숫자를 세고 있는거야?" },
	["cu_galil_destroyer"] = { name = "파괴자", desc = "'DESTROYER'라는 단어가 포함된 디자인을 도색했으며 특정 각도에서만 읽을 수 있습니다.\n\n전부 쳐부숴버려라." },
	["cu_m249_downvote"] = { name = "다운타운", desc = "사방을 가리키는 파란색과 분홍색 화살표 디자인을 도색했습니다.\n\n다양한 종류의 스프레이 패턴" },
