	["cu_mac10_monkeyflage"] = { name = "위장 원숭이", desc = "MAC-10을 빨간색, 황갈색, 초록색 원숭이가 어우러진 디자인으로 도색했습니다.\n\n활기찬 원숭이!" },
	["cu_sg553_cyber_dragon"] = { name = "드래곤 테크", desc = "몸체에 역동적으로 움직이는 듯한 초록색 용 디자인을 커스텀 페인트로 도색했습니다.\n\n그건 내 용이 아니야…" },
	["cu_negev_clear_sky"] = { name = "드롭", desc = "갈색 바탕을 칠한 후 총열 그립에 정지 표지를 커스텀 페인트로 도색했습니다.\n\n정지 표지에 멈추지 않는다면 총알 세례에는 멈추겠지. " },
	["gs_p250_visions"] = { name = "비전", desc = "다채로운 색상의 디자인으로 도색했습니다.\n\n죽일 듯이 노려보는 까마귀" },
	["gs_p90_tangled"] = { name = "벤트 러시", desc = "접착테이프, 튜브관 그리고 불이 P90을 휩싸고 있는 디자인을 도색했습니다.\n\n“네가 얼마나 빠를지 생각해 봤어?”" },
	["cu_sawedoff_kisslove"] = { name = "키스♥러브", desc = "별이 빛나는 눈을 가진 여성 디자인을 분홍색과 보라색 커스텀 페인트로 도색했습니다.\n\n\"아니라고 하지 마. 난 가져야겠어!\"" },
	["cu_dual_elites_evil_flora"] = { name = "플로라 카니보라", desc = "듀얼 베레타 양쪽 측면에 밝은 색상의 식인 식물 3종을 도색했습니다.\n\n크고 튼튼하게 자라도록 도와줘." },
	["gs_ump_roadblock"] = { name = "로드블록", desc = "주황색 수평선과 화살표 디자인을 도색했습니다.\n\n경로를 이탈하여 재탐색합니다." },
	["gs_glock_elite_camo"] = { name = "혹한기", desc = "군용 위장 무늬를 겨울 색상으로 도색했습니다.\n\n꼼짝 마!" },
	["cu_mag7_insomnia"] = { name = "불면증", desc = "주황색과 검정색 팔레트를 사용하여 물고기가 사람의 입으로 헤엄치는 모습으로 도색했습니다.\n\n꿀꺽, 꿀꺽, 꿀꺽" },
	["cu_ak_head_shot_holo"] = { name = "헤드샷", desc = "'HEAD SHOT GUN'이라는 그래피티와 진주 광택 코팅으로 도색했습니다.\n\n모두가 목표를 가지고 있지. 하지만 너는 목표를 이루기 위해 무엇을 했지? - 부스, 무기 거래상" },
	["gs_awp_limbo_snake"] = { name = "이중성", desc = "무기 양쪽을 뱀 디자인으로 도색했습니다. 한 마리는 붉은색을 띠고 있으며, 다른 한 마리는 금색을 띠고 있습니다.\n\n모든 이야기에는 두 가지 측면이 존재한다." },
	["cu_m4a1s_feeding_frenzy"] = { name = "엠포로사우르-S", desc = "공룡이 무기의 옆면을 할퀸 것처럼 보이는 디자인을 커스텀 페인트로 도색했습니다.\n\n광란의 식사" },
	["cu_p2000_decline"] = { name = "위키드 시크", desc = "시선을 사로잡는 네온 색상의 디자인을 커스텀 페인트로 도색했습니다.\n\n주목해 주시겠어요?" },
	["cu_ump_clutch_kick"] = { name = "와일드 차일드", desc = "UMP-45에 네온 색상으로 띠를 칠하고, 그 위에 총알, 왕관, 기타 여러 가지 디자인을 커스텀 페인트로 도색했습니다.\n\n개념은 단순하지만 실행은 어렵다." },
	["cu_mp9_superlight"] = { name = "페더웨이트", desc = "검은색과 하얀색으로 작은 구멍을 표현하여 무기가 가볍게 보이도록 도색했습니다.\n\n장인은 도구를 탓하지 않는다." },
	["gs_scar_fragments_black"] = { name = "프래그먼트", desc = "건 메탈 색상 페인트를 이용하여 무기의 형상이 변경된 것 같은 착각을 불러일으키는 디자인으로 도색했습니다.\n\n상상의 산물" },
	["cu_glock_moon_rabbit"] = { name = "달 토끼", desc = "보름달이 뜨기 전 신비로운 모습의 토끼가 하늘을 향해 뛰는 몽환적인 장면을 표현하는 디자인으로 도색했습니다.\n\n믿음의 도약" },
	["cu_m4a4_temukau"] = { name = "테무카우", desc = "일본 애니메이션 스타일로 도색했으며, 테러리스트의 습격에 대항하는 여성 대테러부대 요원을 묘사합니다.\n\n'끝까지 맞서 싸우자!'" },
	["cu_mac10_sakkaku"] = { name = "사카쿠", desc = "MAC-10의 눈이 특정 조명에서 빛나도록 특수 페인트로 도색했습니다.\n\n네가 본 것을 현실화하라." },
	["cu_p250_rebuilt"] = { name = "리빌트", desc = "P250에 맞물리는 삼각형 그리고 최소화된 디자인을 도색했습니다.\n\n아홉 번째는 매력" },
	["cu_mp5sd_quick_liquidation"] = { name = "청산", desc = "어두운 바탕에 엄선된 빨간색과 파란색 커스텀 페인트로 하이라이트를 추가하여 MP5-SD가 마치 조명 아래에서 빛나는 느낌을 커스텀 페인트로 도색했습니다.\n\n조용한 반향" },
	["gs_r8_banana"] = { name = "바나나 캐논", desc = "아주 잘 익은 바나나를 묘사하는 디자인으로 도색했습니다.\n\n미끄러울 수 있으니 조심하라고!" },
	["gs_p90_neoqueen"] = { name = "네오퀸", desc = "군사와 공상과학 스타일을 혼합하여 도색했습니다.\n\n내게 필요한 수작업 제작품" },
	["cu_sg553_cyberforce"] = { name = "사이버포스", desc = "검은색과 빨간색 배경에 사이버포스 주식회사 로고가 들어간 디자인으로 도색했습니다.\n\n'사이버포스 주식회사: 당신의 안전은 우리가 책임집니다!'" },
	["cu_tec9_freedom"] = { name = "반란군", desc = "이 TEC-9은 차분한 색상 팔레트를 사용하여 도색했습니다.\n\n한 방에 한 명씩, 한 번에 한 라운드씩!" },
