	["cu_glitter_deagle"] = { name = "스타케이드", desc = "추상적인 디자인을 금속 반짝이가 포함된 커스텀 페인트로 도색했습니다.\n\n새로운 최고 점수!" },
	["gs_lilpig_aug"] = { name = "리틀 피그", desc = "바탕을 빨간색 커스텀 페인트로 도색하고 금속성 금색 페인트로 장식했습니다. 개머리판 근처에 빨간색 돼지 디자인을 추가했습니다.\n\n언덕 위의 집에서" },
	["gs_independe_awp"] = { name = "CMYK", desc = "흰색 바탕에 추상적인 디자인을 다양한 색상의 커스텀 페인트로 도색했습니다.\n\n돌아올 줄은 알았지만 이렇게 빨리 올 줄이야... - 부스, 무기 거래상" },
	["sp_nukestripe"] = { name = "sp_nukestripe", desc = "방사능 위험 경고 무늬로 도색했습니다." },
	["sp_spray_waves_bravo"] = { name = "sp_spray_waves_bravo", desc = "해변 맵을 기념하여 대조되는 색깔의 짧고 굵은 선 무늬를 스프레이 페인트로 도색했습니다.\n\n자주 이동하는 폭도라면 꼭 필요함" },
	["hy_ocean_bravo"] = { name = "hy_ocean_bravo", desc = "해변 맵을 기념하는 무기에 푸른색의 위장 무늬를 수전사 방식으로 입혔습니다.\n\n우리가 사는 세상에서는 위장이 중요하지... 하지만 가끔씩 위장이 일부러 보이도록 할 때도 있어 - 부스 , 무기 상인" },
	["sp_spitfire_famas_bravo"] = { name = "sp_spitfire_famas_bravo", desc = "무기창고 맵을 기념하여 상어의 입을 연상시키는 디자인을 도색했습니다.\n\n제스터는 죽었어." },
	["hy_tile_bravo"] = { name = "hy_tile_bravo", desc = "알리에서 찾은 이 기념품은 모자이크를 수전사 공법으로 도색했습니다.\n\n인내는 미덕이다" },
	["an_navy_bravo"] = { name = "an_navy_bravo", desc = "알리 맵을 기념하여 크롬 페인트로 바탕을 칠한 다음 양극 산화 처리 효과를 주기 위해 투명한 군청색 페인트로 도색했습니다. \n\n난 이미 한 명을 발레리아의 거짓말에 잃었다... 더 이상의 피해는 용납할 수 없다. - 필릭스 라일리, 지휘관" },
	["sp_hazard_bravo"] = { name = "sp_hazard_bravo", desc = "시즈 맵을 기념하여 노란색과 검은색 줄무늬로 이루어진 경고 표시 패턴을 스프레이 페인트로 도색했습니다.\n\n넘어오지 말 것" },
	["sp_tape_dots_bravo"] = { name = "sp_tape_dots_bravo", desc = "괄리오르에서 찾은 이 기념품은 찢겨진 테이프 조각과 촘촘하게 구멍이 난 금속판을 스텐실 기법을 이용하여 스프레이 페인트로 도색했습니다.\n\n걱정 마... 사업이 회복 조짐을 보이고 있으니 - 부스, 무기 상인" },
	["sp_palm_bravo"] = { name = "sp_palm_bravo", desc = "괄리오르에서 찾은 이 기념품은 골판지 조각과 촘촘한 망사 그리고 야자 잎을 스텐실 기법을 이용하여 스프레이 페인트로 도색했습니다.\n\n오늘은 아름답지만… 내일은 재가 된다" },
	["hy_ddpat_bravo"] = { name = "hy_ddpat_bravo", desc = "폐허에서 찾은 이 기념품은 디지털 위장 패턴 (DDPAT)이 수전사 공법으로 도색되었습니다.\n\n네게 보이지 않을 지라도, 확실히 들을 순 있을 것이다" },
	["so_jungle_bravo"] = { name = "so_jungle_bravo", desc = "폐허에서 찾은 이 기념품은 정글 계통의 단색으로 총기 부품을 하나하나 스프레이 페인트로 도색했습니다.\n\n파괴에 대한 열망을 가진 자들을 위해	" },
	["so_tornado_bravo"] = { name = "so_tornado_bravo", desc = "알리에서 찾은 이 기념품은 토네이도 계통의 단색으로 총기 부품을 하나하나 스프레이 페인트로 도색했습니다.\n\n말은 바람같이 변덕스럽지만, 바람은 사람을 죽일 수 있다" },
	["so_sand_bravo"] = { name = "so_sand_bravo", desc = "괄리오르 맵을 기념하여 모래 사장을 연상시키는 색채 배합으로 도색했습니다.\n\n알렉스 킨케이드 기자가 선봉 작전을 취재하기 위해 마그레브로 떠난 후 실종됐다는 소식이 들어왔습니다" },
	["so_olive_bravo"] = { name = "so_olive_bravo", desc = "무기창고에서 찾은 이 기념품은 짙은 황록색 계통의 단색으로 총기 부품을 하나하나 스프레이 페인트로 도색했습니다.\n\n절대 승리를 당연하게 받아들이지 마라" },
	["an_gunmetal_bravo"] = { name = "an_gunmetal_bravo", desc = "시즈 맵을 기념하여 크롬 바탕에 청회색 양극 산화 처리 효과를 내는 페인트로 도색했습니다.\n\n넌 나보다 필릭스 라일리를 잘 몰라... 그 놈은 꺾을 수 없어 - 배신자와 진실의 추적자 1장" },
	["am_ossify_blue_p2000_bravo"] = { name = "am_ossify_blue_p2000_bravo", desc = "해변 맵을 기념하여 메탈릭 페인트로 바탕을 칠한 다음 선명한 파란색 추상적인 무늬를 도색했습니다. \n\n파도가 부서진다. 우리의 다짐은 부서지지 않는다. - 코타로 이자키, 파괴 전문가" },
	["am_crumple_bravo"] = { name = "am_crumple_bravo", desc = "에이전시 맵을 기념하여 크롬 바탕에 다각형 무늬를 여러 방향으로 빛을 반사하는 메탈릭 페인트로 도색했습니다.\n\n연필 내리고, 손 들어!" },
	["aq_steel"] = { name = "aq_steel", desc = "외관만 녹슬었을 뿐 완벽하게 작동합니다.\n\n어떤 사람들은 자신의 행동이 얼마나 더러운 지 숨길 필요를 느끼지 않는다" },
	["hy_hex"] = { name = "hy_hex", desc = "육각형 무늬를 수전사 방식으로 입혔습니다." },
