	["am_ddpatdense"] = { name = "am_ddpatdense", desc = "이 무기는 디지털 위장 무늬가 전사되어 있고 메탈릭 도료가 첨가된 포일이 씌워져 있습니다." },
	["twigs"] = { name = "twigs", desc = "나뭇가지를 스텐실로 사용하여 스프레이 페인트로 도색했습니다." },
	["varicamo"] = { name = "varicamo", desc = "이 무기는 수전사 공법으로 VariCamo 위장 무늬로 도색했습니다.\n\n재건은 파괴의 잔해 위에서만 이뤄질 수 있다" },
	["aq_damascus_knife"] = { name = "aq_damascus_knife", desc = "두 가지 탄소강으로 만든 철판에서 제련되었습니다.\n\n마침내 당신의 결의에 맞는 강력한 무기가 마련되었습니다." },
	["am_crystallized"] = { name = "am_crystallized", desc = "금속성 도료를 특수처리하여 무늬를 만들었습니다.\n\n모두가 깊은 인상을 주고 싶어하지" },
	["snakeskin"] = { name = "snakeskin", desc = "뱀의 피부를 연상시키는 패턴을 수전사 방식으로 입혔습니다. \n\n네가 조직을 믿지 못하는 걸 알고 있어. 그리고 내가 너를 노출시키면 발레리아는 너를 죽일거야. - 방패 조직과 뱀 조직 제 1장" },
	["cu_usp-s_elegant"] = { name = "cu_usp-s_elegant", desc = "코발트 블루 색으로 바탕을 칠한 다음 슬라이드에 대테러부대 로고를 스텐실 기법으로 도색했습니다.\n\n작전에 따라 행동해서 7명을 구출하거나, 내 방식대로 해서 10명을 모두 구출할 수 있지... 결정해라 - 카르멘 코시네로, 인질 구출 전문가" },
	["hy_plaid2"] = { name = "hy_plaid2", desc = "회갈색과 파란색 색채의 녹색을 사용한 격자 무늬를 수전사 방식으로 입혔습니다.\n\n백파이프는 아니지만 누군가의 장례식이 올 때마다 연주를 하더군요" },
	["cu_fiveseven_urban_hazard"] = { name = "cu_fiveseven_urban_hazard", desc = "도시 위장색과 빨간 강조점으로 도색했습니다.\n\n부스는 매년 세계 각지에서 최고의 총기 제작가들을 모아 그들의 새 디자인을 선보입니다" },
	["an_emerald"] = { name = "an_emerald", desc = "크롬 베이스 코팅제를 씌운 뒤 투명한 에메랄드색의 표면 처리 효과 도료로 마무리 했습니다.\n\n이게 세바스찬 헤네켓라고? 잔소리만 해대는 학교 선생님? 대체 무슨 일이 있었던 건가... - 스승과 우상 파괴자 2장" },
	["so_keycolors"] = { name = "so_keycolors", desc = "각 부품을 선명한 색채의 파란색과 주황색 스프레이 페인트로 도색했습니다.\n\n위대한 심판자" },
	["cu_awp_mastery"] = { name = "cu_awp_mastery", desc = "패턴을 수전사 방식으로 입히고 섬세한 스텐실 그림과 열전사 데칼로 장식했습니다. \n\n체이스 터너는 대단한 인물이었어. 따라가려면 멀었다고. - 피닉스와 신입, 제 1장" },
	["aq_dualberettas_cartel"] = { name = "aq_dualberettas_cartel", desc = "뱀과, 꽃, 해골들이 새겨져 있습니다.\n\n양 손에 무기가 있더라도 뒤는 항상 조심해야 한다... - 뱀과 진리 탐구자, 제 2장" },
	["aq_famas_contour"] = { name = "aq_famas_contour", desc = "빨간색과 회색으로 무기의 형태를 돋보이게 하는 현대적인 무늬를 도색했습니다.\n\n우리는 함께하면 항상 강합니다" },
	["cu_nova_hyperbeast"] = { name = "cu_nova_hyperbeast", desc = "흉칙한 괴물이 환상적인 색채로 도색했습니다. \n\n사람들이 널 오니라고 부르지?  불쌍한 남자에게 너무나 무서운 이름이군... – 엘리엇 킹스맨, 용병" },
	["hy_ocean_knife"] = { name = "hy_ocean_knife", desc = "파란색 위장 무늬를 수전사 방식으로 입혔습니다." },
	["cu_m4a1s_printstream"] = { name = "cu_m4a1s_printstream", desc = "검은색과 하얀색을 조합한 디자인을 도색했으며 진줏빛 광택을 더했습니다.\n\nWHITE_1; BLACK_1; PEARLESCENT_1; SILENCED_1;" },
	["cu_usp_printstream"] = { name = "cu_usp_printstream", desc = "검은색과 하얀색을 조합한 디자인을 도색했으며 진줏빛 광택을 더했습니다.\n\nWHITE_1; BLACK_1; PEARLESCENT_1; SILENCED_1; PISTOL_1;" },
	["m4a1s_black_lotus"] = { name = "m4a1s_black_lotus", desc = "파란색과 보라색 바탕에 검은색 줄기의 연꽃 디자인을 커스텀 페인트로 도색했습니다.\n\n소총 라운드에서 2명을 추가로 처치한 후, 버리세요." },
	["ssg08_dezastre"] = { name = "ssg08_dezastre", desc = "검은색, 하얀색, 빨간색으로 차세대 지향적 디자인을 도색했습니다.\n\n안전한 사용을 위해 항상 제조업체 지침을 준수해 주세요." },
	["ump45_neo_noir"] = { name = "ump45_neo_noir", desc = "여성 디자인을 그레이 스케일 바탕에 블루와 마젠타 색상을 사용하여 커스텀 페인트로 도색했습니다.\n\n\"칼을 손에 들고 이제는 맞서야 할 때라고 결심했습니다.\"" },
