	["so_purple"] = { name = "Ultravioleta", desc = "As partes desta arma foram pintadas individualmente de cores sólidas, resultando em uma combinação de preto e roxo.\n\nDesign elegante combinado com intenção brutal" },
	["so_night"] = { name = "Noite", desc = "As partes desta arma foram pintadas individualmente de cores sólidas, resultando em uma aparência noturna.\n\nÉ o soprar do vento... é a mordida no seu pescoço" },
