	["so_sand"] = { name = "Duna de Areia", desc = "As partes desta arma foram pintadas individualmente de cores sólidas, resultando em uma aparência de duna de areia.\n\nCom a morte de Turner, temos uma nova prioridade: resgatar Alex Kincaide — Felix Riley, comandante" },
	["so_stormfront"] = { name = "Tempestade", desc = "As partes desta arma foram pintadas individualmente de cores sólidas, resultando em uma aparência tempestuosa.\n\nEstará à sua espera no monotrilho" },
	["so_tornado"] = { name = "Tornado", desc = "As partes desta arma foram pintadas individualmente de cores sólidas, resultando em uma aparência tumultuosa.\n\nNão se engane com a simplicidade dela" },
	["so_whiteout"] = { name = "Deu um Branco", desc = "Esta arma foi totalmente pintada de branco.\n\nEla parece tão pura, mas as aparências enganam" },
