	["so_space_marine"] = { name = "Colônia", desc = "As partes desta arma foram pintadas individualmente de cáqui e cinza." },
	["so_pmc"] = { name = "Contratante", desc = "As partes desta arma foram pintadas individualmente de bege, azul-marinho e verde-escuro.\n\nRona Sabri ainda não perdoou Sebastian por não tê-la escolhido para ir atrás de Turner" },
	["so_tangerine"] = { name = "Demolição", desc = "As partes desta arma foram pintadas individualmente de cores sólidas, resultando em uma combinação de preto e laranja-tangerina.\n\nRona é tão boa quanto pensa... ela só não é madura o suficiente para perceber que não deveria contar isso para todo mundo — Felix Riley, comandante" },
