	["aq_forced"] = { name = "Manchado", desc = "Esta arma foi pintada com pátina forçada, usando limão e mostarda respingados na superfície.\n\nSe acha que já está suja, espere vê-la embaixo de uma luz negra" },
	["aq_oiled"] = { name = "Aquecimento de Aço", desc = "Esta arma recebeu aquecimento a altas temperaturas por carvão vegetal para aumentar a sua resistência.\n\nUm pouco de cor não faz mal" },
