	["am_zebra"] = { name = "Massacre", desc = "Esta arma foi pintada com um padrão de listras de zebra aluminadas e cromadas com várias refletividades, coberto com uma camada minúscula de vermelho tomate.\n\nValéria não paga Javier para fazer perguntas... ele é pago para arrancar respostas" },
	["am_zebra_dark"] = { name = "Água Escura", desc = "Esta arma foi pintada com um padrão de listras aquáticas com tintas de carvão metálico de várias refletividades sobre um revestimento cromado.\n\nA água é o berço da vida, mas também pode ser o da morte..." },
	["aa_vertigo"] = { name = "Hipnótico", desc = "Esta arma foi pintada com um revestimento branco pérola sob um padrão gráfico com tinta de spray preta de estêncil.\n\nVocê não está sonolento, mas sim desmaiando por perda de sangue" },
