	["am_carbon_fiber"] = { name = "Fibra de Carbono", desc = "Esta arma foi pintada com um hidrográfico de fibra de carvão sobre um revestimento de grafite.\n\nCarregar a equipe não significa que não foi um trabalho em equipe — Rona Sabri, estrela em ascensão" },
