	["am_metal_inlay"] = { name = "Idade das Trevas", desc = "Esta arma foi pintada com tintas metálicas para parecer incrustada com metais em um padrão medieval.\n\nEu poderia matar o amor dela... talvez isso lhe consiga tempo com Valeria — O Amor de um Pai, Parte 2" },
	["hy_vines"] = { name = "Roseira", desc = "Esta arma foi pintada com um hidrográfico de flores e videiras.\n\nO beijo do amor verdadeiro não pode lhe salvar dos seus espinhos" },
	["so_indigo"] = { name = "Azul Real", desc = "Esta arma foi pintada com cores sólidas em índigo, carmesim e cinza.\n\nVocê deveria sorrir, Felix, pois teria mais sorte com as mulheres se não fosse um sisudo — Imogen, traficante de armas em treinamento" },
	["so_indigo_and_grey"] = { name = "Índigo", desc = "Esta arma foi pintada com cores sólidas em índigo, azul e cinza.\n\nAlex Kincaide, estou certo de que tem muito a perguntas... permita-me lhe dar respostas — A Fênix e o Contador de Histórias, Parte 1" },
	["cu_well_traveled_ak47"] = { name = "Viajante", desc = "Algumas partes desta arma foram revestidas com couro marrom. Esta arma viajou por várias cidades.\n\nAqueles que prestam atenção conectarão os pontos e verão o que ela fez. Não temos muito tempo para encontrar uma solução... — O Amor de um Pai, Parte 1" },
	["cu_green_leather"] = { name = "Primeira Classe", desc = "Algumas partes desta arma foram revestidas com couro verde.\n\nMimosa não inclusa" },
