	["cu_luggage_mac10"] = { name = "Mochileiro", desc = "Esta arma foi pintada com um hidrográfico xadrez e parcialmente revestida com couro sintético de crocodilo.\n\nEles morderam a isca, vamos em frente... — Elliott Kingsman, mercenário" },
	["cu_medieval_dragon_awp"] = { name = "Sabedoria do Dragão", desc = "Esta arma foi pintada com o desenho de um dragão celta.\n\nNem 200 chaves destrancariam os seus segredos" },
