	["cu_leather_xm1014"] = { name = "Couro Vermelho", desc = "Algumas partes desta arma foram revestidas com couro vermelho.\n\nFaçam as malas. Vamos matar aquele filho da puta... — O Oni e a Valquíria, Parte 2" },
	["cu_luggage_sg553"] = { name = "Viajante", desc = "Esta arma foi pintada com um padrão listrado clássico e possui algumas partes revestidas com couro marrom.\n\nTodos têm uma história e Javier arrancará a sua de você antes de matá-lo" },
	["cu_luggage_usp-s"] = { name = "Classe Executiva", desc = "Esta arma foi pintada com cores e padrões inspirados em estilos de malas antigas e algumas de suas partes foram revestidas com couro marrom.\n\nAlguns não desejam mais espaço para as pernas... apenas paz e silêncio" },
