	["aq_m4a1s_basilisk"] = { name = "Basilisco", desc = "Esta arma foi pintada com o desenho de um basilisco.\n\nÉ feio ficar encarando, mas na guerra não há tempo para ser bem-educado" },
	["cu_m4a4_griffin"] = { name = "Grifo", desc = "Esta arma foi pintada à mão com a ilustração de um grifo.\n\nFelix escolheu a dedo a sua equipe com operadores do mundo todo... e embora alguns questionem suas personalidades, nunca questionam os resultados" },
	["sp_mag7_firebitten"] = { name = "Incendiário", desc = "Esta arma foi pintada com camadas de spray e depois desgastada para parecer que as bordas estão em chamas.\n\nSempre começa com uma pequena faísca" },
