	["aq_p250_cartel"] = { name = "Cartel", desc = "Esta arma foi gravada com desenhos de esqueletos, flores e serpentes.\n\nUma pressionada leve no gatilho já é o suficiente para transformar qualquer dia no dia de finados" },
	["cu_p2000_fire_elemental"] = { name = "Elemental de Fogo", desc = "Esta arma foi pintada com o desenho estilizado de uma criatura feita de fogo.\n\nÀs vezes brincar com fogo é uma boa ideia" },
	["aq_sawedoff_blackgold"] = { name = "Fora da Lei", desc = "Esta arma foi pintada de preto e dourado usando o desenho de um cardo escocês.\n\nVocê não precisa esticar o mindinho quando puxar o gatilho, mas dá um toque de classe" },
	["cu_scar20_intervention"] = { name = "Cardíaca", desc = "Esta arma foi pintada com um padrão moderno azul e laranja e aquecida em um revestimento de vinil.\n\nBip, bip... biiiiiiiiiiiip..." },
