	["cu_xm1014_caritas"] = { name = "Tranquilidade", desc = "Esta arma foi pintada à mão com a imagem de uma mulher com cabelos longos.\n\nNão estou aqui por algo que eu preciso; estou aqui por algo que eu quero — Imogen e o Oni, Parte 2" },
	["am_awp_glory"] = { name = "Man-o'-war", desc = "Esta arma recebeu uma camada de tinta azul metálica e depois foi pintada à mão com curvas e detalhes dourados.\n\nMude algumas cabeças antes de explodi-las" },
