	["cu_galil_abrasion"] = { name = "Tagarela", desc = "Esta arma foi pintada à mão e desgastada. A caveira pintada sobre a tampa protetora parece abrir a boca quando o ferrolho se move.\n\nAlguns dizem que quando você ri, o mundo ri com você... mas quando a Tagarela começa a rir, ouve-se apenas gritos" },
