	["cu_m4a4_ancestral"] = { name = "龍王 (Rei Dragão)", desc = "Esta arma foi pintada com o desenho de um dragão em vermelho e azul.\n\nUma arma digna do Rei Macaco — Booth, traficante de armas" },
	["cu_m249_sektor"] = { name = "Bloqueio do Sistema", desc = "Esta arma foi pintada com um design de ficção científica.\n\nOs tiros de cobertura nunca foram tão lindos" },
	["am_mac10_malachite"] = { name = "Malaquita", desc = "Esta arma foi pintada usando efeitos metálicos com um padrão ondulado.\n\nCores brilhantes escondem uma arma com um propósito sombrio" },
	["cu_mp9_deadly_poison"] = { name = "Veneno Mortal", desc = "Esta arma foi pintada com formas dinâmicas e angulares, e uma aranha estilizada.\n\nNão é a primeira mordida que mata você, mas sim as 29 que vêm depois" },
