	["aq_scar20_leak"] = { name = "Gruta", desc = "Esta arma foi esponjada e pintada em escova com tinta azul de efeito metálico.\n\nO fundador da Season Corporation encomendou esta peça após ser resgatado pelo renomado operador Kotaro Izaki" },
