	["an_tiger_orange"] = { name = "Dente de Tigre", desc = "Esta arma foi anodizada laranja e gravada à mão com um padrão de listras de tigre.\n\nRara como o tigre... mortal como o tigre" },
	["am_marble_fade"] = { name = "Mármore Desbotado", desc = "Esta arma foi pintada com tintas metálicas pretas e prateadas usando marmorização média e depois revestida com um acabamento especial de três cores.\n\nA lâmina é feita de muitas cores, mas em breve todas se tornarão vermelhas" },
