	["cu_ak47_mastery"] = { name = "Elite Build", desc = "It has been custom painted using a combination of hydrographics and careful stenciling and detailed with heat-transfer decals.\n\nTread softly Naomi...people in your line of work aren't known for their longevity - The Shield and The Serpent Part 2" },
	["aq_mp7_ultramodern"] = { name = "Núcleo Couraçado", desc = "Esta arma foi pintada com tinta à óleo preta e montada com peças e inserções de aço inoxidável escovado.\n\nPerfeita para o operador que valoriza a sofisticação tanto quanto o poder de fogo — Booth, traficante de armas" },
	["aq_awp_twine"] = { name = "Deus Minhoca", desc = "Esta arma foi pintada com a imagem de uma caveira e raízes usando tintas à base de esmalte de alta durabilidade\n\nHá raízes que você não quer se apossar." },
