	["aq_p250_contour"] = { name = "Valência", desc = "Esta arma foi pintada em um design azul e cinza que complementa a forma da arma.\n\nNão é a sua habilidade que está à prova, é a sua atitude — O Professor e o Iconoclasta, Parte 1" },
