	["hy_geometric_steps_yellow"] = { name = "Terraço", desc = "Esta arma foi pintada usando um hidrográfico com tema geométrico.\n\nVocê confia demais nos outros, Valeria... isso causará a sua morte — Franz Kriegeld, estrategista da Fênix" },
	["hy_kimono_diamonds"] = { name = "Quimono Neon", desc = "Esta arma foi pintada usando um hidrográfico com tema geométrico.\n\nSei que estou pedindo muito de você... Turner era como um filho para você — O Paladino e o Professor, Parte 1" },
