	["hy_kimono_diamonds_red"] = { name = "Quimono Carmesim", desc = "Esta arma foi pintada usando um hidrográfico com tema geométrico.\n\nTurner, todos os homens podem ser derrubados... é uma questão de encontrar o ponto de pressão certo — O Traidor e o Investigador da Verdade, Parte 2" },
