	["snakeskin"] = { name = "snakeskin", desc = "Esta arma foi pintada com um hidrográfico com um padrão de pele de cobra.\n\nEu sei que você não acredita na nossa causa... e quando eu te expor, a Valeria vai pôr a sua cabeça a prêmio — O Escudo e a Serpente, Parte 1" },
	["cu_usp-s_elegant"] = { name = "cu_usp-s_elegant", desc = "Esta arma foi pintada de azul cobalto e marcada com um estêncil do logo dos CTs no ferrolho.\n\nEu posso seguir o procedimento padrão e salvar sete, ou posso fazer do meu jeito e salvar todos os dez... você decide — Carmen Cocinero, especialista em extrações" },
	["hy_plaid2"] = { name = "hy_plaid2", desc = "Esta arma foi pintada com um hidrográfico usando um padrão xadrez com as cores marrom-acinzentado e azul-esverdeado.\n\nNão é uma gaita de foles, mas mesmo assim toca uma melodia antes de todo funeral" },
	["cu_fiveseven_urban_hazard"] = { name = "cu_fiveseven_urban_hazard", desc = "Esta arma foi pintada com uma camuflagem urbana com detalhes vermelhos.\n\nTodo ano, Booth reúne os mais renomados fabricantes de armas para mostrarem as suas mais novas criações" },
	["an_emerald"] = { name = "an_emerald", desc = "Esta arma foi pintada com um revestimento cromado e com detalhes de tinta com efeito esmeralda transparente.\n\nO grande Sebastien Hennequet se reduziu a isto? Uma professora rabugenta? O que aconteceu com você... — O Professor e o Iconoclasta, Parte 2" },
	["so_keycolors"] = { name = "so_keycolors", desc = "As partes desta arma foram pintadas individualmente de cores sólidas, resultando em uma combinação de azul e laranja.\n\nO grande equalizador" },
	["cu_awp_mastery"] = { name = "cu_awp_mastery", desc = "Esta arma foi pintada usando uma combinação de hidrográficos e cuidadosamente detalhada com estênceis de transferência térmica.\n\nChase Turner foi um bom homem... você tem um grande vazio para preencher — A Fênix e o Iniciado, Parte 1" },
	["aq_dualberettas_cartel"] = { name = "aq_dualberettas_cartel", desc = "Esta arma foi gravada com desenhos de esqueletos, flores e serpentes.\n\nÉ por isso que Huxley faz o trabalho pesado, tudo o que temos que fazer é acompanhá-la... — A Serpente e o Investigador da Verdade, Parte 2" },
	["aq_famas_contour"] = { name = "aq_famas_contour", desc = "Esta arma foi pintada em um design vermelho e cinza que complementa a forma da arma.\n\nNós sempre somos mais fortes juntos" },
	["cu_nova_hyperbeast"] = { name = "cu_nova_hyperbeast", desc = "Esta arma foi pintada com uma criatura feroz em cores psicodélicas.\n\nEles te chamam de Demônio, né? Que nome assustador para um homem tão triste... — Elliot Kingsman, mercenário" },
	["hy_ocean_knife"] = { name = "hy_ocean_knife", desc = "Esta arma foi pintada usando um hidrográfico de camuflagem azul." },
	["cu_m4a1s_printstream"] = { name = "cu_m4a1s_printstream", desc = "Esta arma foi pintada com um design preto e branco simples com revestimentos perolados em destaque.\n\nBRANCO_1; PRETO_1; PEROLADO_1; SILENCIADO_1;" },
	["cu_usp_printstream"] = { name = "cu_usp_printstream", desc = "Esta arma foi pintada com um design preto e branco simples com revestimentos perolados em destaque.\n\nBRANCO_1; PRETO_1; PEROLADO_1; SILENCIADO_1; PISTOLA_1;" },
