	["so_tornado"] = { name = "Tornado", desc = "Certas partes desta arma foram pintadas com cores sólidas, relembrando a aparência de um tornado.\n\nNão te deixes enganar pela sua simplicidade" },
	["so_whiteout"] = { name = "Whiteout", desc = "Esta arma em particular foi totalmente pintada de branco.\n\nPode parecer pura, mas as aparências iludem" },
	["so_jungle"] = { name = "Jungle", desc = "Certas partes individuais deste item foram pintadas totalmente com o tema 'selva'." },
	["so_space_marine"] = { name = "Colony", desc = "Certas partes individuais deste item foram pintadas a spray com cores esverdeadas e acinzentadas." },
	["so_pmc"] = { name = "Contractor", desc = "Certas partes individuais deste item foram pintadas a spray com tons de azul-marinho, azul-escuro e amarelo-torrado.\n\nRona Sabri ainda não perdoou o Sebastien por ele não a ter escolhido para ir atrás do Turner" },
	["so_tangerine"] = { name = "Demolition", desc = "Certas partes individuais deste item foram pintadas totalmente com um tema preto e cor de laranja.\n\nA Rona é tão competente como ela pensa que é... Ela só não é madura o suficiente para perceber que não deveria relembrar constantemente toda a gente disso - Felix Riley, Comandante" },
