	["aa_fade"] = { name = "Fade", desc = "Esta arma em particular foi pintada com um aerógrafo com tintas transparentes que se misturam sobre um revestimento cromado.\n\nIsto não é só uma arma, é um bom assunto para uma conversa - Imogen, traficante de armas em formação" },
