	["aq_forced"] = { name = "Stained", desc = "Este item tem uma camada de pátina, formada pelo contacto forçado com elementos como o limão e a mostarda.\n\nSe achas que está suja agora, espera até a veres sob luz UV" },
	["aq_oiled"] = { name = "Case Hardened", desc = "A cor desta arma em particular foi afetada por um processo de endurecimento externo através da aplicação de carvão em temperaturas elevadas.\n\nUm bocadinho de cor nunca fez mal a ninguém" },
	["sp_spitfire_famas"] = { name = "Spitfire", desc = "sp_spitfire_famas" },
	["am_zebra"] = { name = "Slaughter", desc = "Esta arma em particular foi pintada com um padrão de listras de zebra com tintas de alumínio e cobre com várias refletividades, tendo sido depois tratada com uma camada de tinta vermelha.\n\nA Valeria não paga ao Javier para fazer perguntas... Ela paga-lhe para arrancar respostas" },
	["am_zebra_dark"] = { name = "Dark Water", desc = "Esta arma em particular foi pintada com um padrão de listras com tintas metalizadas de várias refletividades sobre uma camada base de crómio.\n\nA água dá vida, mas também pode tirá-la..." },
	["aa_vertigo"] = { name = "Hypnotic", desc = "Esta arma em particular foi pintada com uma camada base cor de pérola e decorada com um padrão preto pintado a spray com uma técnica de stencil.\n\nTu não estás com sono, estás a desmaiar por falta de sangue" },
