	["aq_rust"] = { name = "Rusty", desc = "Embora o seu exterior pareça enferrujado, esta arma ainda está perfeitamente operacional." },
	["am_slither_p90"] = { name = "Cold Blooded", desc = "Esta arma em particular foi pintada com uma camada base de crómio e acabada com uma camada de tinta transparente vermelha de efeito anodizado. Foi depois gravado a laser um desenho de uma cobra." },
	["am_carbon_fiber"] = { name = "Carbon Fiber", desc = "Esta arma em particular foi pintada com um padrão de fibra de carbono sobre uma camada base de grafite.\n\nLá porque eu tenha carregado com a equipa toda, não quer dizer que isso não tenha sido um esforço de equipa - Rona Sabri, estrela em ascensão" },
	["am_scorpion_p2000"] = { name = "Scorpion", desc = "Esta arma em particular foi pintada com uma camada de tinta de efeito anodizado num padrão desvanecente e decorada com um desenho de um escorpião." },
	["hy_feathers_aug"] = { name = "Wings", desc = "Esta arma em particular foi decorada com um padrão decalcado de penas de aves de rapina.\n\nUma águia parece majestosa até ao momento em que te arranca os olhos" },
