	["cu_fireserpent_ak47_bravo"] = { name = "Fire Serpent", desc = "Esta arma em particular é uma lembrança do mapa Ruins e foi decorada com um desenho de uma serpente de fogo.\n\nSe queres sobreviver nas ruas, aprende a cuspir fogo" },
