	["cu_dragon_p90_bravo"] = { name = "Emerald Dragon", desc = "Esta arma em particular é uma lembrança do mapa Chinatown e foi decorada com um desenho de um dragão.\n\nA tradição pode ser perigosa" },
	["hy_siege_bravo"] = { name = "Overgrowth", desc = "Esta arma em particular é uma lembrança do mapa Siege e foi pintada com um padrão de camuflagem.\n\nOlha-me nos olhos e diz-me se isto veio assim da fábrica - Felix Riley, Oficial Comandante" },
	["am_scales_bravo"] = { name = "Golden Koi", desc = "Esta arma em particular é uma lembrança do mapa Chinatown, foi revestida em película metálica e foi decorada com um padrão de escamas.\n\nUma arma intensa, em todos os sentidos da palavra" },
	["aq_etched_mac10_bravo"] = { name = "Graven", desc = "Esta arma em particular é uma lembrança do mapa Gwalior e possui partes de latão decoradas.\n\nQuebra dois mandamentos ao mesmo tempo" },
	["cu_season_elites_bravo"] = { name = "Black Limba", desc = "Estas armas são uma lembrança do mapa Agency, são de um tom escurecido e os respetivos cabos são de um acabamento em madeira limba.\n\nOnde construção clássica e estética moderna se juntam" },
