	["aq_steel_bravo"] = { name = "Rust Coat", desc = "Esta arma em particular é uma lembrança do mapa Siege e ainda está perfeitamente operacional, embora o seu exterior pareça enferrujado.\n\nAs cicatrizes dão personalidade" },
